import React, { useState, useEffect, useRef } from 'react';

const AccountManage = () => {
  const [stats, setStats] = useState({
    total: 0,
    active: 0,
    inactive: 0
  });
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [processingIds, setProcessingIds] = useState(new Set());
  const [searchTerm, setSearchTerm] = useState('');
  const [activeFilter, setActiveFilter] = useState('ì „ì²´');
  const [error, setError] = useState(null);
  
  // ì»´í¬ë„ŒíŠ¸ ë§ˆìš´íŠ¸ ìƒíƒœ ì¶”ì 
  const isMountedRef = useRef(true);
  const intervalRef = useRef(null);

  // API í˜¸ì¶œ í•¨ìˆ˜ë“¤
  const fetchUsers = async () => {
    try {
      console.log('ğŸ‘¥ ì‚¬ìš©ì ëª©ë¡ API í˜¸ì¶œ ì‹œì‘...');
      setLoading(true);
      
      const response = await fetch('http://localhost:5480/api/admin/users');
      const result = await response.json();
      
      console.log('ğŸ‘¥ ì‚¬ìš©ì ëª©ë¡ API ì‘ë‹µ:', result);
      
      if (response.ok && result.success) {
        console.log('ğŸ‘¥ âœ… ì‚¬ìš©ì ëª©ë¡ ë°ì´í„° ì²˜ë¦¬ ì„±ê³µ:', result.data);
        if (isMountedRef.current) {
          setUsers(result.data || []);
          setFilteredUsers(result.data || []);
          calculateStats(result.data || []);
          setError(null);
        }
      } else {
        console.error('ğŸ‘¥ âŒ ì‚¬ìš©ì ëª©ë¡ API ì‹¤íŒ¨:', result);
        setError('ì‚¬ìš©ì ëª©ë¡ ì¡°íšŒ ì‹¤íŒ¨: ' + (result.message || 'Unknown error'));
        if (isMountedRef.current) {
          setUsers([]);
          setFilteredUsers([]);
        }
      }
    } catch (error) {
      console.error('ğŸ‘¥ âŒ ì‚¬ìš©ì ëª©ë¡ ì¡°íšŒ ë„¤íŠ¸ì›Œí¬ ì˜¤ë¥˜:', error);
      setError('ì‚¬ìš©ì ëª©ë¡ ì¡°íšŒ ì¤‘ ë„¤íŠ¸ì›Œí¬ ì˜¤ë¥˜: ' + error.message);
      if (isMountedRef.current) {
        setUsers([]);
        setFilteredUsers([]);
      }
    } finally {
      if (isMountedRef.current) {
        setLoading(false);
      }
    }
  };

  const calculateStats = (userData) => {
    const total = userData.length;
    const active = userData.filter(user => user.isActive).length;
    const inactive = total - active;
    
    setStats({ total, active, inactive });
    console.log('ğŸ“Š í†µê³„ ê³„ì‚°:', { total, active, inactive });
  };

  const handleStatusToggle = async (userId, currentStatus, buttonElement) => {
    if (!isMountedRef.current) return;
    
    try {
      const newStatus = !currentStatus;
      console.log(`ğŸ”„ ê³„ì • ìƒíƒœ ë³€ê²½ ì‹œì‘: ${userId}, ${currentStatus} -> ${newStatus}`);
      
      // HTMLê³¼ ë™ì¼í•œ ë²„íŠ¼ íš¨ê³¼
      if (buttonElement) {
        buttonElement.innerHTML = '<svg class="w-4 h-4 animate-spin" fill="currentColor" viewBox="0 0 24 24"><path d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"/></svg>';
        buttonElement.disabled = true;
      }
      
      setProcessingIds(prev => new Set([...prev, userId]));
      
      const response = await fetch(`http://localhost:5480/api/admin/users/${userId}/status`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          isActive: newStatus,
          notes: `ê´€ë¦¬ìì— ì˜í•œ ${newStatus ? 'í™œì„±í™”' : 'ë¹„í™œì„±í™”'}`
        }),
      });

      const result = await response.json();
      console.log('ğŸ”„ ìƒíƒœ ë³€ê²½ API ì‘ë‹µ:', result);
      
      if (response.ok && result.success) {
        // HTMLê³¼ ë™ì¼í•œ ì™„ë£Œ íš¨ê³¼
        setTimeout(() => {
          if (buttonElement) {
            const parentCard = buttonElement.closest('.bg-white');
            const statusBadge = parentCard.querySelector('span[class*="rounded-full"]');
            
            if (newStatus) {
              // í™œì„±í™”
              buttonElement.innerHTML = 'ë¹„í™œì„±í™”';
              buttonElement.className = 'px-4 py-2 bg-red-100 text-red-600 text-sm rounded-lg';
              if (statusBadge) {
                statusBadge.className = 'px-2 py-1 bg-green-100 text-green-600 text-xs rounded-full';
                statusBadge.textContent = 'í™œì„±';
              }
            } else {
              // ë¹„í™œì„±í™”
              buttonElement.innerHTML = 'í™œì„±í™”';
              buttonElement.className = 'px-4 py-2 bg-green-100 text-green-600 text-sm rounded-lg';
              if (statusBadge) {
                statusBadge.className = 'px-2 py-1 bg-gray-100 text-gray-600 text-xs rounded-full';
                statusBadge.textContent = 'ë¹„í™œì„±';
              }
            }
            buttonElement.disabled = false;
          }
        }, 1500);
        
        // ë°ì´í„° ìƒˆë¡œê³ ì¹¨
        await fetchUsers();
      } else {
        console.error('ğŸ”„ ìƒíƒœ ë³€ê²½ ì‹¤íŒ¨:', result);
        alert(`ìƒíƒœ ë³€ê²½ ì‹¤íŒ¨: ${result.message}`);
        if (buttonElement) {
          buttonElement.innerHTML = currentStatus ? 'ë¹„í™œì„±í™”' : 'í™œì„±í™”';
          buttonElement.disabled = false;
        }
      }
    } catch (error) {
      console.error('ğŸ”„ ìƒíƒœ ë³€ê²½ ì¤‘ ì˜¤ë¥˜:', error);
      alert('ìƒíƒœ ë³€ê²½ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.');
      if (buttonElement) {
        buttonElement.innerHTML = currentStatus ? 'ë¹„í™œì„±í™”' : 'í™œì„±í™”';
        buttonElement.disabled = false;
      }
    } finally {
      setProcessingIds(prev => {
        const newSet = new Set(prev);
        newSet.delete(userId);
        return newSet;
      });
    }
  };

  const handleSearch = (term) => {
    setSearchTerm(term);
    filterUsers(term, activeFilter);
  };

  const handleFilterChange = (filter) => {
    setActiveFilter(filter);
    filterUsers(searchTerm, filter);
  };

  const filterUsers = (searchTerm, filter) => {
    let filtered = [...users];
    
    // ê²€ìƒ‰ í•„í„°ë§
    if (searchTerm.trim()) {
      filtered = filtered.filter(user => 
        user.managerName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        user.storeCode.toLowerCase().includes(searchTerm.toLowerCase()) ||
        user.userId.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }
    
    // ìƒíƒœ í•„í„°ë§
    if (filter === 'í™œì„±') {
