import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useDelivery } from '../../context/DeliveryContext';
import { useAuth } from '../../context/AuthContext';
import Header from '../common/Header';
import FloatingPreview from '../common/FloatingPreview';
import Button from '../ui/Button';
import OriginStoreSelector from './OriginStoreSelector'; // â† ì¶”ê°€

const DeliveryTypeSelector = () => {
  const context = useDelivery();
  const { user, logout, changePassword, token } = useAuth();
  const navigate = useNavigate();
  
  // ì‚¬ìš©ì ë©”ë‰´ ê´€ë ¨ ìƒíƒœ
  const [showUserMenu, setShowUserMenu] = useState(false);
  const [showChangePassword, setShowChangePassword] = useState(false);
  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });
  const [isChangingPassword, setIsChangingPassword] = useState(false);
  const [storeName, setStoreName] = useState('');
  const [userDetails, setUserDetails] = useState(null);
  const [loadingUserDetails, setLoadingUserDetails] = useState(true);

  // âœ… ëª¨ë‹¬ ìƒíƒœ ì¶”ê°€
  const [showOriginModal, setShowOriginModal] = useState(false);

  // ì•ˆì „ì„± ì²´í¬ ì¶”ê°€
  if (!context) {
    return <div>Loading...</div>;
  }

  const { deliveryType, dispatch } = context;
  const [selectedOption, setSelectedOption] = useState(deliveryType || '');

  // ì‚¬ìš©ì ì´ë¦„ ê¸°ë°˜ ì•„ë°”íƒ€ URL ìƒì„± í•¨ìˆ˜
  const generateAvatarUrl = (name) => {
    if (!name) return null;
    
    // ì‚¬ìš©ì ì´ë¦„ì—ì„œ ì²« ê¸€ì ì¶”ì¶œ (í•œê¸€/ì˜ë¬¸ ëª¨ë‘ ì§€ì›)
    const firstChar = name.charAt(0);
    
    // UI Avatars ì„œë¹„ìŠ¤ ì‚¬ìš© - í•œê¸€ë„ ì§€ì›í•˜ë©° ì•ˆì •ì 
    const avatarUrl = `https://ui-avatars.com/api/?` + 
      `name=${encodeURIComponent(firstChar)}&` +
      `size=40&` +
      `background=f97316&` +  // ì˜¤ë Œì§€ ë°°ê²½ìƒ‰
      `color=ffffff&` +       // í°ìƒ‰ í…ìŠ¤íŠ¸
      `font-size=0.6&` +      // í°íŠ¸ í¬ê¸°
      `rounded=true&` +       // ë‘¥ê·¼ ëª¨ì–‘
      `bold=true`;            // êµµì€ ê¸€ì”¨
    
    return avatarUrl;
  };

  // ì‚¬ìš©ì ìƒì„¸ ì •ë³´ ê°€ì ¸ì˜¤ê¸°
  const fetchUserDetails = async () => {
    if (!user || !token) {
      setLoadingUserDetails(false);
      return;
    }

    try {
      console.log('ğŸ‘¤ ì‚¬ìš©ì ìƒì„¸ ì •ë³´ ì¡°íšŒ ì‹œì‘:', user.user_id || user.userId);
      
      const response = await fetch(`https://quinors-lv-backend.ngrok.io/api/auth/profile`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        }
      });

      const result = await response.json();
      
      if (response.ok && result.success) {
        console.log('ğŸ‘¤ âœ… ì‚¬ìš©ì ì •ë³´ ì¡°íšŒ ì„±ê³µ:', result.data);
        setUserDetails(result.data);
        
        // ë§¤ì¥ëª… ì„¤ì •
        if (result.data.cust_name) {
          setStoreName(result.data.cust_name);
        } else if (result.data.dept_name) {
          setStoreName(result.data.dept_name);
        }
      } else {
        console.error('ğŸ‘¤ âŒ ì‚¬ìš©ì ì •ë³´ ì¡°íšŒ ì‹¤íŒ¨:', result);
        // ê¸°ì¡´ user ì •ë³´ ì‚¬ìš©
        setUserDetails(user);
        if (user.storeName) {
          setStoreName(user.storeName);
        } else if (user.storeCode) {
          setStoreName(user.storeCode);
        }
      }
    } catch (error) {
      console.error('ğŸ‘¤ âŒ ë„¤íŠ¸ì›Œí¬ ì˜¤ë¥˜:', error);
      // ê¸°ì¡´ user ì •ë³´ ì‚¬ìš©
      setUserDetails(user);
      if (user.storeName) {
        setStoreName(user.storeName);
      } else if (user.storeCode) {
        setStoreName(user.storeCode);
      }
    } finally {
      setLoadingUserDetails(false);
    }
  };

  // ë§¤ì¥ëª… ì¡°íšŒ í•¨ìˆ˜ (ê¸°ì¡´ ìœ ì§€)
  const fetchStoreName = async (storeCode) => {
    try {
      const response = await fetch(`https://quinors-lv-backend.ngrok.io/api/admin/stores?search=${storeCode}&limit=1`);
      const result = await response.json();
      
      if (response.ok && result.success && result.data.length > 0) {
        const store = result.data.find(s => s.storeCode === storeCode);
        if (store) {
          setStoreName(store.storeName);
          console.log('âœ… ë§¤ì¥ëª… ì¡°íšŒ ì„±ê³µ:', { storeCode, storeName: store.storeName });
        }
      } else {
        console.log('âŒ ë§¤ì¥ëª… ì¡°íšŒ ì‹¤íŒ¨, ë§¤ì¥ì½”ë“œ ì‚¬ìš©:', storeCode);
        setStoreName(storeCode);
      }
    } catch (error) {
      console.error('âŒ ë§¤ì¥ëª… ì¡°íšŒ ì˜¤ë¥˜:', error);
      setStoreName(storeCode);
    }
  };

  // ì‚¬ìš©ì ì •ë³´ ë³€ê²½ ì‹œ ìƒì„¸ ì •ë³´ ì¡°íšŒ
  useEffect(() => {
    if (user && token) {
      fetchUserDetails();
    }
  }, [user, token]);

  // ì‚¬ìš©ì ë©”ë‰´ ì™¸ë¶€ í´ë¦­ ì‹œ ë‹«ê¸°
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (showUserMenu && !event.target.closest('.user-menu-container')) {
        setShowUserMenu(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [showUserMenu]);

  const deliveryOptions = [
    {
      id: 'store-to-store',
      title: 'ë§¤ì¥ â†” ë§¤ì¥',
      subtitle: 'ë§¤ì¥ë³„ ë°°ì†¡',
      icon: 'fa-store'
    },
    {
      id: 'store-to-customer',
      title: 'ë§¤ì¥ â†” ê³ ê°',
      subtitle: 'ê³ ê° ë°°ì†¡',
      icon: 'fa-user'
    },
    {
      id: 'store-to-partner',
      title: 'ë§¤ì¥ â†” í˜‘ë ¥ì‚¬',
      subtitle: 'í˜‘ë ¥ì‚¬ ë°°ì†¡',
      icon: 'fa-handshake'
    }
  ];

  const handleOptionSelect = (option) => {
    setSelectedOption(option.title);
  };

  // âœ… handleNext í•¨ìˆ˜ ìˆ˜ì • - ëª¨ë‹¬ ì—°ê²°
  const handleNext = () => {
    console.log('ğŸ”„ ë‹¤ìŒ ë‹¨ê³„ í´ë¦­');
    console.log('ğŸ”„ ì„ íƒëœ ì˜µì…˜:', selectedOption);
    console.log('ğŸ”„ dispatch ì¡´ì¬:', !!dispatch);
    
    if (selectedOption && dispatch) {
      dispatch({ type: 'SET_DELIVERY_TYPE', payload: selectedOption });
      console.log('ğŸ”„ ë°°ì†¡ ìœ í˜• ì„¤ì • ì™„ë£Œ');
      
      // ë§¤ì¥ â†” ë§¤ì¥ ì„ íƒ ì‹œ ëª¨ë‹¬ ì—´ê¸°
      if (selectedOption === 'ë§¤ì¥ â†” ë§¤ì¥') {
        console.log('ğŸª ì¶œë°œì§€ ì„ íƒ ëª¨ë‹¬ ì—´ê¸°');
        setShowOriginModal(true);
      } else {
        // ë‹¤ë¥¸ ë°°ì†¡ ìœ í˜•ì˜ ê²½ìš° ë‹¤ìŒ ë‹¨ê³„ë¡œ
        console.log('â¡ï¸ ë‹¤ë¥¸ ë°°ì†¡ ìœ í˜• - ë‹¤ìŒ ë‹¨ê³„ë¡œ');
        // ì¶”í›„ ë‹¤ë¥¸ ë°°ì†¡ ìœ í˜•ë³„ í˜ì´ì§€ ì´ë™ ë¡œì§ ì¶”ê°€
        alert(`${selectedOption} ì„ íƒë¨. ì•„ì§ êµ¬í˜„ë˜ì§€ ì•Šì€ ê¸°ëŠ¥ì…ë‹ˆë‹¤.`);
      }
    }
  };

  // âœ… ëª¨ë‹¬ ë‹«ê¸° í•¸ë“¤ëŸ¬
  const handleOriginModalClose = () => {
    console.log('ğŸª ì¶œë°œì§€ ì„ íƒ ëª¨ë‹¬ ë‹«ê¸°');
    setShowOriginModal(false);
    // ì¶œë°œì§€ ì„ íƒ ì™„ë£Œ í›„ ë‹¤ìŒ ë‹¨ê³„ë¡œ ì´ë™í•˜ê±°ë‚˜ ì¶”ê°€ ë¡œì§ ì²˜ë¦¬
    // navigate('/delivery/destination'); // í•„ìš”ì‹œ í™œì„±í™”
  };

  const handleEditPreview = () => {
    setSelectedOption('');
  };

  const handleLogout = () => {
    setShowUserMenu(false);
    if (window.confirm('ì •ë§ ë¡œê·¸ì•„ì›ƒ í•˜ì‹œê² ìŠµë‹ˆê¹Œ?')) {
      logout();
      setStoreName('');
    }
  };

  const handleChangePasswordClick = () => {
    setShowUserMenu(false);
    setShowChangePassword(true);
  };

  const handleNavigateToAdmin = () => {
    setShowUserMenu(false);
    console.log('ğŸ”§ ê´€ë¦¬ì íŒ¨ë„ë¡œ ì´ë™ ì¤‘...');
    navigate('/admin');
  };

  const handleChangePasswordSubmit = async () => {
    if (!passwordData.currentPassword || !passwordData.newPassword || !passwordData.confirmPassword) {
      alert('ëª¨ë“  í•„ë“œë¥¼ ì…ë ¥í•´ì£¼ì„¸ìš”.');
      return;
    }

    if (passwordData.newPassword !== passwordData.confirmPassword) {
      alert('ìƒˆ ë¹„ë°€ë²ˆí˜¸ê°€ ì¼ì¹˜í•˜ì§€ ì•ŠìŠµë‹ˆë‹¤.');
      return;
    }

    if (passwordData.newPassword.length < 6) {
      alert('ìƒˆ ë¹„ë°€ë²ˆí˜¸ëŠ” 6ìë¦¬ ì´ìƒì´ì–´ì•¼ í•©ë‹ˆë‹¤.');
      return;
    }

