import React, { useState, useEffect, useRef } from 'react';

const AdminPanel = () => {
  const [stats, setStats] = useState({
    requests: { total: 0, pending: 0, approved: 0, rejected: 0 },
    users: { total: 0, active: 0, inactive: 0 }
  });
  const [requests, setRequests] = useState([]);
  const [loading, setLoading] = useState(false);
  const [processingIds, setProcessingIds] = useState(new Set());
  const [activeTab, setActiveTab] = useState('dashboard');
  const [recentActivities, setRecentActivities] = useState([]);
  
  // 컴포넌트 마운트 상태 추적
  const isMountedRef = useRef(true);
  const intervalRef = useRef(null);

  // 안전한 상태 업데이트 함수
  const safeSetState = (setter) => {
    if (isMountedRef.current) {
      setter();
    }
  };

  // API 호출 함수들
  const fetchStats = async () => {
    try {
      const response = await fetch('http://localhost:5480/api/admin/stats');
      const result = await response.json();
      if (result.success && isMountedRef.current) {
        safeSetState(() => setStats(result.data));
      }
    } catch (error) {
      console.error('통계 조회 실패:', error);
    }
  };

  const fetchRequests = async (status = 'pending') => {
    try {
      safeSetState(() => setLoading(true));
      const response = await fetch(`http://localhost:5480/api/admin/requests?status=${status}`);
      const result = await response.json();
      if (result.success && isMountedRef.current) {
        safeSetState(() => {
          setRequests(result.data);
          updateRecentActivities(result.data);
        });
      }
    } catch (error) {
      console.error('신청 목록 조회 실패:', error);
    } finally {
      if (isMountedRef.current) {
        safeSetState(() => setLoading(false));
      }
    }
  };

  const updateRecentActivities = (requestsData) => {
    if (!isMountedRef.current) return;
    
    const activities = requestsData
      .filter(req => req.processedDate || req.createdAt)
      .sort((a, b) => new Date(b.processedDate || b.createdAt) - new Date(a.processedDate || a.createdAt))
      .slice(0, 5)
      .map(req => ({
        id: req._id,
        type: req.status === 'approved' ? 'approved' : req.status === 'rejected' ? 'rejected' : 'request',
        title: req.status === 'approved' ? '계정 승인 완료' : req.status === 'rejected' ? '계정 거부 처리' : '신규 신청 접수',
        subtitle: `${req.storeCode} - ${req.managerName}`,
        time: getTimeAgo(req.processedDate || req.createdAt),
        icon: req.status === 'approved' ? 'user-check' : req.status === 'rejected' ? 'user-times' : 'user-plus',
        color: req.status === 'approved' ? 'green' : req.status === 'rejected' ? 'red' : 'blue'
      }));
    
    safeSetState(() => setRecentActivities(activities));
  };

  const getTimeAgo = (dateString) => {
    const now = new Date();
    const date = new Date(dateString);
    const diffMs = now - date;
    const diffMins = Math.floor(diffMs / 60000);
    const diffHours = Math.floor(diffMs / 3600000);
    const diffDays = Math.floor(diffMs / 86400000);

    if (diffMins < 1) return '방금 전';
    if (diffMins < 60) return `${diffMins}분 전`;
    if (diffHours < 24) return `${diffHours}시간 전`;
    return `${diffDays}일 전`;
  };

  const handleApprove = async (requestId) => {
    if (!isMountedRef.current) return;
    
    try {
      safeSetState(() => setProcessingIds(prev => new Set([...prev, requestId])));
      
      const response = await fetch(`http://localhost:5480/api/admin/approve/${requestId}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          notes: '관리자 승인 완료'
        }),
      });

      const result = await response.json();
      if (result.success && isMountedRef.current) {
        alert(`계정 승인 완료!\n사용자 ID: ${result.data.userId}\n임시 비밀번호: ${result.data.tempPassword}`);
        await Promise.all([fetchRequests(activeTab === 'requests' ? 'pending' : 'all'), fetchStats()]);
      } else {
        alert(`승인 실패: ${result.message}`);
      }
    } catch (error) {
      console.error('승인 처리 실패:', error);
      alert('승인 처리 중 오류가 발생했습니다.');
    } finally {
      if (isMountedRef.current) {
        safeSetState(() => setProcessingIds(prev => {
          const newSet = new Set(prev);
          newSet.delete(requestId);
          return newSet;
        }));
      }
    }
  };

  const handleReject = async (requestId) => {
    if (!isMountedRef.current) return;
    
    try {
      safeSetState(() => setProcessingIds(prev => new Set([...prev, requestId])));
      
      const notes = prompt('거부 사유를 입력해주세요:', '정보 불일치');
      if (!notes) return;

      const response = await fetch(`http://localhost:5480/api/admin/reject/${requestId}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ notes }),
      });

      const result = await response.json();
      if (result.success && isMountedRef.current) {
        alert('신청이 거부되었습니다.');
        await Promise.all([fetchRequests(activeTab === 'requests' ? 'pending' : 'all'), fetchStats()]);
      } else {
        alert(`거부 실패: ${result.message}`);
      }
    } catch (error) {
      console.error('거부 처리 실패:', error);
      alert('거부 처리 중 오류가 발생했습니다.');
    } finally {
      if (isMountedRef.current) {
        safeSetState(() => setProcessingIds(prev => {
          const newSet = new Set(prev);
          newSet.delete(requestId);
          return newSet;
        }));
      }
    }
  };

  // 컴포넌트 마운트 시 데이터 로드
  useEffect(() => {
    fetchStats();
    fetchRequests('all');

    // 자동 새로고침 설정
    intervalRef.current = setInterval(() => {
      if (isMountedRef.current) {
        fetchStats();
        if (activeTab === 'dashboard') {
          fetchRequests('all');
        }
      }
    }, 5000);

    // Cleanup 함수
    return () => {
      isMountedRef.current = false;
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, []);

  // activeTab 변경 시 interval 재설정
  useEffect(() => {
    if (intervalRef.current) {
      clearInterval(intervalRef.current);
    }
    
    intervalRef.current = setInterval(() => {
      if (isMountedRef.current) {
        fetchStats();
        if (activeTab === 'dashboard') {
          fetchRequests('all');
        }
      }
    }, 5000);

    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, [activeTab]);

  // Font Awesome 아이콘 컴포넌트 (안전한 버전)
  const SafeIcon = ({ icon, className = "" }) => {
    return (
      <i 
        className={`fas fa-${icon} ${className}`}
        key={`${icon}-${Math.random()}`} // 강제 리렌더링 방지
        style={{ fontFamily: '"Font Awesome 6 Free"' }}
      />
    );
  };

  return (
    <div className="bg-gray-100 font-sans" key="admin-panel">
      <div className="w-full max-w-sm mx-auto bg-white shadow-lg min-h-screen">
        
        {/* Header */}
        <header className="bg-gradient-to-br from-orange-400 via-orange-500 to-orange-500 text-white p-4 pt-12">
          <div className="flex items-center justify-between mb-6">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 bg-white/20 rounded-lg flex items-center justify-center">
                <SafeIcon icon="shield-alt" className="text-white" />
              </div>
              <div>
                <h1 className="text-lg font-bold">관리자 대시보드</h1>
                <p className="text-white/80 text-xs">퀴노스 비즈니스</p>
              </div>
            </div>
            <div className="flex items-center gap-2">
              <button className="w-8 h-8 bg-white/20 rounded-lg flex items-center justify-center">
                <SafeIcon icon="bell" className="text-white text-sm" />
              </button>
              <img 
                src="https://storage.googleapis.com/uxpilot-auth.appspot.com/avatars/avatar-2.jpg" 
                className="w-8 h-8 rounded-lg"
                alt="Admin Avatar"
              />
            </div>
          </div>
          
          {/* Stats */}
          <div className="grid grid-cols-2 gap-3">
            <div className="bg-white/10 rounded-xl p-4">
              <div className="text-white/80 text-xs mb-1">총 신청</div>
              <div className="text-xl font-bold text-white">{stats.requests.total}</div>
              <div className="text-green-300 text-xs">+{stats.requests.pending} 대기중</div>
            </div>
            <div className="bg-white/10 rounded-xl p-4">
              <div className="text-white/80 text-xs mb-1">활성 사용자</div>
              <div className="text-xl font-bold text-white">{stats.users.active}</div>
              <div className="text-green-300 text-xs">{stats.users.total}명 전체</div>
            </div>
          </div>
        </header>

        {/* Main Content */}
        <main className="p-4 pb-20">
          {activeTab === 'dashboard' && (
            <div className="mb-6">
              <h2 className="text-lg font-bold text-gray-800 mb-4">대시보드 개요</h2>
              
              {/* Quick Stats */}
              <div className="grid grid-cols-2 gap-4 mb-6">
                <div className="bg-white rounded-xl p-4 shadow-sm border">
                  <div className="text-gray-500 text-sm mb-1">대기중</div>
                  <div className="text-2xl font-bold text-gray-800">{stats.requests.pending}</div>
                  <div className="text-orange-500 text-sm">처리 필요</div>
                </div>
                <div className="bg-white rounded-xl p-4 shadow-sm border">
                  <div className="text-gray-500 text-sm mb-1">승인완료</div>
                  <div className="text-2xl font-bold text-gray-800">{stats.requests.approved}</div>
                  <div className="text-green-500 text-sm">
                    {stats.requests.total > 0 ? Math.round((stats.requests.approved / stats.requests.total) * 100) : 0}% 승인률
                  </div>
                </div>
              </div>

              {/* Recent Activity */}
              <div className="bg-white rounded-xl p-4 shadow-sm border">
                <h3 className="font-semibold text-gray-800 mb-3">최근 활동</h3>
                <div className="space-y-3">
                  {recentActivities.length > 0 ? recentActivities.map((activity, index) => (
                    <div key={`${activity.id}-${index}`} className="flex items-center gap-3">
                      <div className={`w-8 h-8 rounded-full flex items-center justify-center ${
                        activity.color === 'green' ? 'bg-green-50' :
                        activity.color === 'red' ? 'bg-red-50' : 'bg-blue-50'
                      }`}>
                        <SafeIcon 
                          icon={activity.icon}
                          className={`text-xs ${
                            activity.color === 'green' ? 'text-green-500' :
                            activity.color === 'red' ? 'text-red-500' : 'text-blue-500'
                          }`}
                        />
                      </div>
                      <div className="flex-1">
                        <div className="text-sm font-medium text-gray-800">{activity.title}</div>
                        <div className="text-xs text-gray-500">{activity.subtitle} - {activity.time}</div>
                      </div>
                    </div>
                  )) : (
                    <div className="text-center py-4 text-gray-500 text-sm">
                      최근 활동이 없습니다.
                    </div>
                  )}
                </div>
              </div>
            </div>
          )}

          {activeTab === 'requests' && (
            <div className="mb-6">
              <div className="flex items-center justify-between mb-4">
                <h2 className="text-lg font-bold text-gray-800">계정 신청 관리</h2>
                <button 
                  onClick={() => fetchRequests('pending')}
                  className="text-orange-500 text-sm"
                  disabled={loading}
                >
                  {loading ? '로딩...' : '새로고침'}
                </button>
              </div>

              {/* Filter Tabs */}
              <div className="flex gap-2 mb-4">
                {['pending', 'approved', 'rejected', 'all'].map(status => (
                  <button
                    key={status}
                    onClick={() => fetchRequests(status)}
                    className={`px-3 py-1 rounded-full text-xs font-medium ${
                      status === 'pending' ? 'bg-orange-100 text-orange-600' :
                      status === 'approved' ? 'bg-green-100 text-green-600' :
                      status === 'rejected' ? 'bg-red-100 text-red-600' :
                      'bg-gray-100 text-gray-600'
                    }`}
                  >
                    {status === 'pending' ? '대기중' :
                     status === 'approved' ? '승인됨' :
                     status === 'rejected' ? '거부됨' : '전체'}
                  </button>
                ))}
              </div>

              {/* Requests List */}
              <div className="space-y-3">
                {requests.length > 0 ? requests.map((request) => (
                  <div key={request._id} className="bg-white rounded-xl p-4 shadow-sm border">
                    <div className="flex items-start justify-between mb-3">
                      <div>
                        <div className="font-medium text-gray-800">{request.managerName}</div>
                        <div className="text-sm text-gray-500">{request.storeCode} • ****{request.phoneLast4}</div>
                        <div className="text-xs text-gray-400">
                          {new Date(request.createdAt).toLocaleString('ko-KR')}
                        </div>
                      </div>
                      <div className={`px-2 py-1 rounded-full text-xs font-medium ${
                        request.status === 'pending' ? 'bg-yellow-100 text-yellow-600' :
                        request.status === 'approved' ? 'bg-green-100 text-green-600' :
                        'bg-red-100 text-red-600'
                      }`}>
                        {request.status === 'pending' ? '대기중' :
                         request.status === 'approved' ? '승인됨' : '거부됨'}
                      </div>
                    </div>

                    {request.status === 'approved' && request.generatedUserId && (
                      <div className="bg-green-50 p-3 rounded-lg mb-3">
                        <div className="text-xs text-green-600 font-medium">생성된 계정 정보</div>
                        <div className="text-sm text-green-800">ID: {request.generatedUserId}</div>
                      </div>
                    )}

                    {request.status === 'rejected' && request.notes && (
                      <div className="bg-red-50 p-3 rounded-lg mb-3">
                        <div className="text-xs text-red-600 font-medium">거부 사유</div>
                        <div className="text-sm text-red-800">{request.notes}</div>
                      </div>
                    )}

                    {request.status === 'pending' && (
                      <div className="flex gap-2">
                        <button
                          onClick={() => handleApprove(request._id)}
                          disabled={processingIds.has(request._id)}
                          className="flex-1 bg-green-500 text-white py-2 rounded-lg text-sm font-medium hover:bg-green-600 transition-colors disabled:opacity-50"
                        >
                          {processingIds.has(request._id) ? (
                            <>⏳ 처리중...</>
                          ) : (
                            '승인'
                          )}
                        </button>
                        <button
                          onClick={() => handleReject(request._id)}
                          disabled={processingIds.has(request._id)}
                          className="flex-1 bg-red-500 text-white py-2 rounded-lg text-sm font-medium hover:bg-red-600 transition-colors disabled:opacity-50"
                        >
                          {processingIds.has(request._id) ? (
                            <>⏳ 처리중...</>
                          ) : (
                            '거부'
                          )}
                        </button>
                      </div>
                    )}
                  </div>
                )) : (
                  <div className="text-center py-8 text-gray-500">
                    {loading ? '⏳ 로딩 중...' : '신청 내역이 없습니다.'}
                  </div>
                )}
              </div>
            </div>
          )}
        </main>

        {/* Bottom Navigation */}
        <nav className="fixed bottom-0 left-1/2 transform -translate-x-1/2 w-full max-w-sm bg-white border-t border-gray-200 p-4">
          <div className="grid grid-cols-3 gap-1">
            <button 
              onClick={() => setActiveTab('dashboard')}
              className={`flex flex-col items-center gap-1 py-2 ${
                activeTab === 'dashboard' ? 'text-orange-500' : 'text-gray-400'
              }`}
            >
              <SafeIcon icon="home" className="text-lg" />
              <span className="text-xs">홈</span>
            </button>
            <button 
              onClick={() => {
                setActiveTab('requests');
                fetchRequests('pending');
              }}
              className={`flex flex-col items-center gap-1 py-2 relative ${
                activeTab === 'requests' ? 'text-orange-500' : 'text-gray-400'
              }`}
            >
              <SafeIcon icon="clipboard-list" className="text-lg" />
              <span className="text-xs">신청관리</span>
              {stats.requests.pending > 0 && (
                <div className="absolute -top-1 -right-1 bg-red-500 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center">
                  {stats.requests.pending > 9 ? '9+' : stats.requests.pending}
                </div>
              )}
            </button>
            <button className="flex flex-col items-center gap-1 py-2 text-gray-400">
              <SafeIcon icon="cog" className="text-lg" />
              <span className="text-xs">설정</span>
            </button>
          </div>
        </nav>
      </div>
    </div>
  );
};

export default AdminPanel;
