import React, { useState, useEffect, useRef } from 'react';

const AccountManage = ({ onBackToAdmin }) => {
  const [stats, setStats] = useState({
    total: 0,
    pending: 0,
    approved: 0,
    rejected: 0
  });
  const [allAccounts, setAllAccounts] = useState([]);
  const [filteredAccounts, setFilteredAccounts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [processingIds, setProcessingIds] = useState(new Set());
  const [searchTerm, setSearchTerm] = useState('');
  const [activeFilter, setActiveFilter] = useState('ì „ì²´');
  const [error, setError] = useState(null);
  
  // ì»´í¬ë„ŒíŠ¸ ë§ˆìš´íŠ¸ ìƒíƒœ ì¶”ì 
  const isMountedRef = useRef(true);
  const intervalRef = useRef(null);

  // API í˜¸ì¶œ í•¨ìˆ˜ë“¤
  const fetchAllAccounts = async () => {
    try {
      console.log('ğŸ‘¥ ì „ì²´ ê³„ì • ì •ë³´ API í˜¸ì¶œ ì‹œì‘...');
      setLoading(true);
      
      // ëª¨ë“  ì‹ ì²­ ëª©ë¡ì„ ê°€ì ¸ì˜¤ê¸°
      const response = await fetch('http://localhost:5480/api/admin/requests?status=all');
      const result = await response.json();
      
      console.log('ğŸ“‹ ì‹ ì²­ ëª©ë¡ API ì‘ë‹µ:', result);
      
      if (response.ok && result.success) {
        const requests = result.data || [];
        
        // ì‹ ì²­ ëª©ë¡ì„ ê³„ì • í˜•íƒœë¡œ ë³€í™˜
        const accounts = requests.map(request => ({
          ...request,
          accountType: request.status, // pending, approved, rejected
        }));
        
        console.log('ğŸ‘¥ âœ… ì „ì²´ ê³„ì • ë°ì´í„° ì²˜ë¦¬ ì„±ê³µ:', accounts);
        
        if (isMountedRef.current) {
          setAllAccounts(accounts);
