import React, { createContext, useContext, useReducer, useEffect } from 'react';

const AuthContext = createContext();

const initialState = {
  isAuthenticated: false,
  user: null,
  loading: false,
  error: null,
  token: null
};

const authReducer = (state, action) => {
  switch (action.type) {
    case 'LOGIN_START':
      return { ...state, loading: true, error: null };
    
    case 'LOGIN_SUCCESS':
      return {
        ...state,
        isAuthenticated: true,
        user: action.payload.user,
        token: action.payload.token,
        loading: false,
        error: null
      };
    
    case 'LOGIN_FAILURE':
      return {
        ...state,
        isAuthenticated: false,
        user: null,
        token: null,
        loading: false,
        error: action.payload
      };
    
    case 'LOGOUT':
      return {
        ...state,
        isAuthenticated: false,
        user: null,
        token: null,
        loading: false,
        error: null
      };
    
    case 'RESTORE_SESSION':
      return {
        ...state,
        isAuthenticated: true,
        user: action.payload.user,
        token: action.payload.token,
        loading: false
      };
    
    case 'CLEAR_ERROR':
      return { ...state, error: null };
    
    case 'REGISTER_START':
      return { ...state, loading: true, error: null };
    
    case 'REGISTER_SUCCESS':
      return { ...state, loading: false, error: null };
    
    case 'REGISTER_FAILURE':
      return { ...state, loading: false, error: action.payload };
    
    default:
      return state;
  }
};

export const AuthProvider = ({ children }) => {
  const [state, dispatch] = useReducer(authReducer, initialState);

  // ÌéòÏù¥ÏßÄ Î°úÎìú Ïãú Ï†ÄÏû•Îêú Ïù∏Ï¶ù Ï†ïÎ≥¥ Î≥µÏõê
  useEffect(() => {
    const token = localStorage.getItem('authToken');
    const userInfo = localStorage.getItem('userInfo');
    
    if (token && userInfo) {
      try {
        const parsedUserInfo = JSON.parse(userInfo);
        dispatch({
          type: 'RESTORE_SESSION',
          payload: {
            user: parsedUserInfo,
            token: token
          }
        });
        console.log('‚úÖ ÏÑ∏ÏÖò Î≥µÏõê ÏÑ±Í≥µ:', parsedUserInfo.charge_name || parsedUserInfo.user_id);
      } catch (error) {
        console.error('ÏÑ∏ÏÖò Î≥µÏõê Ïò§Î•ò:', error);
        localStorage.removeItem('authToken');
        localStorage.removeItem('userInfo');
      }
    }
  }, []);

  // Î°úÍ∑∏Ïù∏ Ìï®Ïàò
  const login = async (credentials) => {
    dispatch({ type: 'LOGIN_START' });
    
    try {
      console.log('üîê Î°úÍ∑∏Ïù∏ ÏãúÎèÑ:', { userId: credentials.username });
      
      // ÏÑúÎ≤Ñ ÏÉÅÌÉú ÌôïÏù∏
      const healthResponse = await fetch('https://quinors-lv-backend.ngrok.io/api/health');
      if (!healthResponse.ok) {
        throw new Error('ÏÑúÎ≤ÑÏóê Ïó∞Í≤∞Ìï† Ïàò ÏóÜÏäµÎãàÎã§.');
      }

      // Î°úÍ∑∏Ïù∏ API Ìò∏Ï∂ú
      const response = await fetch('https://quinors-lv-backend.ngrok.io/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify({
          user_id: credentials.username, // ÏÉàÎ°úÏö¥ ÌïÑÎìúÎ™ÖÏóê ÎßûÍ≤å ÏàòÏ†ï
          password: credentials.password,
          rememberMe: credentials.rememberMe || false
        })
      });

      const contentType = response.headers.get('content-type');
      if (!contentType || !contentType.includes('application/json')) {
        throw new Error('ÏÑúÎ≤ÑÏóêÏÑú Ïò¨Î∞îÎ•¥ÏßÄ ÏïäÏùÄ ÏùëÎãµÏùÑ Î∞õÏïòÏäµÎãàÎã§.');
      }

      const result = await response.json();
      console.log('üîê Î°úÍ∑∏Ïù∏ API ÏùëÎãµ:', result);

      if (response.ok && result.success) {
        const { user, token } = result.data;
        
        // Î°úÏª¨ Ïä§ÌÜ†Î¶¨ÏßÄÏóê Ï†ÄÏû•
        if (credentials.rememberMe) {
          localStorage.setItem('authToken', token);
          localStorage.setItem('userInfo', JSON.stringify(user));
        } else {
          sessionStorage.setItem('authToken', token);
          sessionStorage.setItem('userInfo', JSON.stringify(user));
        }

        dispatch({
          type: 'LOGIN_SUCCESS',
          payload: { user, token }
        });

        console.log('‚úÖ Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ:', user.charge_name || user.user_id);
      } else {
        throw new Error(result.message || 'Î°úÍ∑∏Ïù∏Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      }
    } catch (error) {
      console.error('‚ùå Î°úÍ∑∏Ïù∏ Ïò§Î•ò:', error);
      
      // Íµ¨Ï≤¥Ï†ÅÏù∏ ÏóêÎü¨ Î©îÏãúÏßÄ Ï≤òÎ¶¨
      let errorMessage = error.message;
      
      if (error.message.includes('Failed to fetch')) {
        errorMessage = 'ÏÑúÎ≤ÑÏóê Ïó∞Í≤∞Ìï† Ïàò ÏóÜÏäµÎãàÎã§. ÎÑ§Ìä∏ÏõåÌÅ¨ Ïó∞Í≤∞ÏùÑ ÌôïÏù∏Ìï¥Ï£ºÏÑ∏Ïöî.';
      } else if (error.message.includes('JSON')) {
        errorMessage = 'ÏÑúÎ≤Ñ ÏùëÎãµ Ï≤òÎ¶¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.';
      }
      
      dispatch({
        type: 'LOGIN_FAILURE',
        payload: errorMessage
      });
      
      throw new Error(errorMessage);
    }
  };

  // ÌöåÏõêÍ∞ÄÏûÖ Ìï®Ïàò
  const register = async (registerData) => {
    dispatch({ type: 'REGISTER_START' });
    
    try {
      console.log('üìù ÌöåÏõêÍ∞ÄÏûÖ ÏãúÎèÑ:', { user_id: registerData.user_id });
      
      // ÏÑúÎ≤Ñ ÏÉÅÌÉú ÌôïÏù∏
      const healthResponse = await fetch('https://quinors-lv-backend.ngrok.io/api/health');
      if (!healthResponse.ok) {
        throw new Error('ÏÑúÎ≤ÑÏóê Ïó∞Í≤∞Ìï† Ïàò ÏóÜÏäµÎãàÎã§.');
      }

      // ÌöåÏõêÍ∞ÄÏûÖ API Ìò∏Ï∂ú
      const response = await fetch('https://quinors-lv-backend.ngrok.io/api/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify({
          user_id: registerData.user_id,
          password: registerData.password,
          cust_name: registerData.cust_name,
          dong_name: registerData.dong_name,
          dong_detail: registerData.dong_detail || '',
          dept_name: registerData.dept_name,
          charge_name: registerData.charge_name,
          tel_no: registerData.tel_no.replace(/[^0-9]/g, '') // Ïà´ÏûêÎßå Ï†ÄÏû•
        })
      });

      const contentType = response.headers.get('content-type');
      if (!contentType || !contentType.includes('application/json')) {
        throw new Error('ÏÑúÎ≤ÑÏóêÏÑú Ïò¨Î∞îÎ•¥ÏßÄ ÏïäÏùÄ ÏùëÎãµÏùÑ Î∞õÏïòÏäµÎãàÎã§.');
      }

      const result = await response.json();
      console.log('üìù ÌöåÏõêÍ∞ÄÏûÖ API ÏùëÎãµ:', result);

      if (response.ok && result.success) {
        dispatch({ type: 'REGISTER_SUCCESS' });
        console.log('‚úÖ ÌöåÏõêÍ∞ÄÏûÖ ÏÑ±Í≥µ:', registerData.user_id);
        return result;
      } else {
        throw new Error(result.message || 'ÌöåÏõêÍ∞ÄÏûÖÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      }
    } catch (error) {
      console.error('‚ùå ÌöåÏõêÍ∞ÄÏûÖ Ïò§Î•ò:', error);
      
      // Íµ¨Ï≤¥Ï†ÅÏù∏ ÏóêÎü¨ Î©îÏãúÏßÄ Ï≤òÎ¶¨
      let errorMessage = error.message;
      
      if (error.message.includes('Failed to fetch')) {
        errorMessage = 'ÏÑúÎ≤ÑÏóê Ïó∞Í≤∞Ìï† Ïàò ÏóÜÏäµÎãàÎã§. ÎÑ§Ìä∏ÏõåÌÅ¨ Ïó∞Í≤∞ÏùÑ ÌôïÏù∏Ìï¥Ï£ºÏÑ∏Ïöî.';
      } else if (error.message.includes('JSON')) {
        errorMessage = 'ÏÑúÎ≤Ñ ÏùëÎãµ Ï≤òÎ¶¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.';
      }
      
      dispatch({
        type: 'REGISTER_FAILURE',
        payload: errorMessage
      });
      
      throw new Error(errorMessage);
    }
  };

  // Î°úÍ∑∏ÏïÑÏõÉ Ìï®Ïàò
  const logout = () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('userInfo');
    sessionStorage.removeItem('authToken');
    sessionStorage.removeItem('userInfo');
    
    dispatch({ type: 'LOGOUT' });
    console.log('üö™ Î°úÍ∑∏ÏïÑÏõÉ ÏôÑÎ£å');
  };

  // ÏóêÎü¨ Ï¥àÍ∏∞Ìôî Ìï®Ïàò
  const clearError = () => {
    dispatch({ type: 'CLEAR_ERROR' });
  };

  // ÎπÑÎ∞ÄÎ≤àÌò∏ Î≥ÄÍ≤Ω Ìï®Ïàò
  const changePassword = async (currentPassword, newPassword) => {
    try {
      const token = state.token || localStorage.getItem('authToken') || sessionStorage.getItem('authToken');
      
      if (!token) {
        throw new Error('Ïù∏Ï¶ù ÌÜ†ÌÅ∞Ïù¥ ÏóÜÏäµÎãàÎã§. Îã§Ïãú Î°úÍ∑∏Ïù∏Ìï¥Ï£ºÏÑ∏Ïöî.');
      }

      const response = await fetch('https://quinors-lv-backend.ngrok.io/api/auth/change-password', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          currentPassword,
          newPassword
        })
      });

      const result = await response.json();

      if (response.ok && result.success) {
        console.log('‚úÖ ÎπÑÎ∞ÄÎ≤àÌò∏ Î≥ÄÍ≤Ω ÏÑ±Í≥µ');
        return result;
      } else {
        throw new Error(result.message || 'ÎπÑÎ∞ÄÎ≤àÌò∏ Î≥ÄÍ≤ΩÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      }
    } catch (error) {
      console.error('‚ùå ÎπÑÎ∞ÄÎ≤àÌò∏ Î≥ÄÍ≤Ω Ïò§Î•ò:', error);
      throw error;
    }
  };

  // ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ ÏóÖÎç∞Ïù¥Ìä∏ Ìï®Ïàò
  const updateUserInfo = async (updateData) => {
    try {
      const token = state.token || localStorage.getItem('authToken') || sessionStorage.getItem('authToken');
      
      if (!token) {
        throw new Error('Ïù∏Ï¶ù ÌÜ†ÌÅ∞Ïù¥ ÏóÜÏäµÎãàÎã§. Îã§Ïãú Î°úÍ∑∏Ïù∏Ìï¥Ï£ºÏÑ∏Ïöî.');
      }

      const response = await fetch('https://quinors-lv-backend.ngrok.io/api/auth/profile', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(updateData)
      });

      const result = await response.json();

      if (response.ok && result.success) {
        // ÏóÖÎç∞Ïù¥Ìä∏Îêú ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥Î•º ÏÉÅÌÉúÏóê Î∞òÏòÅ
        const updatedUser = { ...state.user, ...result.data };
        
        // Ïä§ÌÜ†Î¶¨ÏßÄ ÏóÖÎç∞Ïù¥Ìä∏
        if (localStorage.getItem('userInfo')) {
          localStorage.setItem('userInfo', JSON.stringify(updatedUser));
        }
        if (sessionStorage.getItem('userInfo')) {
          sessionStorage.setItem('userInfo', JSON.stringify(updatedUser));
        }

        dispatch({
          type: 'LOGIN_SUCCESS',
          payload: { user: updatedUser, token: state.token }
        });

        console.log('‚úÖ ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ ÏóÖÎç∞Ïù¥Ìä∏ ÏÑ±Í≥µ');
        return result;
      } else {
        throw new Error(result.message || 'ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ ÏóÖÎç∞Ïù¥Ìä∏Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      }
    } catch (error) {
      console.error('‚ùå ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ ÏóÖÎç∞Ïù¥Ìä∏ Ïò§Î•ò:', error);
      throw error;
    }
  };

  // ÌÜ†ÌÅ∞ Ïú†Ìö®ÏÑ± Í≤ÄÏÇ¨ Ìï®Ïàò
  const validateToken = async () => {
    try {
      const token = state.token || localStorage.getItem('authToken') || sessionStorage.getItem('authToken');
      
      if (!token) {
        return false;
      }

      const response = await fetch('https://quinors-lv-backend.ngrok.io/api/auth/validate', {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const result = await response.json();
        return result.success;
      }
      
      return false;
    } catch (error) {
      console.error('ÌÜ†ÌÅ∞ Í≤ÄÏ¶ù Ïò§Î•ò:', error);
      return false;
    }
  };

  // Context value
  const contextValue = {
    // ÏÉÅÌÉú
    ...state,
    
    // Ìï®ÏàòÎì§
    login,
    register,
    logout,
    clearError,
    changePassword,
    updateUserInfo,
    validateToken,
    
    // Ïú†Ìã∏Î¶¨Ìã∞
    isLoggedIn: state.isAuthenticated && state.user && state.token,
    userRole: state.user?.role || 'user',
    userId: state.user?.user_id || null,
    userName: state.user?.charge_name || state.user?.user_id || null
  };

  return (
    <AuthContext.Provider value={contextValue}>
      {children}
    </AuthContext.Provider>
  );
};

// Hook
export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within AuthProvider');
  }
  return context;
};

// Í∏∞Î≥∏ ÎÇ¥Î≥¥ÎÇ¥Í∏∞
export default AuthContext;
