import React, { useState } from 'react';
import { useDelivery } from '../../context/DeliveryContext';

const FloatingPreview = ({ content, onEdit, show = true }) => {
  const [isExpanded, setIsExpanded] = useState(false);
  const { deliveryType, originStore, destinationStore, currentStep } = useDelivery();

  if (!show) return null;

  const toggleExpanded = () => {
    setIsExpanded(!isExpanded);
  };

  // Îß§Ïû• Ï†ïÎ≥¥ Ìè¨Îß∑ÌåÖ Ìï®Ïàò - departments Î∞∞Ïó¥ÏóêÏÑú Îã¥ÎãπÏûê Ï†ïÎ≥¥ Ï∂îÏ∂ú
  const formatStoreInfo = (store) => {
    if (!store) return null;
    
    let managerInfo = {
        name: 'Îã¥ÎãπÏûê ÏóÜÏùå',
        phone: 'Ïó∞ÎùΩÏ≤ò ÏóÜÏùå'
    };

    // 1. departments Î∞∞Ïó¥ÏóêÏÑú ÏÑ†ÌÉùÎêú Î∂ÄÏÑú Îã¥ÎãπÏûê Ï†ïÎ≥¥ Ï∞æÍ∏∞
    if (store.departments && store.departments.length > 0) {
        let selectedDepartment = null;
        
        // üéØ ÏÑ†ÌÉùÎêú Î∂ÄÏÑú Ï†ïÎ≥¥ ÌôïÏù∏ Î∞©Î≤ïÎì§
        if (store.selectedDepartment) {
        // Î∞©Î≤ï 1: selectedDepartment ÌïÑÎìúÍ∞Ä ÏûàÎäî Í≤ΩÏö∞
        selectedDepartment = store.departments.find(dept => 
            dept.department === store.selectedDepartment
        );
        } else if (store.selectedDepartmentIndex !== undefined) {
        // Î∞©Î≤ï 2: selectedDepartmentIndexÍ∞Ä ÏûàÎäî Í≤ΩÏö∞
        selectedDepartment = store.departments[store.selectedDepartmentIndex];
        } else if (store.departmentId) {
        // Î∞©Î≤ï 3: departmentIdÎ°ú Ï∞æÎäî Í≤ΩÏö∞
        selectedDepartment = store.departments.find(dept => 
            dept._id && dept._id.toString() === store.departmentId
        );
        }
        
        // üö® ÏÑ†ÌÉùÎêú Î∂ÄÏÑúÍ∞Ä ÏóÜÎäî Í≤ΩÏö∞ - ÎîîÎ≤ÑÍπÖ Ï†ïÎ≥¥ Ï∂úÎ†•
        if (!selectedDepartment) {
        console.warn('‚ö†Ô∏è ÏÑ†ÌÉùÎêú Î∂ÄÏÑú Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§:', {
            storeName: store.storeName || store.name,
            selectedDepartment: store.selectedDepartment,
            selectedDepartmentIndex: store.selectedDepartmentIndex,
            departmentId: store.departmentId,
            availableDepartments: store.departments.map(d => d.department)
        });
        
        // ÏûÑÏãúÎ°ú Ï≤´ Î≤àÏß∏ Î∂ÄÏÑú ÏÇ¨Ïö©ÌïòÏßÄÎßå Í≤ΩÍ≥† ÌëúÏãú
        selectedDepartment = store.departments[0];
        }
        
        // Îã¥ÎãπÏûê Ï†ïÎ≥¥ ÏÑ§Ï†ï
        if (selectedDepartment) {
        managerInfo = {
            name: selectedDepartment.managerName || 'Îã¥ÎãπÏûê ÏóÜÏùå',
            phone: selectedDepartment.fullPhone || 'Ïó∞ÎùΩÏ≤ò ÏóÜÏùå',
            department: selectedDepartment.department || ''
        };
        }
    }
    
    // 2. Í∏∞Ï°¥ Î∞©Ïãù ÏßÄÏõê (Ìò∏ÌôòÏÑ±)
    else if (store.managerName || store.fullPhone) {
        managerInfo = {
        name: store.managerName || 'Îã¥ÎãπÏûê ÏóÜÏùå',
        phone: store.fullPhone || store.phone || store.phoneNumber || 'Ïó∞ÎùΩÏ≤ò ÏóÜÏùå'
        };
    }

    const info = {
        name: store.name || store.storeName || 'Îß§Ïû•Î™Ö ÏóÜÏùå',
        address: store.address || 'Ï£ºÏÜå ÏóÜÏùå',
        manager: managerInfo.name,
        phone: managerInfo.phone,
        department: managerInfo.department || null
    };
    
    return info;
    };

  // Îã®Í≥ÑÎ≥Ñ Ï†ïÎ≥¥ Íµ¨ÏÑ±
  const getStepInfo = () => {
    const steps = [];
    
    // 1Îã®Í≥Ñ: Î∞∞ÏÜ° Ïú†Ìòï
    if (deliveryType) {
      steps.push({
        step: 1,
        title: 'Î∞∞ÏÜ° Ïú†Ìòï',
        content: deliveryType,
        icon: 'fa-truck',
        completed: true
      });
    }
    
    // 2Îã®Í≥Ñ: Ï∂úÎ∞úÏßÄ
    if (originStore) {
      const storeInfo = formatStoreInfo(originStore);
      steps.push({
        step: 2,
        title: 'Ï∂úÎ∞úÏßÄ',
        content: storeInfo.name,
        detail: storeInfo.address,
        managerInfo: {
          name: storeInfo.manager,
          phone: storeInfo.phone,
          department: storeInfo.department // üëà Î∂ÄÏÑú Ï†ïÎ≥¥ Ï∂îÍ∞Ä
        },
        icon: 'fa-store',
        completed: true
      });
    }
    
    // 3Îã®Í≥Ñ: ÎèÑÏ∞©ÏßÄ
    if (destinationStore) {
      const storeInfo = formatStoreInfo(destinationStore);
      steps.push({
        step: 3,
        title: 'ÎèÑÏ∞©ÏßÄ',
        content: storeInfo.name,
        detail: storeInfo.address,
        managerInfo: {
          name: storeInfo.manager,
          phone: storeInfo.phone,
          department: storeInfo.department // üëà Î∂ÄÏÑú Ï†ïÎ≥¥ Ï∂îÍ∞Ä
        },
        icon: 'fa-map-marker-alt',
        completed: true
      });
    }

    // ÌòÑÏû¨ Îã®Í≥Ñ ÌëúÏãú (ÎØ∏ÏôÑÎ£å Îã®Í≥Ñ)
    if (currentStep > steps.length) {
      const stepTitles = ['', 'Î∞∞ÏÜ° Ïú†Ìòï', 'Ï∂úÎ∞úÏßÄ', 'ÎèÑÏ∞©ÏßÄ', 'Î∞∞ÏÜ° Ï†ïÎ≥¥'];
      steps.push({
        step: currentStep,
        title: stepTitles[currentStep],
        content: 'ÏÑ†ÌÉù Ï§ë...',
        icon: 'fa-clock',
        completed: false,
        current: true
      });
    }

    return steps;
  };

  const stepInfo = getStepInfo();

  return (
    <>
      {/* Î∞∞Í≤Ω Ïò§Î≤ÑÎ†àÏù¥ (ÌôïÏû•Îê† ÎïåÎßå) */}
      {isExpanded && (
        <div 
          className="fixed inset-0 bg-black/20 backdrop-blur-sm z-40 transition-opacity duration-300"
          onClick={toggleExpanded}
        />
      )}

      {/* Ï¢åÏ∏° Ïä¨ÎùºÏù¥Îìú Ìå®ÎÑê */}
      <div className={`fixed left-0 top-0 bottom-0 z-50 transition-transform duration-300 ease-out ${
        isExpanded ? 'translate-x-0' : '-translate-x-full'
      }`}>
        
        {/* Î©îÏù∏ Ìå®ÎÑê */}
        <div className="h-full w-80 bg-white shadow-2xl flex flex-col">
          
          {/* Ìó§Îçî */}
          <div className="bg-gradient-to-r from-orange-400 to-orange-500 text-white p-4">
            <div className="flex items-center justify-between">
              <h3 className="font-bold text-lg">ÏÑ†ÌÉù Ï†ïÎ≥¥</h3>
              <button 
                onClick={toggleExpanded}
                className="text-white/80 hover:text-white transition-colors p-2"
              >
                <i className="fa-solid fa-times"></i>
              </button>
            </div>
            <p className="text-white/80 text-sm mt-1">Îã®Í≥ÑÎ≥Ñ ÏÑ†ÌÉù ÎÇ¥Ïö©</p>
          </div>

          {/* Îã®Í≥ÑÎ≥Ñ Ï†ïÎ≥¥ */}
          <div className="flex-1 p-6 overflow-y-auto">
            <div className="space-y-4">
              {stepInfo.map((step, index) => (
                <div key={step.step} className="relative">
                  
                  <div className="flex items-start gap-4">
                    {/* Îã®Í≥Ñ ÏïÑÏù¥ÏΩò */}
                    <div className={`w-10 h-10 rounded-full flex items-center justify-center flex-shrink-0 ${
                      step.completed 
                        ? 'bg-gradient-to-br from-orange-400 to-orange-500 text-white' 
                        : step.current
                        ? 'bg-blue-100 text-blue-600'
                        : 'bg-gray-100 text-gray-400'
                    }`}>
                      <i className={`fa-solid ${step.icon}`}></i>
                    </div>

                    {/* Îã®Í≥Ñ Ï†ïÎ≥¥ */}
                    <div className="flex-1 min-w-0">
                      <div className="flex items-center gap-2 mb-2">
                        <span className={`text-sm font-bold ${
                          step.completed 
                            ? 'text-gray-800' 
                            : step.current
                            ? 'text-blue-600'
                            : 'text-gray-400'
                        }`}>
                          STEP {step.step}
                        </span>
                        {step.completed && (
                          <i className="fa-solid fa-check-circle text-green-500"></i>
                        )}
                        {step.current && (
                          <i className="fa-solid fa-clock text-blue-500"></i>
                        )}
                      </div>
                      
                      <h4 className={`font-semibold mb-1 ${
                        step.completed 
                          ? 'text-gray-800' 
                          : step.current
                          ? 'text-blue-600'
                          : 'text-gray-400'
                      }`}>
                        {step.title}
                      </h4>
                      
                      <p className={`text-sm font-medium mb-2 ${
                        step.completed 
                          ? 'text-gray-700' 
                          : step.current
                          ? 'text-blue-600'
                          : 'text-gray-400'
                      }`}>
                        {step.content}
                      </p>
                      
                      {step.detail && (
                        <p className="text-xs text-gray-500 mb-2 flex items-center gap-1">
                          <i className="fa-solid fa-map-marker-alt text-gray-400"></i>
                          {step.detail}
                        </p>
                      )}

                      {/* Îã¥ÎãπÏûê Ï†ïÎ≥¥ ÌëúÏãú - Î∂ÄÏÑú Ï†ïÎ≥¥ Ìè¨Ìï® */}
                      {step.managerInfo && (
                        <div className="bg-gray-50 rounded-lg p-3 mt-2 space-y-1">
                          {/* Î∂ÄÏÑú Ï†ïÎ≥¥ ÌëúÏãú */}
                          {step.managerInfo.department && (
                            <div className="flex items-center gap-2 text-xs">
                              <i className="fa-solid fa-building text-gray-400"></i>
                              <span className="text-gray-600">Îß§Ïû•:</span>
                              <span className="font-medium text-orange-600">
                                {step.managerInfo.department}Îß§Ïû•
                              </span>
                            </div>
                          )}
                          <div className="flex items-center gap-2 text-xs">
                            <i className="fa-solid fa-user text-gray-400"></i>
                            <span className="text-gray-600">Îã¥ÎãπÏûê:</span>
                            <span className="font-medium text-gray-800">
                              {step.managerInfo.name}
                            </span>
                          </div>
                          <div className="flex items-center gap-2 text-xs">
                            <i className="fa-solid fa-phone text-gray-400"></i>
                            <span className="text-gray-600">Ïó∞ÎùΩÏ≤ò:</span>
                            <span className="font-medium text-gray-800">
                              {step.managerInfo.phone}
                            </span>
                          </div>
                        </div>
                      )}
                    </div>
                  </div>

                  {/* Ïó∞Í≤∞ÏÑ† */}
                  {index < stepInfo.length - 1 && (
                    <div className="absolute left-5 top-10 w-0.5 h-8 bg-gray-200"></div>
                  )}
                </div>
              ))}
            </div>

            {/* ÏßÑÌñâÎ•† ÌëúÏãú */}
            <div className="mt-8 p-4 bg-gray-50 rounded-xl">
              <div className="flex items-center justify-between mb-3">
                <span className="text-sm text-gray-600 font-medium">Ï†ÑÏ≤¥ ÏßÑÌñâÎ•†</span>
                <span className="text-lg text-orange-600 font-bold">
                  {Math.round((stepInfo.filter(s => s.completed).length / 4) * 100)}%
                </span>
              </div>
              <div className="w-full bg-gray-200 rounded-full h-3">
                <div 
                  className="bg-gradient-to-r from-orange-400 to-orange-500 h-3 rounded-full transition-all duration-500"
                  style={{ 
                    width: `${(stepInfo.filter(s => s.completed).length / 4) * 100}%` 
                  }}
                ></div>
              </div>
              <div className="text-xs text-gray-500 mt-2 text-center">
                {stepInfo.filter(s => s.completed).length} / 4 Îã®Í≥Ñ ÏôÑÎ£å
              </div>
            </div>

            {/* ÏöîÏïΩ Ï†ïÎ≥¥ */}
            {stepInfo.filter(s => s.completed).length >= 2 && (
              <div className="mt-6 p-4 bg-blue-50 rounded-xl border border-blue-100">
                <h5 className="font-semibold text-blue-800 mb-3 flex items-center gap-2">
                  <i className="fa-solid fa-info-circle"></i>
                  Î∞∞ÏÜ° ÏöîÏïΩ
                </h5>
                <div className="space-y-2 text-sm">
                  {deliveryType && (
                    <div className="flex items-center gap-2">
                      <i className="fa-solid fa-truck text-blue-600"></i>
                      <span className="text-blue-700 font-medium">{deliveryType}</span>
                    </div>
                  )}
                  {originStore && destinationStore && (
                    <div className="text-xs text-blue-600 bg-blue-100 rounded-lg p-2 mt-2">
                      <div className="flex items-center justify-between">
                        <span>Ï∂úÎ∞ú:</span>
                        <div className="text-right">
                          <div className="font-medium">{formatStoreInfo(originStore).name}</div>
                          {formatStoreInfo(originStore).department && (
                            <div className="text-orange-600">
                              {formatStoreInfo(originStore).department}Î∂Ä
                            </div>
                          )}
                        </div>
                      </div>
                      <div className="flex items-center justify-center my-1">
                        <i className="fa-solid fa-arrow-down text-blue-500"></i>
                      </div>
                      <div className="flex items-center justify-between">
                        <span>ÎèÑÏ∞©:</span>
                        <div className="text-right">
                          <div className="font-medium">{formatStoreInfo(destinationStore).name}</div>
                          {formatStoreInfo(destinationStore).department && (
                            <div className="text-orange-600">
                              {formatStoreInfo(destinationStore).department}Î∂Ä
                            </div>
                          )}
                        </div>
                      </div>
                    </div>
                  )}
                </div>
              </div>
            )}
          </div>

          {/* ÌïòÎã® Ïï°ÏÖò Î≤ÑÌäº */}
          <div className="p-6 bg-white border-t border-gray-100">
            <div className="space-y-3">
              <button 
                onClick={onEdit}
                className="w-full bg-white border-2 border-orange-500 text-orange-500 py-3 px-4 rounded-xl font-semibold hover:bg-orange-50 transition-colors"
              >
                <i className="fa-solid fa-edit mr-2"></i>
                Ï≤òÏùåÎ∂ÄÌÑ∞ Îã§Ïãú ÏÑ†ÌÉù
              </button>
              <button 
                onClick={toggleExpanded}
                className="w-full bg-gradient-to-r from-orange-400 to-orange-500 text-white py-3 px-4 rounded-xl font-semibold hover:shadow-lg transition-all"
              >
                <i className="fa-solid fa-check mr-2"></i>
                ÌôïÏù∏ ÏôÑÎ£å
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Ï¢åÏ∏° Í∞ÄÏû•ÏûêÎ¶¨ ÌÉ≠ (Ìï≠ÏÉÅ ÌëúÏãú) */}
      <div className={`fixed left-0 top-1/2 transform -translate-y-1/2 z-40 transition-all duration-300 ${
        isExpanded ? 'opacity-0 pointer-events-none' : 'opacity-100'
      }`}>
        <button
          onClick={toggleExpanded}
          className="bg-gradient-to-r from-orange-400 to-orange-500 text-white p-3 rounded-r-xl shadow-lg hover:shadow-xl transition-all duration-300 group"
        >
          <div className="flex flex-col items-center gap-1">
            <i className="fa-solid fa-chevron-right group-hover:translate-x-0.5 transition-transform"></i>
            <div className="writing-mode-vertical text-xs font-medium">
              ÏÑ†ÌÉùÏ†ïÎ≥¥
            </div>
            {stepInfo.filter(s => s.completed).length > 0 && (
              <div className="w-2 h-2 bg-white rounded-full animate-pulse"></div>
            )}
          </div>
        </button>
      </div>
    </>
  );
};

export default FloatingPreview;
