import React, { useState } from 'react';
import { useDelivery } from '../../context/DeliveryContext';
import { useAuth } from '../../context/AuthContext';
import AddressConverter from './AddressConverter';

const FloatingPreview = ({ content, onEdit, show = true }) => {
  const [isExpanded, setIsExpanded] = useState(false);
  const { deliveryType, originStore, destinationStore, currentStep } = useDelivery();
  const { user } = useAuth(); // Î°úÍ∑∏Ïù∏Îêú ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥
  
  // Ï£ºÏÜå Î≥ÄÌôò Í≤∞Í≥º Ï†ÄÏû•
  const [originConvertedAddress, setOriginConvertedAddress] = useState(null);
  const [destinationConvertedAddress, setDestinationConvertedAddress] = useState(null);

  if (!show) return null;

  const toggleExpanded = () => {
    setIsExpanded(!isExpanded);
  };

  // ÏùòÎ¢∞Ïûê Ï†ïÎ≥¥ Ìè¨Îß∑ÌåÖ Ìï®Ïàò
  const formatRequesterInfo = (userInfo) => {
    if (!userInfo) return null;

    return {
      custName: userInfo.cust_name || 'ÏÉÅÌò∏Î™Ö ÏóÜÏùå',
      deptName: userInfo.dept_name || 'Î∂ÄÏÑúÎ™Ö ÏóÜÏùå', 
      chargeName: userInfo.charge_name || 'Îã¥ÎãπÏûêÎ™Ö ÏóÜÏùå',
      telNo: userInfo.tel_no || 'Ïó∞ÎùΩÏ≤ò ÏóÜÏùå'
    };
  };

  // Îß§Ïû• Ï†ïÎ≥¥ Ìè¨Îß∑ÌåÖ Ìï®Ïàò - Í∞úÏÑ†Îêú Î≤ÑÏ†Ñ
  const formatStoreInfo = (store) => {
    if (!store) return null;

    let managerInfo = {
      name: 'Îã¥ÎãπÏûê ÏóÜÏùå',
      phone: 'Ïó∞ÎùΩÏ≤ò ÏóÜÏùå',
      department: null
    };

    // üéØ 1. selectedManagerInfoÍ∞Ä ÏûàÎäî Í≤ΩÏö∞ (ÏÉàÎ°úÏö¥ Î∞©Ïãù - Ïö∞ÏÑ†ÏàúÏúÑ ÎÜíÏùå)
    if (store.selectedManagerInfo) {
      console.log('‚úÖ selectedManagerInfo Î∞úÍ≤¨:', store.selectedManagerInfo);
      managerInfo = {
        name: store.selectedManagerInfo.name || 'Îã¥ÎãπÏûê ÏóÜÏùå',
        phone: store.selectedManagerInfo.phone || 'Ïó∞ÎùΩÏ≤ò ÏóÜÏùå',
        department: store.selectedManagerInfo.department || null
      };
    }
    // üéØ 2. departments Î∞∞Ïó¥ÏóêÏÑú ÏÑ†ÌÉùÎêú Î∂ÄÏÑú Îã¥ÎãπÏûê Ï†ïÎ≥¥ Ï∞æÍ∏∞
    else if (store.departments && store.departments.length > 0) {
      let selectedDepartment = null;

      // ÏÑ†ÌÉùÎêú Î∂ÄÏÑú Ï†ïÎ≥¥ ÌôïÏù∏ Î∞©Î≤ïÎì§
      if (store.selectedDepartment) {
        selectedDepartment = store.departments.find(dept =>
          dept.department === store.selectedDepartment
        );
      } else if (store.selectedDepartmentIndex !== undefined) {
        selectedDepartment = store.departments[store.selectedDepartmentIndex];
      } else if (store.departmentId) {
        selectedDepartment = store.departments.find(dept =>
          dept._id && dept._id.toString() === store.departmentId
        );
      }

      // ÏÑ†ÌÉùÎêú Î∂ÄÏÑúÍ∞Ä ÏóÜÎäî Í≤ΩÏö∞ ÎîîÎ≤ÑÍπÖ
      if (!selectedDepartment) {
        console.warn('‚ö†Ô∏è ÏÑ†ÌÉùÎêú Î∂ÄÏÑú Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§:', {
          storeName: store.storeName || store.name,
          selectedDepartment: store.selectedDepartment,
          selectedDepartmentIndex: store.selectedDepartmentIndex,
          departmentId: store.departmentId,
          availableDepartments: store.departments.map(d => d.department)
        });
        selectedDepartment = store.departments[0];
      }

      // Îã¥ÎãπÏûê Ï†ïÎ≥¥ ÏÑ§Ï†ï
      if (selectedDepartment) {
        managerInfo = {
          name: selectedDepartment.managerName || 'Îã¥ÎãπÏûê ÏóÜÏùå',
          phone: selectedDepartment.fullPhone || selectedDepartment.managerPhone || 'Ïó∞ÎùΩÏ≤ò ÏóÜÏùå',
          department: selectedDepartment.department || ''
        };
      }
    }
    // üéØ 3. Í∏∞Ï°¥ Î∞©Ïãù ÏßÄÏõê (Ìò∏ÌôòÏÑ±)
    else if (store.managerName || store.fullPhone) {
      managerInfo = {
        name: store.managerName || 'Îã¥ÎãπÏûê ÏóÜÏùå',
        phone: store.fullPhone || store.phone || store.phoneNumber || 'Ïó∞ÎùΩÏ≤ò ÏóÜÏùå'
      };
    }

    const info = {
      name: store.name || store.storeName || 'Îß§Ïû•Î™Ö ÏóÜÏùå',
      address: store.address || 'Ï£ºÏÜå ÏóÜÏùå',
      manager: managerInfo.name,
      phone: managerInfo.phone,
      department: managerInfo.department || null
    };

    console.log('üè™ Îß§Ïû• Ï†ïÎ≥¥ Ìè¨Îß∑ÌåÖ Í≤∞Í≥º:', info);
    return info;
  };

  // Ï£ºÏÜå Î≥ÄÌôò ÏΩúÎ∞± Ìï®ÏàòÎì§
  const handleOriginAddressConverted = (convertedData) => {
    console.log('Ï∂úÎ∞úÏßÄ Ï£ºÏÜå Î≥ÄÌôò ÏôÑÎ£å:', convertedData);
    setOriginConvertedAddress(convertedData);
  };

  const handleDestinationAddressConverted = (convertedData) => {
    console.log('ÎèÑÏ∞©ÏßÄ Ï£ºÏÜå Î≥ÄÌôò ÏôÑÎ£å:', convertedData);
    setDestinationConvertedAddress(convertedData);
  };

  // Îã®Í≥ÑÎ≥Ñ Ï†ïÎ≥¥ Íµ¨ÏÑ±
  const getStepInfo = () => {
    const steps = [];

    // 0Îã®Í≥Ñ: ÏùòÎ¢∞Ïûê Ï†ïÎ≥¥ (ÏÉàÎ°ú Ï∂îÍ∞Ä)
    if (user) {
      const requesterInfo = formatRequesterInfo(user);
      if (requesterInfo) {
        steps.push({
          step: 0,
          title: 'ÏùòÎ¢∞Ïûê Ï†ïÎ≥¥',
          content: (
            <div className="text-left">
              <div className="font-medium text-gray-800">{requesterInfo.custName}</div>
              <div className="text-xs text-gray-600 mt-1">
                {/* Î∂ÄÏÑúÎ™Ö */}
                <div className="flex items-center gap-1">
                  <i className="fa-solid fa-building text-purple-500"></i>
                  <span>{requesterInfo.deptName}</span>
                </div>
                
                {/* Îã¥ÎãπÏûêÎ™Ö */}
                <div className="flex items-center gap-1 mt-1">
                  <i className="fa-solid fa-user-tie text-orange-500"></i>
                  <span>{requesterInfo.chargeName}</span>
                </div>
                
                {/* Ïó∞ÎùΩÏ≤ò */}
                <div className="flex items-center gap-1 mt-1">
                  <i className="fa-solid fa-phone text-green-500"></i>
                  <span>{requesterInfo.telNo}</span>
                </div>
              </div>
            </div>
          ),
          icon: 'fa-user-circle',
          completed: true
        });
      }
    }

    // 1Îã®Í≥Ñ: Î∞∞ÏÜ° Ïú†Ìòï
    if (deliveryType) {
      steps.push({
        step: 1,
        title: 'Î∞∞ÏÜ° Ïú†Ìòï',
        content: deliveryType,
        icon: 'fa-truck',
        completed: true
      });
    }

    // 2Îã®Í≥Ñ: Ï∂úÎ∞úÏßÄ - Ï£ºÏÜå Î≥ÄÌôò Ìè¨Ìï®
    if (originStore) {
      const originInfo = formatStoreInfo(originStore);
      steps.push({
        step: 2,
        title: 'Ï∂úÎ∞úÏßÄ',
        content: originInfo ? (
          <div className="text-left">
            <div className="font-medium text-gray-800">{originInfo.name}</div>
            <div className="text-xs text-gray-600 mt-1">
              {/* Îã¥ÎãπÏûê Ï†ïÎ≥¥ */}
              <div className="flex items-center gap-1">
                <i className="fa-solid fa-user text-orange-500"></i>
                <span>{originInfo.manager}</span>
                {originInfo.department && (
                  <span className="ml-1 px-1.5 py-0.5 bg-orange-100 text-orange-700 text-xs rounded">
                    {originInfo.department}
                  </span>
                )}
              </div>
              
              {/* Ïó∞ÎùΩÏ≤ò */}
              <div className="flex items-center gap-1 mt-1">
                <i className="fa-solid fa-phone text-green-500"></i>
                <span>{originInfo.phone}</span>
              </div>
              
              {/* Ï£ºÏÜå Î∞è Î≥ÄÌôò */}
              <div className="mt-2 space-y-1">
                {/* ÎèÑÎ°úÎ™ÖÏ£ºÏÜå */}
                <div className="flex items-center gap-1">
                  <i className="fa-solid fa-map-marker-alt text-blue-500"></i>
                  <span className="line-clamp-2">{originInfo.address}</span>
                </div>
                
                {/* ÏßÄÎ≤àÏ£ºÏÜå (Ïπ¥Ïπ¥Ïò§ APIÎ°ú Î≥ÄÌôòÎêú Ï£ºÏÜå) */}
                <AddressConverter
                  originalAddress={originInfo.address}
                  onAddressConverted={handleOriginAddressConverted}
                  showOriginal={false}
                  className=""
                />
              </div>
            </div>
          </div>
        ) : 'Ï∂úÎ∞úÏßÄ Ï†ïÎ≥¥ ÏóÜÏùå',
        icon: 'fa-store',
        completed: true
      });
    }

    // 3Îã®Í≥Ñ: ÎèÑÏ∞©ÏßÄ - Ï£ºÏÜå Î≥ÄÌôò Ìè¨Ìï®
    if (destinationStore) {
      const destinationInfo = formatStoreInfo(destinationStore);
      steps.push({
        step: 3,
        title: 'ÎèÑÏ∞©ÏßÄ',
        content: destinationInfo ? (
          <div className="text-left">
            <div className="font-medium text-gray-800">{destinationInfo.name}</div>
            <div className="text-xs text-gray-600 mt-1">
              {/* Îã¥ÎãπÏûê Ï†ïÎ≥¥ */}
              <div className="flex items-center gap-1">
                <i className="fa-solid fa-user text-orange-500"></i>
                <span>{destinationInfo.manager}</span>
                {destinationInfo.department && (
                  <span className="ml-1 px-1.5 py-0.5 bg-orange-100 text-orange-700 text-xs rounded">
                    {destinationInfo.department}
                  </span>
                )}
              </div>
              
              {/* Ïó∞ÎùΩÏ≤ò */}
              <div className="flex items-center gap-1 mt-1">
                <i className="fa-solid fa-phone text-green-500"></i>
                <span>{destinationInfo.phone}</span>
              </div>
              
              {/* Ï£ºÏÜå Î∞è Î≥ÄÌôò */}
              <div className="mt-2 space-y-1">
                {/* ÎèÑÎ°úÎ™ÖÏ£ºÏÜå */}
                <div className="flex items-center gap-1">
                  <i className="fa-solid fa-map-marker-alt text-blue-500"></i>
                  <span className="line-clamp-2">{destinationInfo.address}</span>
                </div>
                
                {/* ÏßÄÎ≤àÏ£ºÏÜå (Ïπ¥Ïπ¥Ïò§ APIÎ°ú Î≥ÄÌôòÎêú Ï£ºÏÜå) */}
                <AddressConverter
                  originalAddress={destinationInfo.address}
                  onAddressConverted={handleDestinationAddressConverted}
                  showOriginal={false}
                  className=""
                />
              </div>
            </div>
          </div>
        ) : 'ÎèÑÏ∞©ÏßÄ Ï†ïÎ≥¥ ÏóÜÏùå',
        icon: 'fa-flag-checkered',
        completed: true
      });
    }

    // 4Îã®Í≥Ñ: Î∞∞ÏÜ° Ï†ïÎ≥¥
    steps.push({
      step: 4,
      title: 'Î∞∞ÏÜ° Ï†ïÎ≥¥',
      content: currentStep >= 4 ? 'ÏûÖÎ†• Ï§ë...' : 'ÎØ∏ÏôÑÎ£å',
      icon: 'fa-clipboard-list',
      completed: currentStep > 4
    });

    return steps;
  };

  const stepInfo = getStepInfo();

  return (
    <>
      {/* ÌôïÏû•Îêú ÎØ∏Î¶¨Î≥¥Í∏∞ Ìå®ÎÑê */}
      <div className={`fixed inset-y-0 left-0 z-50 w-full max-w-sm bg-white shadow-2xl transform transition-transform duration-300 ease-in-out ${
        isExpanded ? 'translate-x-0' : '-translate-x-full'
      }`}>
        <div className="flex flex-col h-full">
          {/* Ìó§Îçî */}
          <div className="p-6 bg-gradient-to-r from-orange-400 to-orange-500 text-white">
            <div className="flex items-center justify-between">
              <h2 className="text-xl font-bold">ÏÑ†ÌÉù Ï†ïÎ≥¥</h2>
              <button
                onClick={toggleExpanded}
                className="p-2 hover:bg-white hover:bg-opacity-20 rounded-lg transition-colors"
              >
                <i className="fa-solid fa-times text-lg"></i>
              </button>
            </div>
            <p className="text-orange-100 text-sm mt-1">Î∞∞ÏÜ° ÏöîÏ≤≠ ÏßÑÌñâ ÏÉÅÌô©</p>
          </div>

          {/* Îã®Í≥ÑÎ≥Ñ Ï†ïÎ≥¥ */}
          <div className="flex-1 overflow-y-auto p-6">
            {stepInfo.map((step, index) => (
              <div key={`step-${step.step}`} className="relative">
                <div className={`flex items-start gap-4 pb-6 ${
                  step.completed ? 'text-gray-800' : 'text-gray-400'
                }`}>
                  {/* Îã®Í≥Ñ ÏïÑÏù¥ÏΩò */}
                  <div className={`flex-shrink-0 w-10 h-10 rounded-full flex items-center justify-center ${
                    step.completed 
                      ? 'bg-gradient-to-br from-orange-400 to-orange-500 text-white shadow-lg' 
                      : 'bg-gray-200 text-gray-500'
                  }`}>
                    <i className={`fa-solid ${step.icon} text-sm`}></i>
                  </div>

                  {/* Îã®Í≥Ñ ÎÇ¥Ïö© */}
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center gap-2 mb-1">
                      <h3 className={`font-semibold text-sm ${
                        step.completed ? 'text-gray-800' : 'text-gray-500'
                      }`}>
                        {step.step === 0 ? 'ÏùòÎ¢∞Ïûê' : `${step.step}Îã®Í≥Ñ`}. {step.title}
                      </h3>
                      {step.completed && (
                        <i className="fa-solid fa-check-circle text-green-500 text-xs"></i>
                      )}
                    </div>
                    <div className={`text-sm ${
