import React, { createContext, useContext, useReducer, useEffect } from 'react';

const AuthContext = createContext();

const initialState = {
  isAuthenticated: false,
  user: null,
  loading: false,
  error: null,
  token: null
};

const authReducer = (state, action) => {
  switch (action.type) {
    case 'LOGIN_START':
      return { ...state, loading: true, error: null };
    case 'LOGIN_SUCCESS':
      return {
        ...state,
        isAuthenticated: true,
        user: action.payload.user,
        token: action.payload.token,
        loading: false,
        error: null
      };
    case 'LOGIN_FAILURE':
      return {
        ...state,
        isAuthenticated: false,
        user: null,
        token: null,
        loading: false,
        error: action.payload
      };
    case 'LOGOUT':
      return {
        ...state,
        isAuthenticated: false,
        user: null,
        token: null,
        loading: false,
        error: null
      };
    case 'RESTORE_SESSION':
      return {
        ...state,
        isAuthenticated: true,
        user: action.payload.user,
        token: action.payload.token,
        loading: false
      };
    case 'CLEAR_ERROR':
      return { ...state, error: null };
    default:
      return state;
  }
};

export const AuthProvider = ({ children }) => {
  const [state, dispatch] = useReducer(authReducer, initialState);

  // íŽ˜ì´ì§€ ë¡œë“œ ì‹œ ì €ìž¥ëœ ì¸ì¦ ì •ë³´ ë³µì›
  useEffect(() => {
    const token = localStorage.getItem('authToken');
    const userInfo = localStorage.getItem('userInfo');
    
    if (token && userInfo) {
      try {
        const parsedUserInfo = JSON.parse(userInfo);
        dispatch({
          type: 'RESTORE_SESSION',
          payload: {
            user: parsedUserInfo,
            token: token
          }
        });
        console.log('âœ… ì„¸ì…˜ ë³µì› ì„±ê³µ:', parsedUserInfo.managerName);
      } catch (error) {
        console.error('ì„¸ì…˜ ë³µì› ì˜¤ë¥˜:', error);
        localStorage.removeItem('authToken');
        localStorage.removeItem('userInfo');
      }
    }
  }, []);

  const login = async (credentials) => {
    dispatch({ type: 'LOGIN_START' });
    
    try {
      console.log('ðŸ” ë¡œê·¸ì¸ ì‹œë„:', { userId: credentials.username });

      // ì„œë²„ ìƒíƒœ í™•ì¸
      const healthResponse = await fetch('http://localhost:5480/api/health');
      if (!healthResponse.ok) {
        throw new Error('ì„œë²„ì— ì—°ê²°í•  ìˆ˜ ì—†ìŠµë‹ˆë‹¤.');
      }

      // ë¡œê·¸ì¸ API í˜¸ì¶œ
      const response = await fetch('http://localhost:5480/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
        },
        body: JSON.stringify({
          userId: credentials.username,
          password: credentials.password
        }),
      });

      const result = await response.json();
      console.log('ðŸ“¨ ë¡œê·¸ì¸ ì‘ë‹µ:', result);

      if (response.ok && result.success) {
        const { token, user } = result.data;
        
        // í† í°ê³¼ ì‚¬ìš©ìž ì •ë³´ ì €ìž¥
        if (credentials.rememberMe) {
          localStorage.setItem('authToken', token);
          localStorage.setItem('userInfo', JSON.stringify(user));
        } else {
          sessionStorage.setItem('authToken', token);
          sessionStorage.setItem('userInfo', JSON.stringify(user));
        }
        
        dispatch({
          type: 'LOGIN_SUCCESS',
          payload: { user, token }
        });
        
        console.log('âœ… ë¡œê·¸ì¸ ì„±ê³µ:', user.managerName);
        return { user, token };
      } else {
        throw new Error(result.message || 'ë¡œê·¸ì¸ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.');
      }
    } catch (error) {
      console.error('âŒ ë¡œê·¸ì¸ ì˜¤ë¥˜:', error);
      dispatch({
        type: 'LOGIN_FAILURE',
        payload: error.message
      });
      throw error;
    }
  };

  const logout = () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('userInfo');
    sessionStorage.removeItem('authToken');
    sessionStorage.removeItem('userInfo');
    dispatch({ type: 'LOGOUT' });
    console.log('ðŸšª ë¡œê·¸ì•„ì›ƒ ì™„ë£Œ');
  };

  const clearError = () => {
    dispatch({ type: 'CLEAR_ERROR' });
  };

  return (
    <AuthContext.Provider value={{
      ...state,
      login,
      logout,
      clearError
    }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within AuthProvider');
  }
  return context;
};
