                    </div>
                    <div className="flex justify-between items-center text-sm">
                      <span className="text-gray-500">Ïã†Ï≤≠ÏùºÏãú</span>
                      <span className="font-semibold text-gray-700">{requestResult?.requestDate}</span>
                    </div>
                    <div className="flex justify-between items-center text-sm">
                      <span className="text-gray-500">Ï≤òÎ¶¨ÏÉÅÌÉú</span>
                      <span className={`font-bold ${statusConfig.statusColor}`}>{statusConfig.statusText}</span>
                    </div>
                  </div>

                  {/* ÏÑ±Í≥µ Ïãú Í≥ÑÏ†ï Ï†ïÎ≥¥ ÌëúÏãú */}
                  {statusConfig.showAccountInfo && requestResult?.status === 'auto_approved' && (
                    <div className="mt-6 pt-6 border-t border-gray-100">
                      <h3 className="font-bold text-gray-700 mb-4">Î∞úÍ∏âÎêú Í≥ÑÏ†ï Ï†ïÎ≥¥</h3>
                      <div className="space-y-3 bg-gray-50 p-4 rounded-lg">
                        <div className="flex justify-between items-center text-sm">
                          <span className="text-gray-500">ÏÇ¨Ïö©Ïûê ID</span>
                          <span className="font-semibold text-blue-600 cursor-pointer hover:underline">{requestResult?.userId}</span>
                        </div>
                        <div className="flex justify-between items-center text-sm">
                          <span className="text-gray-500">ÏûÑÏãú ÎπÑÎ∞ÄÎ≤àÌò∏</span>
                          <span className="font-semibold text-red-500">{requestResult?.tempPassword}</span>
                        </div>
                        <div className="flex justify-between items-center text-sm">
                          <span className="text-gray-500">Îß§Ïû•Î™Ö</span>
                          <span className="font-semibold text-gray-700">{requestResult?.storeName}</span>
                        </div>
                      </div>
                    </div>
                  )}

                  {/* Ïã§Ìå® Ïãú ÏÇ¨Ïú† ÌëúÏãú */}
                  {requestResult?.status === 'rejected' && requestResult?.reason && (
                    <div className="mt-6 pt-6 border-t border-gray-100">
                      <h3 className="font-bold text-gray-700 mb-4">Í±∞Î∂Ä ÏÇ¨Ïú†</h3>
                      <div className="bg-red-50 p-4 rounded-lg">
                        <p className="text-sm text-red-700">{requestResult.reason}</p>
                      </div>
                    </div>
