                {/* Bottom Message */}
                <p className="text-xs text-center text-gray-400 px-4 mb-6">
                  {statusConfig.message}
                </p>
              </>
            )}
          </div>
        </main>

        {/* Footer */}
        {showResult ? (
          <footer className="p-6 border-t border-gray-200 bg-white flex-shrink-0">
            {requestResult?.status === 'rejected' ? (
              /* Ïã§Ìå® Ïãú ÏÉàÎ°ú Ïã†Ï≤≠ÌïòÍ∏∞ + ÌôïÏù∏ Î≤ÑÌäº */
              <div className="flex gap-3">
                <button 
                  onClick={resetForm}
                  className="flex-1 bg-gray-100 text-gray-700 py-3 rounded-lg font-medium hover:bg-gray-200 transition-colors"
                >
                  ÏÉàÎ°ú Ïã†Ï≤≠ÌïòÍ∏∞
                </button>
                <button 
                  onClick={handleClose}
                  className="flex-1 bg-gradient-to-br from-orange-400 to-orange-500 text-white font-bold py-3 rounded-xl shadow-lg hover:shadow-xl transition-all duration-300"
