import React, { createContext, useContext, useReducer, useEffect } from 'react';
import api from '../services/api';

const AuthContext = createContext();

const initialState = {
  isAuthenticated: false,
  user: null,
  loading: false,
  error: null,
  token: null
};

const authReducer = (state, action) => {
  switch (action.type) {
    case 'LOGIN_START':
    case 'REGISTER_START':
      return { ...state, loading: true, error: null };
      
    case 'LOGIN_SUCCESS':
      return {
        ...state,
        isAuthenticated: true,
        user: action.payload.user,
        token: action.payload.token,
        loading: false,
        error: null
      };
      
    case 'LOGIN_FAILURE':
    case 'REGISTER_FAILURE':
      return {
        ...state,
        isAuthenticated: false,
        user: null,
        token: null,
        loading: false,
        error: action.payload
      };
      
    case 'REGISTER_SUCCESS':
      return { ...state, loading: false, error: null };
      
    case 'LOGOUT':
      return {
        ...state,
        isAuthenticated: false,
        user: null,
        token: null,
        loading: false,
        error: null
      };
      
    case 'RESTORE_SESSION':
      return {
        ...state,
        isAuthenticated: true,
        user: action.payload.user,
        token: action.payload.token,
        loading: false
      };
      
    case 'CLEAR_ERROR':
      return { ...state, error: null };
      
    default:
      return state;
  }
};

export const AuthProvider = ({ children }) => {
  const [state, dispatch] = useReducer(authReducer, initialState);

  // ÌéòÏù¥ÏßÄ Î°úÎìú Ïãú Ï†ÄÏû•Îêú Ïù∏Ï¶ù Ï†ïÎ≥¥ Î≥µÏõê
  useEffect(() => {
    const token = localStorage.getItem('token');
    const userInfo = localStorage.getItem('userInfo');
    
    if (token && userInfo) {
      try {
        const parsedUserInfo = JSON.parse(userInfo);
        dispatch({
          type: 'RESTORE_SESSION',
          payload: {
            user: parsedUserInfo,
            token: token
          }
        });
        console.log('‚úÖ ÏÑ∏ÏÖò Î≥µÏõê ÏôÑÎ£å:', parsedUserInfo.managerName || parsedUserInfo.userId);
      } catch (error) {
        console.error('‚ùå ÏÑ∏ÏÖò Î≥µÏõê Ï§ë Ïò§Î•ò Î∞úÏÉù:', error);
        localStorage.removeItem('token');
        localStorage.removeItem('userInfo');
      }
    }
  }, []);

  // üîê Î°úÍ∑∏Ïù∏ Ìï®Ïàò - ÏÉàÎ°úÏö¥ API Ïó∞Í≤∞
  const login = async (credentials) => {
    dispatch({ type: 'LOGIN_START' });
    
    try {
      console.log('üîê Î°úÍ∑∏Ïù∏ ÏãúÎèÑ Ï§ë:', { userId: credentials.username });
      
      const response = await api.post('/auth/login', {
        userId: credentials.username,  // ÏÉàÎ°úÏö¥ ÏÑúÎ≤Ñ APIÏóê ÎßûÍ≤å ÏàòÏ†ï
        password: credentials.password
      });

      if (response.data.success) {
        const { user, token } = response.data.data;
        
        // ÌÜ†ÌÅ∞ÏùÑ localStorageÏôÄ axios Í∏∞Î≥∏ Ìó§ÎçîÏóê Ï†ÄÏû•
        localStorage.setItem('token', token);
        localStorage.setItem('userInfo', JSON.stringify(user));
        
        // axios Í∏∞Î≥∏ Ìó§ÎçîÏóê ÌÜ†ÌÅ∞ ÏÑ§Ï†ï
        api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
        
        dispatch({
          type: 'LOGIN_SUCCESS',
          payload: { user, token }
        });
        
        console.log('‚úÖ Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ:', user.managerName || user.userId);
      }
    } catch (error) {
      console.error('‚ùå Î°úÍ∑∏Ïù∏ Ïã§Ìå®:', error);
      
      const errorMessage = error.response?.data?.message || 
                          error.message || 
                          'Î°úÍ∑∏Ïù∏Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.';
      
      dispatch({
        type: 'LOGIN_FAILURE',
        payload: errorMessage
      });
      
      throw new Error(errorMessage);
    }
  };

  // üìù ÌöåÏõêÍ∞ÄÏûÖ Ïã†Ï≤≠ Ìï®Ïàò - ÏÉàÎ°úÏö¥ API Ïó∞Í≤∞
  const register = async (registerData) => {
    dispatch({ type: 'REGISTER_START' });
    
    try {
      console.log('üìù ÌöåÏõêÍ∞ÄÏûÖ Ïã†Ï≤≠ Ï§ë:', registerData);
      
      const response = await api.post('/auth/register', {
        managerName: registerData.managerName,
        storeCode: registerData.storeCode,
        phoneLast4: registerData.phoneLast4,
        department: registerData.department
      });

      if (response.data.success) {
        dispatch({ type: 'REGISTER_SUCCESS' });
        console.log('‚úÖ ÌöåÏõêÍ∞ÄÏûÖ Ïã†Ï≤≠ ÏôÑÎ£å');
        return response.data;
      }
    } catch (error) {
      console.error('‚ùå ÌöåÏõêÍ∞ÄÏûÖ Ïã†Ï≤≠ Ïã§Ìå®:', error);
      
      const errorMessage = error.response?.data?.message || 
                          error.message || 
                          'ÌöåÏõêÍ∞ÄÏûÖ Ïã†Ï≤≠Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.';
      
      dispatch({
        type: 'REGISTER_FAILURE',
        payload: errorMessage
      });
      
      throw new Error(errorMessage);
    }
  };

  // üîë ÎπÑÎ∞ÄÎ≤àÌò∏ Î≥ÄÍ≤Ω Ìï®Ïàò
  const changePassword = async (currentPassword, newPassword) => {
    try {
      console.log('üîê ÎπÑÎ∞ÄÎ≤àÌò∏ Î≥ÄÍ≤Ω ÏöîÏ≤≠ Ï§ë...');
      
      const response = await api.put('/auth/change-password', {
        currentPassword,
        newPassword
      });

      if (response.data.success) {
        console.log('‚úÖ ÎπÑÎ∞ÄÎ≤àÌò∏ Î≥ÄÍ≤Ω ÏôÑÎ£å');
        return response.data;
      }
    } catch (error) {
      console.error('‚ùå ÎπÑÎ∞ÄÎ≤àÌò∏ Î≥ÄÍ≤Ω Ïã§Ìå®:', error);
      
      const errorMessage = error.response?.data?.message || 
                          error.message || 
                          'ÎπÑÎ∞ÄÎ≤àÌò∏ Î≥ÄÍ≤ΩÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.';
      
      throw new Error(errorMessage);
    }
  };

  // üë§ ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Ï°∞Ìöå Ìï®Ïàò
  const getUserInfo = async () => {
    try {
      const response = await api.get('/auth/me');
      
      if (response.data.success) {
        const updatedUser = response.data.data;
        
        // Î°úÏª¨ Ïä§ÌÜ†Î¶¨ÏßÄ ÏóÖÎç∞Ïù¥Ìä∏
        localStorage.setItem('userInfo', JSON.stringify(updatedUser));
        
        dispatch({
          type: 'LOGIN_SUCCESS',
          payload: { user: updatedUser, token: state.token }
        });
        
        return updatedUser;
      }
    } catch (error) {
      console.error('‚ùå ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Ï°∞Ìöå Ïã§Ìå®:', error);
      throw error;
    }
  };

  // üö™ Î°úÍ∑∏ÏïÑÏõÉ Ìï®Ïàò
  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userInfo');
    delete api.defaults.headers.common['Authorization'];
    
    dispatch({ type: 'LOGOUT' });
    console.log('üö™ Î°úÍ∑∏ÏïÑÏõÉ ÏôÑÎ£å');
  };

  // ‚ùå ÏóêÎü¨ Ï¥àÍ∏∞Ìôî Ìï®Ïàò
  const clearError = () => {
    dispatch({ type: 'CLEAR_ERROR' });
  };

  // Context value
  const contextValue = {
    // ÏÉÅÌÉú
    ...state,
    
    // Ìï®ÏàòÎì§
    login,
    register,
    logout,
    clearError,
    changePassword,
    getUserInfo,
    
    // Ïú†Ìã∏Î¶¨Ìã∞
    isLoggedIn: state.isAuthenticated && state.user && state.token,
    userRole: state.user?.role || 'user',
    userId: state.user?.userId || null,
    userName: state.user?.managerName || state.user?.userId || null,
    isAdmin: state.user?.role === 'admin'
  };

  return (
    <AuthContext.Provider value={contextValue}>
      {children}
    </AuthContext.Provider>
  );
};

// Hook
export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuthÎäî AuthProvider ÎÇ¥Î∂ÄÏóêÏÑúÎßå ÏÇ¨Ïö©Ìï† Ïàò ÏûàÏäµÎãàÎã§.');
  }
  return context;
};

export default AuthContext;
