import React, { useState, useEffect, useRef } from 'react';

const StoreManage = ({ onBackToAdmin }) => {
  const [stats, setStats] = useState({
    totalStores: 0,
    totalDepartments: 0,
    totalManagers: 0,
    activeStores: 0
  });
  const [allStores, setAllStores] = useState([]);
  const [filteredStores, setFilteredStores] = useState([]);
  const [loading, setLoading] = useState(false);
  const [processingIds, setProcessingIds] = useState(new Set());
  const [searchTerm, setSearchTerm] = useState('');
  const [activeFilter, setActiveFilter] = useState('ì „ì²´');
  const [error, setError] = useState(null);
  const [showAddModal, setShowAddModal] = useState(false);
  const [editingStore, setEditingStore] = useState(null);

  // ìƒˆ ë§¤ìž¥/ë¶€ì„œ ì¶”ê°€/ìˆ˜ì • í¼ ë°ì´í„°
  const [formData, setFormData] = useState({
    storeName: '',
    department: 'ì—¬ì„±',
    storeCode: '',
    address: '',
    managerName: '',
    managerPhone: '',
    notes: ''
  });

  // ì»´í¬ë„ŒíŠ¸ ë§ˆìš´íŠ¸ ìƒíƒœ ì¶”ì 
  const isMountedRef = useRef(true);
  const intervalRef = useRef(null);

  // API í˜¸ì¶œ í•¨ìˆ˜ë“¤
  const fetchAllStores = async () => {
    try {
      console.log('ðŸª ë§¤ìž¥ ëª©ë¡ API í˜¸ì¶œ ì‹œìž‘...');
      setLoading(true);
      const response = await fetch('https://quinors-lv-backend.ngrok.io/api/admin/stores');
      const result = await response.json();
      console.log('ðŸª ë§¤ìž¥ ëª©ë¡ API ì‘ë‹µ:', result);

      if (response.ok && result.success) {
        const stores = result.data || [];
        console.log('ðŸª âœ… ë§¤ìž¥ ëª©ë¡ ë°ì´í„° ì²˜ë¦¬ ì„±ê³µ:', stores);
        
        if (isMountedRef.current) {
          setAllStores(stores);
          setFilteredStores(stores);
          calculateStats(stores);
          setError(null);
        }
      } else {
        console.error('ðŸª âŒ ë§¤ìž¥ ëª©ë¡ API ì‹¤íŒ¨');
        setError('ë§¤ìž¥ ëª©ë¡ ì¡°íšŒ ì‹¤íŒ¨');
        if (isMountedRef.current) {
          setAllStores([]);
          setFilteredStores([]);
        }
      }
    } catch (error) {
      console.error('ðŸª âŒ ë§¤ìž¥ ëª©ë¡ ì¡°íšŒ ë„¤íŠ¸ì›Œí¬ ì˜¤ë¥˜:', error);
      setError('ë§¤ìž¥ ëª©ë¡ ì¡°íšŒ ì¤‘ ë„¤íŠ¸ì›Œí¬ ì˜¤ë¥˜: ' + error.message);
      if (isMountedRef.current) {
        setAllStores([]);
        setFilteredStores([]);
      }
    } finally {
      if (isMountedRef.current) {
        setLoading(false);
      }
    }
  };

  const calculateStats = (storeData) => {
    const totalDepartments = storeData.length;
    const uniqueStores = [...new Set(storeData.map(store => store.storeName))];
    const totalStores = uniqueStores.length;
    const totalManagers = storeData.filter(store => store.managerName).length;
    const activeStores = storeData.filter(store => store.isActive !== false).length;

    setStats({ totalStores, totalDepartments, totalManagers, activeStores });
    console.log('ðŸ“Š í†µê³„ ê³„ì‚°:', { totalStores, totalDepartments, totalManagers, activeStores });
  };

  // ë§¤ìž¥/ë¶€ì„œ ì¶”ê°€
  const handleAddStore = async () => {
    // ê¸°ë³¸ ì •ë³´ ê²€ì¦
    if (!formData.storeName || !formData.storeCode || !formData.managerName || !formData.managerPhone) {
      alert('í•„ìˆ˜ ì •ë³´ë¥¼ ëª¨ë‘ ìž…ë ¥í•´ì£¼ì„¸ìš”.');
      return;
    }

    // ì „í™”ë²ˆí˜¸ í˜•ì‹ ê²€ì¦
    const phoneRegex = /^010-\d{4}-\d{4}$/;
    if (!phoneRegex.test(formData.managerPhone)) {
      alert('ì˜¬ë°”ë¥¸ íœ´ëŒ€í° ë²ˆí˜¸ í˜•ì‹ìœ¼ë¡œ ìž…ë ¥í•´ì£¼ì„¸ìš”. (010-0000-0000)');
      return;
    }

    try {
      console.log('âž• ë§¤ìž¥/ë¶€ì„œ ì¶”ê°€ ì‹œìž‘:', formData);

      const response = await fetch('https://quinors-lv-backend.ngrok.io/api/admin/stores', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          storeName: formData.storeName.trim(),
          department: formData.department,
          storeCode: formData.storeCode.trim().toUpperCase(),
          address: formData.address.trim(),
          managerName: formData.managerName.trim(),
          managerPhone: formData.managerPhone.replace(/[^0-9]/g, ''),
          notes: formData.notes.trim()
        }),
      });

      const result = await response.json();
      console.log('âž• ë§¤ìž¥/ë¶€ì„œ ì¶”ê°€ API ì‘ë‹µ:', result);

      if (response.ok && result.success) {
        alert('ë§¤ìž¥/ë¶€ì„œê°€ ì„±ê³µì ìœ¼ë¡œ ë“±ë¡ë˜ì—ˆìŠµë‹ˆë‹¤.');
        setShowAddModal(false);
        resetFormData();
        await fetchAllStores();
      } else {
        alert(`ë§¤ìž¥/ë¶€ì„œ ë“±ë¡ ì‹¤íŒ¨: ${result.message}`);
      }
    } catch (error) {
      console.error('âž• ë§¤ìž¥/ë¶€ì„œ ë“±ë¡ ì‹¤íŒ¨:', error);
      alert('ë§¤ìž¥/ë¶€ì„œ ë“±ë¡ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.');
    }
  };

  // ë§¤ìž¥/ë¶€ì„œ ìˆ˜ì •
  const handleEditStore = async () => {
    if (!formData.storeName || !formData.storeCode || !formData.managerName || !formData.managerPhone) {
      alert('í•„ìˆ˜ ì •ë³´ë¥¼ ëª¨ë‘ ìž…ë ¥í•´ì£¼ì„¸ìš”.');
      return;
    }

    const phoneRegex = /^010-\d{4}-\d{4}$/;
    if (!phoneRegex.test(formData.managerPhone)) {
      alert('ì˜¬ë°”ë¥¸ íœ´ëŒ€í° ë²ˆí˜¸ í˜•ì‹ìœ¼ë¡œ ìž…ë ¥í•´ì£¼ì„¸ìš”. (010-0000-0000)');
      return;
    }

    try {
      console.log('âœï¸ ë§¤ìž¥/ë¶€ì„œ ìˆ˜ì • ì‹œìž‘:', editingStore._id, formData);

      const response = await fetch(`https://quinors-lv-backend.ngrok.io/api/admin/stores/${editingStore._id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          storeName: formData.storeName.trim(),
          department: formData.department,
          storeCode: formData.storeCode.trim().toUpperCase(),
          address: formData.address.trim(),
          managerName: formData.managerName.trim(),
          managerPhone: formData.managerPhone.replace(/[^0-9]/g, ''),
          notes: formData.notes.trim()
        }),
      });

      const result = await response.json();
      console.log('âœï¸ ë§¤ìž¥/ë¶€ì„œ ìˆ˜ì • API ì‘ë‹µ:', result);

      if (response.ok && result.success) {
        alert('ë§¤ìž¥/ë¶€ì„œ ì •ë³´ê°€ ì„±ê³µì ìœ¼ë¡œ ìˆ˜ì •ë˜ì—ˆìŠµë‹ˆë‹¤.');
        setEditingStore(null);
        resetFormData();
        await fetchAllStores();
      } else {
        alert(`ë§¤ìž¥/ë¶€ì„œ ìˆ˜ì • ì‹¤íŒ¨: ${result.message}`);
      }
    } catch (error) {
      console.error('âœï¸ ë§¤ìž¥/ë¶€ì„œ ìˆ˜ì • ì‹¤íŒ¨:', error);
      alert('ë§¤ìž¥/ë¶€ì„œ ìˆ˜ì • ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.');
    }
  };

  // ë§¤ìž¥/ë¶€ì„œ ì‚­ì œ
  const handleDeleteStore = async (storeId, storeName, department) => {
    if (!confirm(`${storeName} ${department} ë¶€ì„œë¥¼ ì‚­ì œí•˜ì‹œê² ìŠµë‹ˆê¹Œ?\n\nì´ ìž‘ì—…ì€ ë˜ëŒë¦´ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.`)) {
      return;
    }

    try {
      console.log('ðŸ—‘ï¸ ë§¤ìž¥/ë¶€ì„œ ì‚­ì œ ì‹œìž‘:', storeId);

      const response = await fetch(`https://quinors-lv-backend.ngrok.io/api/admin/stores/${storeId}`, {
        method: 'DELETE',
      });

      const result = await response.json();
      console.log('ðŸ—‘ï¸ ë§¤ìž¥/ë¶€ì„œ ì‚­ì œ API ì‘ë‹µ:', result);

      if (response.ok && result.success) {
        alert('ë§¤ìž¥/ë¶€ì„œê°€ ì„±ê³µì ìœ¼ë¡œ ì‚­ì œë˜ì—ˆìŠµë‹ˆë‹¤.');
        await fetchAllStores();
      } else {
        alert(`ë§¤ìž¥/ë¶€ì„œ ì‚­ì œ ì‹¤íŒ¨: ${result.message}`);
      }
    } catch (error) {
      console.error('ðŸ—‘ï¸ ë§¤ìž¥/ë¶€ì„œ ì‚­ì œ ì‹¤íŒ¨:', error);
      alert('ë§¤ìž¥/ë¶€ì„œ ì‚­ì œ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.');
    }
  };

  // í¼ ë°ì´í„° ë¦¬ì…‹
  const resetFormData = () => {
    setFormData({
      storeName: '',
      department: 'ì—¬ì„±',
      storeCode: '',
      address: '',
      managerName: '',
      managerPhone: '',
      notes: ''
    });
  };

  // ì „í™”ë²ˆí˜¸ í¬ë§·íŒ…
  const handlePhoneInput = (e) => {
    const value = e.target.value.replace(/[^0-9]/g, '');
    let formattedValue = value;
    
    if (value.length <= 3) {
      formattedValue = value;
    } else if (value.length <= 7) {
      formattedValue = `${value.slice(0, 3)}-${value.slice(3)}`;
    } else if (value.length <= 11) {
      formattedValue = `${value.slice(0, 3)}-${value.slice(3, 7)}-${value.slice(7)}`;
    }
    
    setFormData(prev => ({
      ...prev,
      managerPhone: formattedValue
    }));
