import React, { useState, useEffect } from 'react';
import { useDelivery } from '../../context/DeliveryContext';
import { useAuth } from '../../context/AuthContext';
import Header from '../common/Header';
import FloatingPreview from '../common/FloatingPreview';
import Button from '../ui/Button';
import SearchInput from '../common/SearchInput';
import Card from '../ui/Card';

const OriginStoreSelector = () => {
  const { deliveryType, originStore, dispatch, setOriginStore } = useDelivery();
  const { user, token } = useAuth();
  
  const [selectedStore, setSelectedStore] = useState(originStore);
  const [searchTerm, setSearchTerm] = useState('');
  const [stores, setStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // 3단계 선택 상태
  const [step, setStep] = useState(1); // 1: 매장선택, 2: 부서선택, 3: 담당자선택
  const [selectedStoreData, setSelectedStoreData] = useState(null);
  const [selectedDepartment, setSelectedDepartment] = useState(null);
  const [selectedManager, setSelectedManager] = useState(null);
  const [departmentManagers, setDepartmentManagers] = useState([]);

  // 매장 데이터 가져오기 (일반 사용자용 API 사용)
  useEffect(() => {
    const fetchStores = async () => {
      try {
        setLoading(true);
        setError('');
        
        // ✅ 일반 사용자용 API 엔드포인트 사용
        const response = await fetch('https://quinors-lv-backend.ngrok.io/api/auth/stores?active=true&limit=100', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
        });

        const result = await response.json();

        if (response.ok && result.success) {
          // 매장별로 그룹화된 데이터 처리
          const transformedStores = result.data.map(store => ({
            id: store._id,
            storeCode: store.storeCode,
            name: store.storeName,
            address: store.address || '주소 정보 없음',
            isActive: store.isActive,
            createdAt: store.createdAt,
            // 부서별 담당자 정보를 객체 형태로 변환
            departments: store.departments ? store.departments.reduce((acc, dept) => {
              if (!acc[dept.department]) {
                acc[dept.department] = [];
              }
              acc[dept.department].push({
                id: `${store._id}_${dept.department}_${dept.managerName}`,
                managerName: dept.managerName,
                phone: dept.managerPhone,
                fullPhone: dept.fullPhone || (dept.managerPhone ? dept.managerPhone.replace(/(\d{3})(\d{4})(\d{4})/, '$1-$2-$3') : '')
              });
              return acc;
            }, {}) : {}
          }));

          setStores(transformedStores);
          console.log('✅ 출발지 매장 데이터 로드 완료:', transformedStores);
        } else {
          throw new Error(result.message || '매장 데이터를 불러오는데 실패했습니다.');
        }
      } catch (error) {
        console.error('❌ 출발지 매장 데이터 로드 실패:', error);
        setError(error.message);
        
        // 에러 발생 시 샘플 데이터 사용
        const sampleStores = [
          {
            id: 'sample-1',
            storeCode: 'ST001',
            name: '강남점',
            address: '서울 강남구 압구정동 454',
            isActive: true,
            departments: {
              '여성': [
                { id: 'sample-1-women-1', managerName: '김여성', phone: '01011111111', fullPhone: '010-1111-1111' }
              ],
              '남성': [
                { id: 'sample-1-men-1', managerName: '이남성', phone: '01022222222', fullPhone: '010-2222-2222' }
              ]
            }
          },
          {
            id: 'sample-2',
            storeCode: 'ST002',
            name: '롯데월드타워점',
            address: '서울 송파구 올림픽로 300',
            isActive: true,
            departments: {
              '여성': [
                { id: 'sample-2-women-1', managerName: '박여성', phone: '01033333333', fullPhone: '010-3333-3333' }
              ],
              '슈즈': [
                { id: 'sample-2-shoes-1', managerName: '최슈즈', phone: '01044444444', fullPhone: '010-4444-4444' }
              ]
            }
          },
          {
            id: 'sample-3',
            storeCode: 'ST003',
            name: '현대백화점본점',
            address: '서울 중구 남대문로 63',
            isActive: true,
            departments: {
              '여성': [
                { id: 'sample-3-women-1', managerName: '김매니저', phone: '01055555555', fullPhone: '010-5555-5555' }
              ]
            }
          }
        ];
        
        setStores(sampleStores);
      } finally {
        setLoading(false);
      }
    };

    if (token) {
      fetchStores();
    }
  }, [token]);

  // 검색 필터링
  const filteredStores = stores.filter(store =>
    store.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    store.storeCode.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // 1단계: 매장 선택
  const handleStoreSelect = (store) => {
    console.log('매장 선택:', store);
    setSelectedStoreData(store);
    setStep(2);
  };

  // 2단계: 부서 선택
  const handleDepartmentSelect = (department) => {
    console.log('부서 선택:', department);
    setSelectedDepartment(department);
    setDepartmentManagers(selectedStoreData.departments[department] || []);
    setStep(3);
  };

  // 3단계: 담당자 선택
  const handleManagerSelect = (manager) => {
    console.log('담당자 선택:', manager);
    setSelectedManager(manager);
    
    // 최종 선택 데이터 구성
    const finalSelection = {
      store: selectedStoreData,
      department: selectedDepartment,
      manager: manager,
      displayName: `${selectedStoreData.name} ${selectedDepartment} - ${manager.managerName}`
    };
    
    setSelectedStore(finalSelection);
  };

  // 뒤로가기
  const handleBack = () => {
    if (step === 3) {
      setStep(2);
      setSelectedManager(null);
      setSelectedStore(null);
    } else if (step === 2) {
      setStep(1);
      setSelectedDepartment(null);
      setDepartmentManagers([]);
      setSelectedStoreData(null);
    } else {
      dispatch({ type: 'SET_STEP', payload: 1 });
    }
  };

  // 다음 단계
  const handleNext = () => {
    if (selectedStore && selectedManager) {
      dispatch({ type: 'SET_ORIGIN_STORE', payload: selectedStore });
      dispatch({ type: 'SET_STEP', payload: 3 });
    }
  };

  // 미리보기 내용
  const getPreviewContent = () => {
    if (selectedStore && selectedManager) {
      return `출발지: ${selectedStore.displayName}`;
    }
    return '';
  };

  // 부서별 색상
  const getDepartmentColor = (department) => {
    const colors = {
      '여성': 'bg-pink-100 text-pink-600 border-pink-200',
      '남성': 'bg-blue-100 text-blue-600 border-blue-200',
      '슈즈': 'bg-green-100 text-green-600 border-green-200'
    };
    return colors[department] || 'bg-gray-100 text-gray-600 border-gray-200';
  };

  return (
    <div className="bg-gray-100 flex items-center justify-center min-h-screen font-sans">
      <div className="w-full max-w-sm mx-auto bg-white shadow-lg rounded-3xl overflow-hidden h-screen max-h-[812px] flex flex-col relative">
        
        <div className="relative z-30">
          <Header 
            title="출발지 선택"
            subtitle={
              step === 1 ? "출발지 매장을 선택해주세요" :
              step === 2 ? "부서를 선택해주세요" :
              "담당자를 선택해주세요"
            }
            currentStep={2}
            onBack={handleBack}
          />
        </div>

        <main className="flex-1 bg-gray-50 p-6 space-y-4 overflow-y-auto pb-20">
          {/* 1단계: 매장 선택 */}
          {step === 1 && (
            <>
              <SearchInput
                placeholder="매장명 또는 매장코드로 검색"
                value={searchTerm}
                onChange={setSearchTerm}
              />

              {loading ? (
                <div className="text-center py-8">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-orange-500 mx-auto"></div>
                  <p className="text-gray-500 mt-2">매장 정보를 불러오는 중...</p>
                </div>
              ) : error ? (
                <div className="text-center py-8">
                  <p className="text-red-500 mb-2">{error}</p>
                  <p className="text-gray-500 text-sm">샘플 데이터로 진행합니다.</p>
                </div>
              ) : (
                <div className="space-y-3">
                  {filteredStores.length === 0 ? (
                    <div className="text-center py-8">
                      <p className="text-gray-500">검색 결과가 없습니다.</p>
                    </div>
                  ) : (
                    filteredStores.map((store) => (
                      <Card
                        key={store.id}
                        onClick={() => handleStoreSelect(store)}
                        className="cursor-pointer hover:border-orange-300 hover:shadow-lg transition-all"
                      >
                        <div className="flex items-center justify-between">
                          <div className="flex-1">
                            <h3 className="font-bold text-gray-800">{store.name}</h3>
                            <p className="text-sm text-gray-600">{store.storeCode}</p>
                            <p className="text-xs text-gray-500 mt-1">{store.address}</p>
                            
                            {/* 부서 표시 */}
                            <div className="flex gap-1 mt-2">
                              {Object.keys(store.departments).map(dept => (
                                <span 
                                  key={dept}
                                  className={`px-2 py-1 text-xs rounded-full border ${getDepartmentColor(dept)}`}
                                >
                                  {getDepartmentIcon(dept)} {dept} ({store.departments[dept].length}명)
                                </span>
                              ))}
                            </div>
                          </div>
                          <i className="fa-solid fa-chevron-right text-gray-400"></i>
                        </div>
                      </Card>
                    ))
                  )}
                </div>
              )}
            </>
          )}

          {/* 2단계: 부서 선택 */}
          {step === 2 && selectedStoreData && (
            <>
              <div className="bg-white rounded-lg p-4 border border-gray-200 mb-4">
                <h3 className="font-bold text-gray-800">{selectedStoreData.name}</h3>
                <p className="text-sm text-gray-600">{selectedStoreData.storeCode}</p>
                <p className="text-xs text-gray-500">{selectedStoreData.address}</p>
              </div>

              <div className="space-y-3">
                {Object.entries(selectedStoreData.departments).map(([dept, managers]) => (
                  <Card
                    key={dept}
                    onClick={() => handleDepartmentSelect(dept)}
                    className="cursor-pointer hover:border-orange-300 hover:shadow-lg transition-all"
                  >
                    <div className="flex items-center justify-between">
                      <div className="flex items-center gap-3">
                        <div className={`w-12 h-12 rounded-xl flex items-center justify-center border ${getDepartmentColor(dept)}`}>
                          <span className="text-lg">
                            {getDepartmentIcon(dept)}
                          </span>
                        </div>
                        <div>
                          <h3 className="font-bold text-gray-800">{dept} 매장</h3>
                          <p className="text-sm text-gray-600">담당자 {managers.length}명</p>
                        </div>
                      </div>
                      <i className="fa-solid fa-chevron-right text-gray-400"></i>
                    </div>
                  </Card>
                ))}
              </div>
            </>
          )}

          {/* 3단계: 담당자 선택 */}
          {step === 3 && selectedDepartment && (
            <>
              <div className="bg-white rounded-lg p-4 border border-gray-200 mb-4">
                <h3 className="font-bold text-gray-800">{selectedStoreData.name}</h3>
                <span className={`inline-block px-3 py-1 text-sm rounded-full border mt-2 ${getDepartmentColor(selectedDepartment)}`}>
                  {getDepartmentIcon(selectedDepartment)} {selectedDepartment} 매장
                </span>
              </div>

              <div className="space-y-3">
                {departmentManagers.map((manager, index) => (
                  <Card
                    key={manager.id || index}
                    onClick={() => handleManagerSelect(manager)}
                    className={`cursor-pointer transition-all ${
                      selectedManager?.id === manager.id
                        ? 'border-orange-500 bg-orange-50'
                        : 'hover:border-orange-300 hover:shadow-lg'
                    }`}
                  >
                    <div className="flex items-center justify-between">
                      <div className="flex items-center gap-3">
                        <div className="w-12 h-12 bg-gradient-to-br from-orange-400 to-orange-500 rounded-xl flex items-center justify-center text-white font-bold">
                          {manager.managerName.charAt(0)}
                        </div>
                        <div>
                          <h3 className="font-bold text-gray-800">{manager.managerName}</h3>
                          <p className="text-sm text-gray-600">
                            {manager.fullPhone || (manager.phone ? manager.phone.replace(/(\d{3})(\d{4})(\d{4})/, '$1-$2-$3') : '연락처 없음')}
                          </p>
                          {manager.notes && (
                            <p className="text-xs text-gray-500 mt-1">{manager.notes}</p>
                          )}
                        </div>
                      </div>
                      {selectedManager?.id === manager.id && (
                        <svg className="w-5 h-5 text-orange-500" fill="currentColor" viewBox="0 0 20 20">
                          <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd"/>
                        </svg>
                      )}
                    </div>
                  </Card>
                ))}
              </div>
            </>
          )}
        </main>

        <FloatingPreview
          content={getPreviewContent()}
          onEdit={() => {
            setStep(1);
            setSelectedStoreData(null);
            setSelectedDepartment(null);
            setSelectedManager(null);
            setSelectedStore(null);
          }}
          show={!!selectedStore && !!selectedManager}
        />

        <footer className="p-6 border-t border-gray-200 bg-white">
          <Button 
            onClick={handleNext} 
            disabled={!selectedStore || !selectedManager || loading}
          >
            다음 단계
          </Button>
        </footer>
      </div>
    </div>
  );
};

export default OriginStoreSelector;
