import React, { useState, useEffect } from 'react';
import { useDelivery } from '../../context/DeliveryContext';
import { useAuth } from '../../context/AuthContext';

const OriginStoreSelector = ({ isOpen, onClose }) => {
  const { deliveryType, originStore, dispatch, setOriginStore } = useDelivery();
  const { user, token } = useAuth();
  
  const [selectedStore, setSelectedStore] = useState(originStore);
  const [searchTerm, setSearchTerm] = useState('');
  const [stores, setStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // 부서/담당자 선택 모달 상태
  const [showDepartmentModal, setShowDepartmentModal] = useState(false);
  const [selectedStoreForModal, setSelectedStoreForModal] = useState(null);
  const [modalStep, setModalStep] = useState(1); // 1: 부서선택, 2: 담당자선택
  const [selectedDepartment, setSelectedDepartment] = useState(null);
  const [selectedManager, setSelectedManager] = useState(null);

  // 모달이 열릴 때마다 초기화
  useEffect(() => {
    if (isOpen) {
      setSelectedStore(null);
      setSearchTerm('');
      setShowDepartmentModal(false);
      setSelectedStoreForModal(null);
      setModalStep(1);
      setSelectedDepartment(null);
      setSelectedManager(null);
    }
  }, [isOpen]);

  // 매장 데이터 가져오기
  useEffect(() => {
    const fetchStores = async () => {
      if (!isOpen || !token) return;
      
      try {
        setLoading(true);
        setError('');
        
        const response = await fetch('https://quinors-lv-backend.ngrok.io/api/auth/stores?active=true&limit=100', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
        });

        const result = await response.json();

        if (response.ok && result.success) {
          const transformedStores = result.data.map(store => ({
            id: store._id,
            storeCode: store.storeCode,
            name: store.storeName,
            address: store.address || '주소 정보 없음',
            isActive: store.isActive,
            createdAt: store.createdAt,
            departments: store.departments ? store.departments.reduce((acc, dept) => {
              if (!acc[dept.department]) {
                acc[dept.department] = [];
              }
              acc[dept.department].push({
                id: `${store._id}_${dept.department}_${dept.managerName}`,
                managerName: dept.managerName,
                phone: dept.managerPhone,
                fullPhone: dept.fullPhone || (dept.managerPhone ? dept.managerPhone.replace(/(\d{3})(\d{4})(\d{4})/, '$1-$2-$3') : '')
              });
              return acc;
            }, {}) : {}
          }));

          setStores(transformedStores);
          console.log('✅ 출발지 매장 데이터 로드 완료:', transformedStores);
        } else {
          throw new Error(result.message || '매장 데이터를 불러오는데 실패했습니다.');
        }
      } catch (error) {
        console.error('❌ 출발지 매장 데이터 로드 실패:', error);
        setError(error.message);
        
        // 샘플 데이터
        const sampleStores = [
          {
            id: 'sample-1',
            storeCode: 'QN01',
            name: '루이비통 퀴노스점 여성',
            address: '서울 중랑구 사가정로54길 72 (면목동) 1층',
            isActive: true,
            departments: {
              '여성': [
                { id: 'qn01-women-1', managerName: '안태욱', phone: '01012345678', fullPhone: '010-1234-5678' },
                { id: 'qn01-women-2', managerName: '홍길동', phone: '01087654321', fullPhone: '010-8765-4321' }
              ]
            }
          },
          {
            id: 'sample-2',
            storeCode: 'ST002',
            name: '롯데월드타워점',
            address: '서울 송파구 올림픽로 300',
            isActive: true,
            departments: {
              '여성': [
                { id: 'st002-women-1', managerName: '박여성', phone: '01033333333', fullPhone: '010-3333-3333' }
              ],
              '남성': [
                { id: 'st002-men-1', managerName: '이남성', phone: '01044444444', fullPhone: '010-4444-4444' }
              ],
              '슈즈': [
                { id: 'st002-shoes-1', managerName: '최슈즈', phone: '01055555555', fullPhone: '010-5555-5555' }
              ]
            }
          }
        ];
        
        setStores(sampleStores);
      } finally {
        setLoading(false);
      }
    };

    fetchStores();
  }, [isOpen, token]);

  // 검색 필터링
  const filteredStores = stores.filter(store =>
    store.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    store.storeCode.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // 매장 선택 시 부서/담당자 선택 모달 열기
  const handleStoreSelect = (store) => {
    console.log('매장 선택:', store);
    setSelectedStoreForModal(store);
    setShowDepartmentModal(true);
    setModalStep(1);
  };

  // 부서 선택
  const handleDepartmentSelect = (department) => {
    console.log('부서 선택:', department);
    setSelectedDepartment(department);
    setModalStep(2);
  };

  // 담당자 선택
  const handleManagerSelect = (manager) => {
    console.log('담당자 선택:', manager);
    setSelectedManager(manager);
  };

  // 부서/담당자 선택 완료
  const handleDepartmentManagerConfirm = () => {
    if (selectedManager && selectedDepartment && selectedStoreForModal) {
      const selection = {
        store: selectedStoreForModal,
        department: selectedDepartment,
        manager: selectedManager,
        displayName: `${selectedStoreForModal.name} ${selectedDepartment} - ${selectedManager.managerName}`
      };
      
      console.log('선택 완료:', selection);
      setSelectedStore(selection);
      dispatch({ type: 'SET_ORIGIN_STORE', payload: selection });
      setShowDepartmentModal(false);
      onClose(); // 메인 모달도 닫기
    }
  };

  // 부서/담당자 모달 닫기
  const handleDepartmentModalClose = () => {
    setShowDepartmentModal(false);
    setModalStep(1);
    setSelectedDepartment(null);
    setSelectedManager(null);
  };

  // 부서/담당자 모달 뒤로가기
  const handleDepartmentModalBack = () => {
    if (modalStep === 2) {
      setModalStep(1);
      setSelectedManager(null);
    } else {
      handleDepartmentModalClose();
    }
  };

  // 부서별 색상
  const getDepartmentColor = (department) => {
    const colors = {
      '여성': 'bg-pink-100 text-pink-600 border-pink-200',
      '남성': 'bg-blue-100 text-blue-600 border-blue-200',
      '슈즈': 'bg-green-100 text-green-600 border-green-200'
    };
    return colors[department] || 'bg-gray-100 text-gray-600 border-gray-200';
  };

  // 모달이 열려있지 않으면 렌더링하지 않음
  if (!isOpen) return null;

  return (
    <>
      {/* 메인 매장 선택 모달 */}
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
        <div className="bg-white rounded-2xl w-full max-w-md h-[600px] flex flex-col overflow-hidden shadow-2xl">
          
          {/* HTML과 동일한 헤더 디자인 */}
          <div className="bg-gradient-to-br from-orange-400 via-orange-500 to-orange-500 text-white p-6 rounded-t-2xl">
            <div className="flex items-center justify-between mb-4">
              <button 
                onClick={onClose}
                className="text-white opacity-90 hover:opacity-100"
              >
                <i className="fas fa-arrow-left fa-lg"></i>
              </button>
              <h1 className="text-xl font-bold">출발지 선택</h1>
              <button 
                onClick={onClose}
                className="text-white opacity-90 hover:opacity-100"
              >
                <i className="fas fa-times fa-lg"></i>
              </button>
            </div>
            <p className="text-center text-sm font-light opacity-80">출발지 매장을 선택해주세요</p>
          </div>

          {/* 검색 섹션 */}
          <div className="p-6 pb-4">
            <div className="relative">
              <input 
                type="text" 
                placeholder="매장명 또는 매장코드로 검색" 
                className="w-full bg-white border border-gray-200 rounded-xl px-4 py-3 pl-12 text-sm focus:outline-none focus:border-orange-500"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
              <i className="fas fa-search absolute left-4 top-1/2 transform -translate-y-1/2 text-gray-400"></i>
            </div>
          </div>

          {/* 매장 목록 */}
          <div className="flex-1 overflow-y-auto">
            {loading ? (
              <div className="text-center py-8">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-orange-500 mx-auto"></div>
                <p className="text-gray-500 mt-2">매장 정보를 불러오는 중...</p>
              </div>
            ) : error ? (
              <div className="text-center py-8">
                <p className="text-red-500 mb-2">{error}</p>
                <p className="text-gray-500 text-sm">샘플 데이터로 진행합니다.</p>
              </div>
            ) : filteredStores.length === 0 ? (
              <div className="text-center py-8">
                <p className="text-gray-500">검색 결과가 없습니다.</p>
              </div>
            ) : (
              <div className="px-6 space-y-3">
                {filteredStores.map((store) => (
                  <div 
                    key={store.id}
                    className="bg-white p-5 rounded-2xl border border-gray-200 shadow-sm hover:border-orange-500 hover:shadow-lg transition-all duration-300 cursor-pointer"
                    onClick={() => handleStoreSelect(store)}
                  >
                    <div className="flex items-center justify-between">
                      <div className="flex-1">
                        <div className="flex items-center gap-3 mb-2">
                          <div className="w-10 h-10 rounded-lg bg-gradient-to-br from-orange-400 to-orange-500 flex items-center justify-center text-white">
                            <i className="fas fa-store"></i>
                          </div>
                          <div>
                            <h3 className="text-base font-bold text-gray-800">{store.name}</h3>
                            <p className="text-xs text-gray-500">{store.storeCode}</p>
                          </div>
                        </div>
                        <p className="text-sm text-gray-600 mb-2">{store.address}</p>
                        
                        {/* 부서별 담당자 수 표시 */}
                        <div className="flex gap-1 flex-wrap">
                          {Object.entries(store.departments).map(([dept, managers]) => (
                            <span 
                              key={dept}
                              className={`text-xs px-2 py-1 rounded-md font-medium ${
                                dept === '여성' ? 'bg-pink-100 text-pink-600' :
                                dept === '남성' ? 'bg-blue-100 text-blue-600' :
                                dept === '슈즈' ? 'bg-green-100 text-green-600' :
                                'bg-gray-100 text-gray-600'
                              }`}
                            >
                              {dept} ({managers.length}명)
                            </span>
                          ))}
                        </div>
                      </div>
                      <i className="fas fa-chevron-right text-gray-400"></i>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* 푸터 */}
          <div className="p-6 border-t border-gray-200">
            <button 
              className="w-full bg-gradient-to-br from-orange-400 to-orange-500 text-white font-bold py-4 rounded-xl shadow-lg hover:shadow-xl focus:outline-none focus:ring-4 focus:ring-orange-300 transition-all duration-300"
              disabled={!selectedStore}
              onClick={onClose}
            >
              {selectedStore ? '선택 완료' : '매장을 선택해주세요'}
            </button>
          </div>
        </div>
      </div>

      {/* 부서/담당자 선택 모달 */}
      {showDepartmentModal && selectedStoreForModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-60 p-4">
          <div className="bg-white rounded-2xl w-full max-w-md h-[600px] flex flex-col overflow-hidden shadow-2xl">
            
            {/* 모달 헤더 */}
            <div className="bg-gradient-to-r from-orange-500 to-orange-600 text-white p-4">
              <div className="flex items-center justify-between mb-3">
                <button 
                  onClick={handleDepartmentModalBack}
                  className="w-8 h-8 bg-white/20 rounded-full flex items-center justify-center hover:bg-white/30 transition-colors"
                >
                  {modalStep === 1 ? (
                    <i className="fas fa-times w-4 h-4"></i>
                  ) : (
                    <i className="fas fa-arrow-left w-4 h-4"></i>
                  )}
                </button>
                
                <h2 className="text-lg font-bold">
                  {modalStep === 1 ? '부서 선택' : '담당자 선택'}
                </h2>
                
                <button 
                  onClick={handleDepartmentModalClose}
                  className="w-8 h-8 bg-white/20 rounded-full flex items-center justify-center hover:bg-white/30 transition-colors"
                >
                  <i className="fas fa-times w-4 h-4"></i>
                </button>
              </div>
              
              <p className="text-white/90 text-sm">
                {modalStep === 1 ? "부서를 선택해주세요" : "담당자를 선택해주세요"}
              </p>

              {/* 진행 상태 표시 */}
              <div className="mt-4">
                <div className="flex items-center justify-between text-xs mb-2">
                  <span className="text-white font-medium">1. 부서</span>
                  <span className={modalStep >= 2 ? 'text-white font-medium' : 'text-white/60'}>
                    2. 담당자
                  </span>
                </div>
                <div className="w-full bg-white/20 rounded-full h-1">
                  <div 
                    className="bg-white h-1 rounded-full transition-all duration-300"
                    style={{ width: `${(modalStep / 2) * 100}%` }}
                  ></div>
                </div>
              </div>
            </div>

            {/* 매장 정보 표시 */}
            <div className="bg-gray-50 p-3 border-b">
              <h3 className="font-bold text-gray-800 text-sm">{selectedStoreForModal.name}</h3>
              <p className="text-xs text-gray-600">{selectedStoreForModal.storeCode}</p>
            </div>

            {/* 모달 내용 */}
            <div className="flex-1 p-4 overflow-y-auto">
              {/* 1단계: 부서 선택 */}
              {modalStep === 1 && (
                <div className="space-y-3">
                  {Object.entries(selectedStoreForModal.departments).map(([dept, managers]) => (
                    <div
                      key={dept}
                      className="bg-white border border-gray-200 rounded-lg p-4 cursor-pointer hover:border-orange-300 hover:shadow-lg transition-all"
                      onClick={() => handleDepartmentSelect(dept)}
                    >
                      <div className="flex items-center justify-between">
                        <div className="flex items-center gap-3">
                          <div className={`w-10 h-10 rounded-lg flex items-center justify-center border ${getDepartmentColor(dept)}`}>
                            <i className="fas fa-users text-sm"></i>
                          </div>
                          <div>
                            <h3 className="font-bold text-gray-800 text-sm">{dept} 매장</h3>
                            <p className="text-xs text-gray-600">담당자 {managers.length}명</p>
                          </div>
                        </div>
                        <i className="fas fa-chevron-right text-gray-400"></i>
                      </div>
                    </div>
                  ))}
                </div>
              )}

              {/* 2단계: 담당자 선택 */}
              {modalStep === 2 && selectedDepartment && (
                <>
                  <div className="bg-gray-50 rounded-lg p-3 border mb-4">
                    <span className={`inline-block px-2 py-1 text-xs rounded-full border ${getDepartmentColor(selectedDepartment)}`}>
                      {selectedDepartment} 매장
                    </span>
                  </div>

                  <div className="space-y-3">
                    {selectedStoreForModal.departments[selectedDepartment].map((manager, index) => (
                      <div
                        key={manager.id || index}
                        className={`bg-white border rounded-lg p-4 cursor-pointer transition-all ${
                          selectedManager?.id === manager.id
                            ? 'border-orange-500 bg-orange-50'
                            : 'border-gray-200 hover:border-orange-300 hover:shadow-lg'
                        }`}
                        onClick={() => handleManagerSelect(manager)}
                      >
                        <div className="flex items-center justify-between">
                          <div className="flex items-center gap-3">
                            <div className="w-10 h-10 bg-gradient-to-br from-orange-400 to-orange-500 rounded-lg flex items-center justify-center text-white font-bold text-sm">
                              {manager.managerName.charAt(0)}
                            </div>
                            <div>
                              <h3 className="font-bold text-gray-800 text-sm">{manager.managerName}</h3>
                              <p className="text-xs text-gray-600">
                                {manager.fullPhone || (manager.phone ? manager.phone.replace(/(\d{3})(\d{4})(\d{4})/, '$1-$2-$3') : '연락처 없음')}
                              </p>
                            </div>
                          </div>
                          {selectedManager?.id === manager.id && (
                            <i className="fas fa-check text-orange-500"></i>
                          )}
                        </div>
                      </div>
                    ))}
                  </div>
                </>
              )}
            </div>

            {/* 모달 푸터 */}
            <div className="p-4 border-t border-gray-200">
              <button
                onClick={handleDepartmentManagerConfirm}
                disabled={!selectedManager}
                className="w-full bg-gradient-to-br from-orange-500 to-orange-600 text-white font-bold py-3 rounded-xl shadow-lg hover:shadow-xl focus:outline-none focus:ring-4 focus:ring-orange-300 transition-all duration-300 disabled:bg-gray-300 disabled:cursor-not-allowed disabled:shadow-none"
              >
                {selectedManager ? '선택 완료' : '담당자를 선택해주세요'}
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default OriginStoreSelector;
