import React, { useState } from 'react';
import { useAuth } from '../../context/AuthContext';
import AccountIssue from '../AccountMD/Register';
import WelcomePopup from '../Popup/WelcomePopup';

const Login = ({ onLogin }) => {
  const { login, error, loading, clearError } = useAuth();
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    rememberMe: false
  });
  
  const [showPassword, setShowPassword] = useState(false);
  const [showAccountIssue, setShowAccountIssue] = useState(false);
  const [loginError, setLoginError] = useState('');
  const [showWelcomePopup, setShowWelcomePopup] = useState(false);

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));

    // 입력 시 에러 메시지 초기화
    if (loginError) setLoginError('');
    if (error) clearError();
  };

  const validateFormData = () => {
    if (!formData.username.trim()) {
      setLoginError('아이디를 입력해주세요.');
      return false;
    }
    
    if (!formData.password.trim()) {
      setLoginError('비밀번호를 입력해주세요.');
      return false;
    }
    
    if (formData.username.length < 6) {
      setLoginError('아이디는 6자리 이상이어야 합니다.');
      return false;
    }
    
    if (formData.password.length < 6) {
      setLoginError('비밀번호는 6자리 이상이어야 합니다.');
      return false;
    }
    
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateFormData()) return;

    try {
      await login(formData);
      console.log('✅ 로그인 성공!');
      
      if (onLogin) {
        onLogin(formData);
      }
    } catch (error) {
      console.error('❌ 로그인 실패:', error);
      setLoginError(error.message || '로그인에 실패했습니다.');
    }
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const handleAccountIssue = () => {
    setShowAccountIssue(true);
  };

  const handleCloseAccountIssue = () => {
    setShowAccountIssue(false);
  };

  const handleCloseWelcome = () => {
    setShowWelcomePopup(false);
  };

  return (
    <>
      <div className="bg-gray-100 min-h-screen flex items-center justify-center p-4 font-sans">
        <div className="w-full max-w-sm mx-auto bg-white shadow-lg rounded-3xl overflow-hidden min-h-[600px] max-h-[90vh] flex flex-col">
          
          {/* 헤더 */}
          <div className="bg-gradient-to-br from-orange-400 to-orange-500 p-6 sm:p-8 text-center">
            <h1 className="text-2xl sm:text-3xl font-bold text-white mb-2">
              Quinors LV
            </h1>
            <p className="text-orange-100 text-sm sm:text-base">
              매장 관리 시스템
            </p>
          </div>

          {/* 메인 콘텐츠 */}
          <main className="flex-1 p-6 sm:p-8 flex flex-col">
            
            {/* 에러 메시지 */}
            {(loginError || error) && (
              <div className="mb-4 p-3 bg-red-50 border border-red-200 rounded-lg">
                <p className="text-red-600 text-sm text-center whitespace-pre-line">
                  {loginError || error}
                </p>
              </div>
            )}

            {/* 로그인 폼 */}
            <form onSubmit={handleSubmit} className="space-y-4 sm:space-y-6">
              
              {/* 아이디 입력 */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  아이디
                </label>
                <input
                  type="text"
                  name="username"
                  value={formData.username}
                  onChange={handleInputChange}
                  className="w-full px-4 py-3 sm:py-4 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-orange-500 focus:border-transparent transition-all"
                  placeholder="아이디를 입력하세요"
                  disabled={loading}
                  autoComplete="username"
                />
              </div>

              {/* 비밀번호 입력 */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  비밀번호
                </label>
                <div className="relative">
                  <input
                    type={showPassword ? "text" : "password"}
                    name="password"
                    value={formData.password}
                    onChange={handleInputChange}
                    className="w-full px-4 py-3 sm:py-4 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-orange-500 focus:border-transparent transition-all pr-12"
                    placeholder="비밀번호를 입력하세요"
                    disabled={loading}
                    autoComplete="current-password"
                  />
                  <button
                    type="button"
                    onClick={togglePasswordVisibility}
                    className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600 transition-colors"
                    disabled={loading}
                  >
                    {showPassword ? (
                      <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
                        <path fillRule="evenodd" d="M3.707 2.293a1 1 0 00-1.414 1.414l14 14a1 1 0 001.414-1.414l-1.473-1.473A10.014 10.014 0 0019.542 10C18.268 5.943 14.478 3 10 3a9.958 9.958 0 00-4.512 1.074l-1.78-1.781zm4.261 4.26l1.514 1.515a2.003 2.003 0 012.45 2.45l1.514 1.514a4 4 0 00-5.478-5.478z" clipRule="evenodd" />
                        <path d="M12.454 16.697L9.75 13.992a4 4 0 01-3.742-3.741L2.335 6.578A9.98 9.98 0 00.458 10c1.274 4.057 5.065 7 9.542 7 .847 0 1.669-.105 2.454-.303z" />
                      </svg>
                    ) : (
                      <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
                        <path d="M10 12a2 2 0 100-4 2 2 0 000 4z" />
                        <path fillRule="evenodd" d="M.458 10C1.732 5.943 5.522 3 10 3s8.268 2.943 9.542 7c-1.274 4.057-5.064 7-9.542 7S1.732 14.057.458 10zM14 10a4 4 0 11-8 0 4 4 0 018 0z" clipRule="evenodd" />
                      </svg>
                    )}
                  </button>
                </div>
              </div>

              {/* 로그인 유지 체크박스 */}
              <div className="flex items-center">
                <input
                  type="checkbox"
                  name="rememberMe"
                  id="rememberMe"
                  checked={formData.rememberMe}
                  onChange={handleInputChange}
                  className="w-4 h-4 text-orange-500 border-gray-300 rounded focus:ring-orange-500 focus:ring-2"
                  disabled={loading}
                />
                <label htmlFor="rememberMe" className="ml-2 text-sm text-gray-600">
                  로그인 상태 유지
                </label>
              </div>

              {/* 로그인 버튼 */}
              <button
                type="submit"
                disabled={loading}
                className="w-full bg-gradient-to-br from-orange-400 to-orange-500 text-white font-bold py-3 sm:py-4 rounded-xl shadow-lg hover:shadow-xl focus:outline-none focus:ring-4 focus:ring-orange-300 transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {loading ? (
                  <div className="flex items-center justify-center">
                    <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                      <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                    </svg>
                    로그인 중...
                  </div>
                ) : (
                  '로그인'
                )}
              </button>
            </form>

            {/* 구분선 */}
            <div className="relative my-6 sm:my-8">
              <div className="absolute inset-0 flex items-center">
                <div className="w-full border-t border-gray-200"></div>
              </div>
              <div className="relative flex justify-center text-sm">
                <span className="px-4 bg-white text-gray-500">또는</span>
              </div>
            </div>

            {/* 회원가입 신청 버튼 */}
            <div className="space-y-3 mb-6">
              <button
                className="w-full flex items-center justify-center gap-3 py-3 sm:py-4 border border-gray-200 rounded-xl hover:bg-gray-50 transition-all"
                onClick={handleAccountIssue}
                disabled={loading}
              >
                <svg className="w-5 h-5 text-orange-500" fill="currentColor" viewBox="0 0 640 512">
                  <path d="M96 128a128 128 0 1 1 256 0A128 128 0 1 1 96 128zM0 482.3C0 383.8 79.8 304 178.3 304h91.4C368.2 304 448 383.8 448 482.3c0 16.4-13.3 29.7-29.7 29.7H29.7C13.3 512 0 498.7 0 482.3zM504 312V248H440c-13.3 0-24-10.7-24-24s10.7-24 24-24h64V136c0-13.3 10.7-24 24-24s24 10.7 24 24v64h64c13.3 0 24 10.7 24 24s-10.7 24-24 24H552v64c0 13.3-10.7 24-24 24s-24-10.7-24-24z"/>
                </svg>
                <span className="font-medium text-gray-700 text-sm sm:text-base">회원가입 신청</span>
              </button>
            </div>

            {/* 안내 텍스트 */}
            <div className="text-center">
              <p className="text-xs sm:text-sm text-gray-500 mb-3">계정이 없으신가요?</p>
              <div className="text-xs text-gray-400 leading-relaxed">
                • 회원가입 신청 후 관리자 승인을 기다려주세요<br/>
                • 승인 완료 후 발급받은 ID로 로그인하세요<br/>
                • 최초 로그인 후 비밀번호를 변경해주세요
              </div>
            </div>
          </main>
        </div>
      </div>

      {/* 회원가입 신청 모달 */}
      <AccountIssue
        isOpen={showAccountIssue}
        onClose={handleCloseAccountIssue}
      />

      {/* 환영 팝업 */}
      <WelcomePopup
        isOpen={showWelcomePopup}
        onClose={handleCloseWelcome}
      />
    </>
  );
};

export default Login;
