import React, { useState, useEffect, useCallback } from 'react';
import { useDelivery } from '../../context/DeliveryContext';
import { useAuth } from '../../context/AuthContext';
import Header from '../common/Header';
import FloatingPreview from '../common/FloatingPreview';
import Button from '../ui/Button';
import SearchInput from '../common/SearchInput';
import Card from '../ui/Card';

const OriginStoreSelector = () => {
  const { deliveryType, originStore, dispatch, setOriginStore } = useDelivery();
  const { user, token } = useAuth();
  
  const [selectedStore, setSelectedStore] = useState(originStore);
  const [searchTerm, setSearchTerm] = useState('');
  const [stores, setStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [forceUpdate, setForceUpdate] = useState(0);

  // ê¸°ì¡´ 3ë‹¨ê³„ ì„ íƒ ìƒíƒœ (ëª¨ë‹¬ì—ì„œ ì‚¬ìš©)
  const [step, setStep] = useState(1);
  const [selectedStoreData, setSelectedStoreData] = useState(null);
  const [selectedDepartment, setSelectedDepartment] = useState(null);
  const [selectedManager, setSelectedManager] = useState(null);
  const [departmentManagers, setDepartmentManagers] = useState([]);

  // ëª¨ë‹¬ ìƒíƒœ
  const [showModal, setShowModal] = useState(false);
  const [modalStep, setModalStep] = useState(1);

  // ëª¨ë‹¬ ë‚´ ê²€ìƒ‰ ìƒíƒœ
  const [modalSearchTerm, setModalSearchTerm] = useState('');

  // ê°•ì œ ë¦¬ë Œë”ë§ í•¨ìˆ˜
  const triggerUpdate = useCallback(() => {
    setForceUpdate(prev => prev + 1);
  }, []);

  // ë§¤ì¥ ë°ì´í„° ê°€ì ¸ì˜¤ê¸°
  useEffect(() => {
    const fetchStores = async () => {
      if (!token) {
        console.log('âŒ í† í°ì´ ì—†ìŒ');
        setLoading(false);
        setError('ë¡œê·¸ì¸ì´ í•„ìš”í•©ë‹ˆë‹¤');
        setStores([]);
        return;
      }

      try {
        setLoading(true);
        setError('');
        
        console.log('ğŸ” API í˜¸ì¶œ ì‹œì‘');
        const response = await fetch('https://quinors-lv-backend.ngrok.io/api/auth/stores?active=true&limit=100', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
        });

        if (!response.ok) {
          throw new Error(`API í˜¸ì¶œ ì‹¤íŒ¨: ${response.status}`);
        }

        const result = await response.json();
        console.log('ğŸ” ì„œë²„ ì‘ë‹µ ì „ì²´:', result);

        if (!result.success || !result.data || result.data.length === 0) {
          throw new Error('ë§¤ì¥ ë°ì´í„°ê°€ ì—†ìŠµë‹ˆë‹¤');
        }

        // âœ… ì„œë²„ ì‘ë‹µ êµ¬ì¡°ì— ë§ê²Œ ë°ì´í„° ì²˜ë¦¬
        const finalStores = result.data.map((store, index) => {
          console.log(`ğŸ” === ë§¤ì¥ ${index + 1}: ${store.storeName} ===`);
          console.log('ì›ë³¸ ë§¤ì¥ ë°ì´í„°:', store);
          console.log('ë¶€ì„œ ë°°ì—´:', store.departments);
          
          // ë¶€ì„œë³„ë¡œ ë‹´ë‹¹ì ê·¸ë£¹í™”
          const departmentGroups = {};
          
          if (Array.isArray(store.departments)) {
            store.departments.forEach((dept, deptIndex) => {
              console.log(`  ë¶€ì„œ ${deptIndex + 1}:`, dept);
              
              const department = dept.department || 'ì¼ë°˜';
              const managerName = dept.managerName || 'ë‹´ë‹¹ì';
              
              if (!departmentGroups[department]) {
                departmentGroups[department] = [];
              }
              
              departmentGroups[department].push({
                id: `${store._id}_${department}_${managerName}_${deptIndex}`,
                managerName: managerName,
                phone: dept.managerPhone || '',
                fullPhone: dept.fullPhone || (dept.managerPhone ? 
                  dept.managerPhone.replace(/(\d{3})(\d{4})(\d{4})/, '$1-$2-$3') : '')
              });
              
              console.log(`  âœ… ì¶”ê°€ë¨: ${department} > ${managerName}`);
            });
          }
          
          const processedStore = {
            id: store._id,
            storeCode: store.storeCode || '',
            name: store.storeName || '',
            address: store.address || '',
            isActive: store.isActive !== false,
            createdAt: store.createdAt,
            departments: departmentGroups
          };
          
          console.log(`ğŸ” ì²˜ë¦¬ëœ ë§¤ì¥:`, processedStore);
          console.log(`ğŸ” ë¶€ì„œ ê°œìˆ˜: ${Object.keys(departmentGroups).length}`);
          Object.entries(departmentGroups).forEach(([dept, managers]) => {
            console.log(`  - ${dept}: ${managers.length}ëª… (${managers.map(m => m.managerName).join(', ')})`);
          });
          console.log(`ğŸ” =============================`);
          
          return processedStore;
        });
        
        console.log('ğŸ” ===== ìµœì¢… ê²°ê³¼ =====');
        console.log('ìµœì¢… ë§¤ì¥ ìˆ˜:', finalStores.length);
        console.log('ìµœì¢… ë§¤ì¥ ë°°ì—´:', JSON.stringify(finalStores, null, 2));
        
        // ê° ë§¤ì¥ì˜ ë¶€ì„œë³„ ë‹´ë‹¹ì ìˆ˜ ìƒì„¸ í™•ì¸
        finalStores.forEach((store, idx) => {
          console.log(`ğŸ” ë§¤ì¥ ${idx + 1}: "${store.name}"`);
          Object.entries(store.departments).forEach(([dept, managers]) => {
            console.log(`  â”” ${dept} ë¶€ì„œ: ${managers.length}ëª…`);
            managers.forEach((manager, mIdx) => {
              console.log(`    ${mIdx + 1}. ${manager.managerName} (${manager.phone || 'ì—°ë½ì²˜ ì—†ìŒ'})`);
            });
          });
        });
        console.log('ğŸ” ==================');
        
        setStores(finalStores);
        
        // ë‹¤ìŒ í‹±ì—ì„œ ê°•ì œ ì—…ë°ì´íŠ¸
        setTimeout(() => {
          console.log('ğŸ” ìƒíƒœ ì—…ë°ì´íŠ¸ í›„ stores ê¸¸ì´:', finalStores.length);
          triggerUpdate();
        }, 100);
        
      } catch (error) {
        console.error('âŒ ì—ëŸ¬:', error);
        setError(error.message);
        setStores([]);
      } finally {
        setLoading(false);
      }
    };

    fetchStores();
  }, [token, triggerUpdate]);

  // ê²€ìƒ‰ í•„í„°ë§
  const filteredStores = React.useMemo(() => {
    console.log('ğŸ” í•„í„°ë§ ì‹œì‘ - ì›ë³¸ stores:', stores);
    
    if (!Array.isArray(stores) || stores.length === 0) {
      console.log('ğŸ” storesê°€ ë¹„ì–´ìˆê±°ë‚˜ ë°°ì—´ì´ ì•„ë‹˜');
      return [];
    }

    if (!searchTerm.trim()) {
      console.log('ğŸ” ê²€ìƒ‰ì–´ ì—†ìŒ, ì „ì²´ ë°˜í™˜');
      return stores;
    }
    
    const searchLower = searchTerm.toLowerCase();
    const filtered = stores.filter(store => {
      const matchStore = (store.name && store.name.toLowerCase().includes(searchLower)) ||
                        (store.storeCode && store.storeCode.toLowerCase().includes(searchLower));
      
      const matchManager = Object.values(store.departments || {}).some(managers =>
        Array.isArray(managers) && managers.some(manager => 
          manager && manager.managerName && manager.managerName.toLowerCase().includes(searchLower)
        )
      );
      
      return matchStore || matchManager;
    });
    
    console.log('ğŸ” í•„í„°ë§ ê²°ê³¼:', filtered);
    return filtered;
  }, [stores, searchTerm, forceUpdate]);

  // ë””ë²„ê¹…ìš© ë¡œê·¸
  console.log('ğŸ” í˜„ì¬ ë Œë”ë§ ìƒíƒœ:', {
    stores: stores.length,
    storesDetail: stores,
    filteredStores: filteredStores.length,
    loading,
    error
  });

  // ëª¨ë‹¬ ê´€ë ¨ í•¨ìˆ˜ë“¤
  const handleStoreSelect = (store) => {
    console.log('ğŸ” ì„ íƒëœ ë§¤ì¥ ì „ì²´:', JSON.stringify(store, null, 2));
    console.log('ğŸ” ì„ íƒëœ ë§¤ì¥ì˜ ë¶€ì„œë“¤:', Object.keys(store.departments));
    Object.entries(store.departments).forEach(([dept, managers]) => {
      console.log(`ğŸ” ${dept} ë¶€ì„œ ë‹´ë‹¹ìë“¤:`, managers);
    });
    setSelectedStoreData(store);
    setShowModal(true);
    setModalStep(1);
    setSelectedDepartment(null);
    setSelectedManager(null);
    setModalSearchTerm('');
  };

  const handleModalDepartmentSelect = (department) => {
    console.log('ğŸ” ì„ íƒëœ ë¶€ì„œ:', department);
    const managers = selectedStoreData.departments[department] || [];
    console.log('ğŸ” í•´ë‹¹ ë¶€ì„œì˜ ë‹´ë‹¹ìë“¤:', managers);
    setSelectedDepartment(department);
    setDepartmentManagers(managers);
    setModalStep(2);
    setModalSearchTerm('');
  };

  const handleModalManagerSelect = (manager) => {
    console.log('ğŸ” ì„ íƒëœ ë‹´ë‹¹ì:', manager);
    setSelectedManager(manager);
  };

  const handleModalConfirm = () => {
    if (selectedManager && selectedDepartment && selectedStoreData) {
      const finalSelection = {
        store: selectedStoreData,
        department: selectedDepartment,
        manager: selectedManager,
        displayName: `${selectedStoreData.name} ${selectedDepartment} - ${selectedManager.managerName}`
      };
      
      setSelectedStore(finalSelection);
      dispatch({ type: 'SET_ORIGIN_STORE', payload: finalSelection });
      setShowModal(false);
    }
  };

  const handleModalClose = () => {
    setShowModal(false);
    setModalStep(1);
    setSelectedDepartment(null);
    setSelectedManager(null);
    setModalSearchTerm('');
  };

  const handleModalBack = () => {
    if (modalStep === 2) {
      setModalStep(1);
      setSelectedManager(null);
