import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import AccountIssue from '../AccountMD/AccountIssue';
import WelcomePopup from '../Popup/WelcomePopup';

const Login = ({ onLogin, onNavigateToRegister, onNavigateToIdRequest }) => {
  const { login, error, loading, clearError } = useAuth();
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    rememberMe: false
  });
  const [showPassword, setShowPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [showAccountIssue, setShowAccountIssue] = useState(false);
  const [loginError, setLoginError] = useState('');
  
  // 팝업 상태 관리
  const [showWelcomePopup, setShowWelcomePopup] = useState(false);

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));

    // 입력 시 에러 메시지 초기화
    if (loginError) {
      setLoginError('');
    }
    if (error) {
      clearError();
    }
  };

  const validateFormData = () => {
    if (!formData.username.trim()) {
      setLoginError('사용자 ID를 입력해주세요.');
      return false;
    }
    if (!formData.password.trim()) {
      setLoginError('비밀번호를 입력해주세요.');
      return false;
    }
    if (formData.username.length < 6) {
      setLoginError('사용자 ID는 6자리 이상이어야 합니다.');
      return false;
    }
    if (formData.password.length < 6) {
      setLoginError('비밀번호는 6자리 이상이어야 합니다.');
      return false;
    }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateFormData()) {
      return;
    }

    setIsLoading(true);
    setLoginError('');

    try {
      await login(formData);
      console.log('✅ 로그인 성공!');
      if (onLogin) {
        onLogin(formData);
      }
    } catch (error) {
      console.error('❌ 로그인 실패:', error);
      setLoginError(error.message || '로그인에 실패했습니다.');
    } finally {
      setIsLoading(false);
    }
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const handleAccountIssue = () => {
    setShowAccountIssue(true);
  };

  const handleCloseAccountIssue = () => {
    setShowAccountIssue(false);
  };

  // 팝업 제어 함수들
  const handleCloseWelcome = () => {
    setShowWelcomePopup(false);
  };

  return (
    <>
      <div className="min-h-screen bg-gradient-to-br from-orange-50 to-white flex items-center justify-center p-4">
        <div className="w-full max-w-sm">
          {/* Header */}
          <header className="text-center mb-8">
            <div className="w-20 h-20 bg-gradient-to-br from-orange-400 to-orange-500 rounded-2xl flex items-center justify-center mx-auto mb-4 shadow-lg">
              <svg className="w-10 h-10 text-white" fill="currentColor" viewBox="0 0 640 512">
                <path d="M144 0a80 80 0 1 1 0 160A80 80 0 1 1 144 0zM512 0a80 80 0 1 1 0 160A80 80 0 1 1 512 0zM0 298.7C0 239.8 47.8 192 106.7 192h42.7c15.9 0 31 3.5 44.6 9.7c-1.3 7.2-1.9 14.7-1.9 22.3c0 38.2 16.8 72.5 43.3 96c-.2 0-.4 0-.7 0H21.3C9.6 320 0 310.4 0 298.7zM405.3 320c-.2 0-.4 0-.7 0c26.6-23.5 43.3-57.8 43.3-96c0-7.6-.7-15-1.9-22.3c13.6-6.3 28.7-9.7 44.6-9.7h42.7C592.2 192 640 239.8 640 298.7c0 11.8-9.6 21.3-21.3 21.3H405.3zM224 224a96 96 0 1 1 192 0 96 96 0 1 1 -192 0zM128 485.3C128 411.7 187.7 352 261.3 352H378.7C452.3 352 512 411.7 512 485.3c0 14.7-11.9 26.7-26.7 26.7H154.7c-14.7 0-26.7-11.9-26.7-26.7z"/>
              </svg>
            </div>
            <h1 className="text-2xl font-bold text-gray-900 mb-2">Quinors LV</h1>
            <p className="text-gray-600">배송 관리 시스템</p>
          </header>

          {/* Main Content */}
          <main className="bg-white rounded-2xl shadow-xl p-6">
            {/* Error Display */}
            {(loginError || error) && (
              <div className="mb-4 p-3 bg-red-50 border border-red-200 rounded-lg">
                <p className="text-red-600 text-sm">{loginError || error}</p>
              </div>
            )}

            {/* Login Form */}
            <form onSubmit={handleSubmit} className="space-y-4">
              {/* Username Field */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  사용자 ID
                </label>
                <input
                  type="text"
                  name="username"
                  value={formData.username}
                  onChange={handleInputChange}
                  className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-orange-500 focus:border-transparent outline-none transition-all"
                  placeholder="승인받은 사용자 ID를 입력하세요"
                  required
                  disabled={isLoading || loading}
                />
              </div>

              {/* Password Field */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  비밀번호
                </label>
                <div className="relative">
                  <input
                    type={showPassword ? "text" : "password"}
                    name="password"
                    value={formData.password}
                    onChange={handleInputChange}
                    className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-orange-500 focus:border-transparent outline-none transition-all pr-10"
                    placeholder="비밀번호를 입력하세요"
                    required
                    disabled={isLoading || loading}
                  />
                  <button
                    type="button"
                    onClick={togglePasswordVisibility}
                    className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600"
                    disabled={isLoading || loading}
                  >
                    <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
                      {showPassword ? (
                        <path fillRule="evenodd" d="M3.707 2.293a1 1 0 00-1.414 1.414l14 14a1 1 0 001.414-1.414l-1.473-1.473A10.014 10.014 0 0019.542 10C18.268 5.943 14.478 3 10 3a9.958 9.958 0 00-4.512 1.074l-1.78-1.781zm4.261 4.26l1.514 1.515a2.003 2.003 0 012.45 2.45l1.514 1.514a4 4 0 00-5.478-5.478z" clipRule="evenodd" />
                      ) : (
                        <path d="M10 12a2 2 0 100-4 2 2 0 000 4z" />
                      )}
                    </svg>
                  </button>
                </div>
              </div>

              {/* Remember Me */}
              <div className="flex items-center">
                <input
                  type="checkbox"
                  name="rememberMe"
                  checked={formData.rememberMe}
                  onChange={handleInputChange}
                  className="w-4 h-4 text-orange-500 border-gray-300 rounded focus:ring-orange-500"
                  disabled={isLoading || loading}
                />
                <label className="ml-2 text-sm text-gray-600">로그인 상태 유지</label>
              </div>

              {/* Login Button */}
              <button
                type="submit"
                disabled={isLoading || loading}
                className="w-full bg-gradient-to-br from-orange-400 to-orange-500 text-white font-bold py-3.5 rounded-xl shadow-lg hover:shadow-xl focus:outline-none focus:ring-4 focus:ring-orange-300 transition-all duration-300 mt-6 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                <div className="flex items-center justify-center">
                  {(isLoading || loading) ? (
                    <>
                      <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                        <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                        <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                      </svg>
                      로그인 중...
                    </>
                  ) : (
                    '로그인'
                  )}
                </div>
              </button>
            </form>

            <div className="relative my-6">
              <div className="absolute inset-0 flex items-center">
                <div className="w-full border-t border-gray-200"></div>
              </div>
              <div className="relative flex justify-center text-sm">
                <span className="px-4 bg-white text-gray-500">또는</span>
              </div>
            </div>

            <div className="space-y-3">
              <button
                onClick={handleAccountIssue}
                className="w-full flex items-center justify-center gap-3 py-3.5 border border-gray-200 rounded-xl hover:bg-gray-50 transition-all"
                disabled={isLoading || loading}
              >
                <svg className="w-5 h-5 text-gray-600" fill="currentColor" viewBox="0 0 20 20">
                  <path fillRule="evenodd" d="M10 3a1 1 0 011 1v5h5a1 1 0 110 2h-5v5a1 1 0 11-2 0v-5H4a1 1 0 110-2h5V4a1 1 0 011-1z" clipRule="evenodd"/>
                </svg>
                <span className="font-medium text-gray-700">회원가입 신청</span>
              </button>
            </div>

            {/* Help Section */}
            <div className="mt-6 bg-gray-50 rounded-lg p-4">
              <h3 className="font-medium text-gray-900 mb-2 text-sm">회원가입 및 로그인 안내</h3>
              <ul className="text-xs text-gray-600 space-y-1">
                <li>• 회원가입 신청 후 관리자 승인을 기다려주세요</li>
                <li>• 승인 완료 후 발급받은 ID와 임시 비밀번호로 로그인하세요</li>
                <li>• 최초 로그인 후 반드시 비밀번호를 변경해주세요</li>
                <li>• 5회 로그인 실패 시 1시간 동안 계정이 잠깁니다</li>
                <li>• 문의사항은 관리자에게 연락해주세요</li>
              </ul>
            </div>
          </main>
        </div>
      </div>

      {/* 회원가입 신청 모달 */}
      <AccountIssue
        isOpen={showAccountIssue}
        onClose={handleCloseAccountIssue}
      />

      {/* 팝업 컴포넌트 - 연결 */}
      <WelcomePopup
        isOpen={showWelcomePopup}
        onClose={handleCloseWelcome}
      />
    </>
  );
};

export default Login;
