import React, { useState, useEffect, useRef } from 'react';

const AccountManage = ({ onBackToAdmin }) => {
  const [stats, setStats] = useState({
    total: 0,
    active: 0,
    inactive: 0
  });
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [processingIds, setProcessingIds] = useState(new Set());
  const [searchTerm, setSearchTerm] = useState('');
  const [activeFilter, setActiveFilter] = useState('Ï†ÑÏ≤¥');
  const [error, setError] = useState(null);
  
  // Ïª¥Ìè¨ÎÑåÌä∏ ÎßàÏö¥Ìä∏ ÏÉÅÌÉú Ï∂îÏ†Å
  const isMountedRef = useRef(true);
  const intervalRef = useRef(null);

  // API Ìò∏Ï∂ú Ìï®ÏàòÎì§
  const fetchUsers = async () => {
    try {
      console.log('üë• ÏÇ¨Ïö©Ïûê Î™©Î°ù API Ìò∏Ï∂ú ÏãúÏûë...');
      setLoading(true);
      
      const response = await fetch('http://localhost:5480/api/admin/users');
      const result = await response.json();
      
      console.log('üë• ÏÇ¨Ïö©Ïûê Î™©Î°ù API ÏùëÎãµ:', result);
      
      if (response.ok && result.success) {
        console.log('üë• ‚úÖ ÏÇ¨Ïö©Ïûê Î™©Î°ù Îç∞Ïù¥ÌÑ∞ Ï≤òÎ¶¨ ÏÑ±Í≥µ:', result.data);
        if (isMountedRef.current) {
          setUsers(result.data || []);
          setFilteredUsers(result.data || []);
          calculateStats(result.data || []);
          setError(null);
        }
      } else {
        console.error('üë• ‚ùå ÏÇ¨Ïö©Ïûê Î™©Î°ù API Ïã§Ìå®:', result);
        setError('ÏÇ¨Ïö©Ïûê Î™©Î°ù Ï°∞Ìöå Ïã§Ìå®: ' + (result.message || 'Unknown error'));
        if (isMountedRef.current) {
          setUsers([]);
          setFilteredUsers([]);
        }
      }
    } catch (error) {
      console.error('üë• ‚ùå ÏÇ¨Ïö©Ïûê Î™©Î°ù Ï°∞Ìöå ÎÑ§Ìä∏ÏõåÌÅ¨ Ïò§Î•ò:', error);
      setError('ÏÇ¨Ïö©Ïûê Î™©Î°ù Ï°∞Ìöå Ï§ë ÎÑ§Ìä∏ÏõåÌÅ¨ Ïò§Î•ò: ' + error.message);
      if (isMountedRef.current) {
        setUsers([]);
        setFilteredUsers([]);
      }
    } finally {
      if (isMountedRef.current) {
        setLoading(false);
      }
    }
  };

  const calculateStats = (userData) => {
    const total = userData.length;
    const active = userData.filter(user => user.isActive).length;
    const inactive = total - active;
    
    setStats({ total, active, inactive });
    console.log('üìä ÌÜµÍ≥Ñ Í≥ÑÏÇ∞:', { total, active, inactive });
  };

  const handleStatusToggle = async (userId, currentStatus, buttonElement) => {
    if (!isMountedRef.current) return;
    
    try {
      const newStatus = !currentStatus;
      console.log(`üîÑ Í≥ÑÏ†ï ÏÉÅÌÉú Î≥ÄÍ≤Ω ÏãúÏûë: ${userId}, ${currentStatus} -> ${newStatus}`);
      
      // HTMLÍ≥º ÎèôÏùºÌïú Î≤ÑÌäº Ìö®Í≥º
      if (buttonElement) {
        buttonElement.innerHTML = '<svg class="w-4 h-4 animate-spin" fill="currentColor" viewBox="0 0 24 24"><path d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"/></svg>';
        buttonElement.disabled = true;
      }
      
      setProcessingIds(prev => new Set([...prev, userId]));
      
      const response = await fetch(`http://localhost:5480/api/admin/users/${userId}/status`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          isActive: newStatus,
          notes: `Í¥ÄÎ¶¨ÏûêÏóê ÏùòÌïú ${newStatus ? 'ÌôúÏÑ±Ìôî' : 'ÎπÑÌôúÏÑ±Ìôî'}`
        }),
      });

      const result = await response.json();
      console.log('üîÑ ÏÉÅÌÉú Î≥ÄÍ≤Ω API ÏùëÎãµ:', result);
      
      if (response.ok && result.success) {
        // HTMLÍ≥º ÎèôÏùºÌïú ÏôÑÎ£å Ìö®Í≥º
        setTimeout(() => {
          if (buttonElement) {
            const parentCard = buttonElement.closest('.bg-white');
            const statusBadge = parentCard.querySelector('span[class*="rounded-full"]');
            
            if (newStatus) {
              // ÌôúÏÑ±Ìôî
              buttonElement.innerHTML = 'ÎπÑÌôúÏÑ±Ìôî';
              buttonElement.className = 'px-4 py-2 bg-red-100 text-red-600 text-sm rounded-lg hover:bg-red-200 transition-colors';
              if (statusBadge) {
                statusBadge.className = 'px-2 py-1 bg-green-100 text-green-600 text-xs rounded-full';
                statusBadge.textContent = 'ÌôúÏÑ±';
              }
            } else {
              // ÎπÑÌôúÏÑ±Ìôî
              buttonElement.innerHTML = 'ÌôúÏÑ±Ìôî';
              buttonElement.className = 'px-4 py-2 bg-green-100 text-green-600 text-sm rounded-lg hover:bg-green-200 transition-colors';
              if (statusBadge) {
                statusBadge.className = 'px-2 py-1 bg-gray-100 text-gray-600 text-xs rounded-full';
                statusBadge.textContent = 'ÎπÑÌôúÏÑ±';
              }
            }
            buttonElement.disabled = false;
          }
        }, 1500);
        
        // Îç∞Ïù¥ÌÑ∞ ÏÉàÎ°úÍ≥†Ïπ®
        await fetchUsers();
      } else {
        console.error('üîÑ ÏÉÅÌÉú Î≥ÄÍ≤Ω Ïã§Ìå®:', result);
        alert(`ÏÉÅÌÉú Î≥ÄÍ≤Ω Ïã§Ìå®: ${result.message}`);
        if (buttonElement) {
          buttonElement.innerHTML = currentStatus ? 'ÎπÑÌôúÏÑ±Ìôî' : 'ÌôúÏÑ±Ìôî';
          buttonElement.disabled = false;
        }
      }
    } catch (error) {
      console.error('üîÑ ÏÉÅÌÉú Î≥ÄÍ≤Ω Ï§ë Ïò§Î•ò:', error);
      alert('ÏÉÅÌÉú Î≥ÄÍ≤Ω Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
      if (buttonElement) {
        buttonElement.innerHTML = currentStatus ? 'ÎπÑÌôúÏÑ±Ìôî' : 'ÌôúÏÑ±Ìôî';
        buttonElement.disabled = false;
      }
    } finally {
      setProcessingIds(prev => {
        const newSet = new Set(prev);
        newSet.delete(userId);
        return newSet;
      });
    }
  };

  const handleSearch = (term) => {
    setSearchTerm(term);
    filterUsers(term, activeFilter);
  };

  const handleFilterChange = (filter) => {
    setActiveFilter(filter);
    filterUsers(searchTerm, filter);
  };

  const filterUsers = (searchTerm, filter) => {
    let filtered = [...users];
    
    // Í≤ÄÏÉâ ÌïÑÌÑ∞ÎßÅ
    if (searchTerm.trim()) {
      filtered = filtered.filter(user => 
        user.managerName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        user.storeCode.toLowerCase().includes(searchTerm.toLowerCase()) ||
        (user.userId && user.userId.toLowerCase().includes(searchTerm.toLowerCase()))
      );
    }
    
    // ÏÉÅÌÉú ÌïÑÌÑ∞ÎßÅ
    if (filter === 'ÌôúÏÑ±') {
      filtered = filtered.filter(user => user.isActive);
    } else if (filter === 'ÎπÑÌôúÏÑ±') {
      filtered = filtered.filter(user => !user.isActive);
    }
    
    setFilteredUsers(filtered);
    console.log('üîç ÌïÑÌÑ∞ÎßÅ Í≤∞Í≥º:', { searchTerm, filter, count: filtered.length });
  };

  const getTimeAgo = (dateString) => {
    try {
      const now = new Date();
      const date = new Date(dateString);
      const diffMs = now - date;
      const diffMins = Math.floor(diffMs / 60000);
      const diffHours = Math.floor(diffMs / 3600000);
      const diffDays = Math.floor(diffMs / 86400000);

      if (diffMins < 1) return 'Î∞©Í∏à Ï†Ñ';
      if (diffMins < 60) return `${diffMins}Î∂Ñ Ï†Ñ`;
      if (diffHours < 24) return `${diffHours}ÏãúÍ∞Ñ Ï†Ñ`;
      return `${diffDays}Ïùº Ï†Ñ`;
    } catch (error) {
      return 'Ïïå Ïàò ÏóÜÏùå';
    }
  };

  const getStoreIcon = (storeCode) => {
    // Îß§Ïû• ÏΩîÎìúÏóê Îî∞Î•∏ ÏïÑÏù¥ÏΩò ÏÉâÏÉÅ Í≤∞Ï†ï
    const hash = storeCode.split('').reduce((a, b) => {
      a = ((a << 5) - a) + b.charCodeAt(0);
      return a & a;
    }, 0);
    
    const colors = [
      'bg-green-100 text-green-600',
      'bg-blue-100 text-blue-600',
      'bg-orange-100 text-orange-600',
      'bg-purple-100 text-purple-600',
      'bg-indigo-100 text-indigo-600',
      'bg-pink-100 text-pink-600',
      'bg-yellow-100 text-yellow-600',
      'bg-red-100 text-red-600'
    ];
    
    return colors[Math.abs(hash) % colors.length];
  };

  // Ïª¥Ìè¨ÎÑåÌä∏ ÎßàÏö¥Ìä∏ Ïãú Îç∞Ïù¥ÌÑ∞ Î°úÎìú
  useEffect(() => {
    console.log('üöÄ Í≥ÑÏ†ï Í¥ÄÎ¶¨ Ïª¥Ìè¨ÎÑåÌä∏ ÎßàÏö¥Ìä∏ - Îç∞Ïù¥ÌÑ∞ Î°úÎìú ÏãúÏûë');
    isMountedRef.current = true;
    
    fetchUsers();

    // ÏûêÎèô ÏÉàÎ°úÍ≥†Ïπ® ÏÑ§Ï†ï (60Ï¥àÎßàÎã§)
    intervalRef.current = setInterval(() => {
      if (isMountedRef.current) {
        console.log('üîÑ ÏûêÎèô ÏÉàÎ°úÍ≥†Ïπ® Ïã§Ìñâ');
        fetchUsers();
      }
    }, 60000);

    // Cleanup Ìï®Ïàò
    return () => {
      console.log('üßπ Í≥ÑÏ†ï Í¥ÄÎ¶¨ Ïª¥Ìè¨ÎÑåÌä∏ Ïñ∏ÎßàÏö¥Ìä∏ - Ï†ïÎ¶¨ ÏûëÏóÖ');
      isMountedRef.current = false;
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, []);

  return (
    <div className="bg-gray-100 font-sans">
