              {/* Font Awesome search ÎåÄÏ≤¥ */}
              <svg className="w-4 h-4 text-gray-400 absolute left-3 top-1/2 transform -translate-y-1/2" fill="currentColor" viewBox="0 0 512 512">
                <path d="M416 208c0 45.9-14.9 88.3-40 122.7L502.6 457.4c12.5 12.5 12.5 32.8 0 45.3s-32.8 12.5-45.3 0L330.7 376c-34.4 25.2-76.8 40-122.7 40C93.1 416 0 322.9 0 208S93.1 0 208 0S416 93.1 416 208zM208 352a144 144 0 1 0 0-288 144 144 0 1 0 0 288z"/>
              </svg>
            </div>
            <div className="flex gap-2">
              {['Ï†ÑÏ≤¥', 'ÌôúÏÑ±', 'ÎπÑÌôúÏÑ±'].map(filter => (
                <button
                  key={filter}
                  className={`px-4 py-2 text-sm rounded-lg transition-colors ${
                    activeFilter === filter 
                      ? 'bg-orange-500 text-white' 
                      : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                  }`}
                  onClick={() => handleFilterChange(filter)}
                >
                  {filter}
                </button>
              ))}
            </div>
          </div>

          {/* Account List */}
          <div id="account-list" className="space-y-3">
            {loading ? (
              <div className="text-center py-8 text-gray-500 text-sm">
                ‚è≥ Í≥ÑÏ†ï Î™©Î°ùÏùÑ Î∂àÎü¨Ïò§Îäî Ï§ë...
