import React, { useState, useEffect, useCallback } from 'react';
import { useDelivery } from '../../context/DeliveryContext';
import { useAuth } from '../../context/AuthContext';
import Header from '../common/Header';
import FloatingPreview from '../common/FloatingPreview';
import Button from '../ui/Button';
import SearchInput from '../common/SearchInput';
import Card from '../ui/Card';

const DestinationStoreSelector = () => {
  const { deliveryType, originStore, destinationStore, dispatch, setDestinationStore } = useDelivery();
  const { user, token } = useAuth();
  
  const [selectedStore, setSelectedStore] = useState(destinationStore);
  const [searchTerm, setSearchTerm] = useState('');
  const [stores, setStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [forceUpdate, setForceUpdate] = useState(0);

  // 모달 상태
  const [showModal, setShowModal] = useState(false);
  const [modalStep, setModalStep] = useState(1);
  const [selectedStoreData, setSelectedStoreData] = useState(null);
  const [selectedDepartment, setSelectedDepartment] = useState(null);
  const [selectedManager, setSelectedManager] = useState(null);
  const [departmentManagers, setDepartmentManagers] = useState([]);
  const [modalSearchTerm, setModalSearchTerm] = useState('');

  // ✅ originStore 구조 디버깅
  useEffect(() => {
    console.log('🔍 === originStore 디버깅 ===');
    console.log('전체 originStore:', originStore);
    if (originStore) {
      console.log('originStore.store:', originStore.store);
      console.log('originStore.department:', originStore.department);
      console.log('originStore.manager:', originStore.manager);
      console.log('originStore.displayName:', originStore.displayName);
    }
    console.log('🔍 ========================');
  }, [originStore]);

  // 강제 리렌더링 함수
  const triggerUpdate = useCallback(() => {
    setForceUpdate(prev => prev + 1);
  }, []);

  // 매장 데이터 가져오기
  useEffect(() => {
    const fetchStores = async () => {
      if (!token) {
        setLoading(false);
        setError('로그인이 필요합니다');
        setStores([]);
        return;
      }

      try {
        setLoading(true);
        setError('');
        
        const response = await fetch('https://quinors-lv-backend.ngrok.io/api/auth/stores?active=true&limit=100', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
        });

        if (!response.ok) {
          throw new Error(`API 호출 실패: ${response.status}`);
        }

        const result = await response.json();

        if (!result.success || !result.data || result.data.length === 0) {
          throw new Error('매장 데이터가 없습니다');
        }

        // 서버 응답 구조에 맞게 데이터 처리
        const finalStores = result.data.map((store) => {
          // 부서별로 담당자 그룹화
          const departmentGroups = {};
          
          if (Array.isArray(store.departments)) {
            store.departments.forEach((dept, deptIndex) => {
              const department = dept.department || '일반';
              const managerName = dept.managerName || '담당자';
              
              if (!departmentGroups[department]) {
                departmentGroups[department] = [];
              }
              
              departmentGroups[department].push({
                id: `${store._id}_${department}_${managerName}_${deptIndex}`,
                managerName: managerName,
                phone: dept.managerPhone || '',
                fullPhone: dept.fullPhone || (dept.managerPhone ? 
                  dept.managerPhone.replace(/(\d{3})(\d{4})(\d{4})/, '$1-$2-$3') : '')
              });
            });
          }
          
          return {
            id: store._id,
            storeCode: store.storeCode || '',
            name: store.storeName || '',
            address: store.address || '',
            isActive: store.isActive !== false,
            createdAt: store.createdAt,
            departments: departmentGroups
          };
        });
        
        setStores(finalStores);
        
        setTimeout(() => {
          triggerUpdate();
        }, 100);
        
      } catch (error) {
        setError(error.message);
        setStores([]);
      } finally {
        setLoading(false);
      }
    };

    fetchStores();
  }, [token, triggerUpdate]);

  // 검색 필터링
  const filteredStores = React.useMemo(() => {
    if (!Array.isArray(stores) || stores.length === 0) {
      return [];
    }

    if (!searchTerm.trim()) {
      return stores;
    }
    
    const searchLower = searchTerm.toLowerCase();
    return stores.filter(store => {
      const matchStore = (store.name && store.name.toLowerCase().includes(searchLower)) ||
                        (store.storeCode && store.storeCode.toLowerCase().includes(searchLower)) ||
                        (store.address && store.address.toLowerCase().includes(searchLower));
      
      const matchManager = Object.values(store.departments || {}).some(managers =>
        Array.isArray(managers) && managers.some(manager => 
          manager && manager.managerName && manager.managerName.toLowerCase().includes(searchLower)
        )
      );
      
      return matchStore || matchManager;
    });
  }, [stores, searchTerm, forceUpdate]);

  // ✅ 수정된 출발지 선택 담당자 체크 함수
  const isSelectedInOrigin = (storeId, department, managerName) => {
    console.log('🔍 === isSelectedInOrigin 체크 ===');
    console.log('체크할 데이터:', { storeId, department, managerName });
    console.log('originStore 전체:', originStore);
    
    if (!originStore) {
      console.log('❌ originStore가 없음');
      return false;
    }

    // originStore 구조에 따른 다양한 접근 방법 시도
    let originStoreId, originDepartment, originManagerName;

    // 케이스 1: originStore.store.id 형태
    if (originStore.store && originStore.store.id) {
      originStoreId = originStore.store.id;
    }
    // 케이스 2: originStore.id 형태  
    else if (originStore.id) {
      originStoreId = originStore.id;
    }

    // 부서 정보 추출
    if (originStore.department) {
      originDepartment = originStore.department;
    }
    // 케이스: selectedDepartment 형태
    else if (originStore.selectedDepartment) {
      originDepartment = originStore.selectedDepartment;
    }

    // 담당자 정보 추출
    if (originStore.manager && originStore.manager.managerName) {
      originManagerName = originStore.manager.managerName;
    }
    // 케이스: selectedManager 형태
    else if (originStore.selectedManager && originStore.selectedManager.managerName) {
      originManagerName = originStore.selectedManager.managerName;
    }
    // 케이스: managerName 직접 접근
    else if (originStore.managerName) {
      originManagerName = originStore.managerName;
    }

    console.log('추출된 출발지 정보:', {
      originStoreId,
      originDepartment, 
      originManagerName
    });

    const isMatch = (
      originStoreId === storeId &&
      originDepartment === department &&
      originManagerName === managerName
    );

    console.log('매칭 결과:', isMatch);
    console.log('🔍 ========================');

    return isMatch;
  };

  // ✅ 해당 매장에서 선택 가능한 담당자가 있는지 체크하는 함수
  const hasAvailableManagers = (store) => {
    if (!store.departments) return false;
    
    const result = Object.entries(store.departments).some(([dept, managers]) => 
      managers.some(manager => {
        const isSelected = isSelectedInOrigin(store.id, dept, manager.managerName);
        console.log(`🔍 ${store.name} ${dept} ${manager.managerName} 선택됨: ${isSelected}`);
        return !isSelected;
      })
    );
    
    console.log(`🔍 ${store.name} 선택 가능한 담당자 있음: ${result}`);
    return result;
  };

  // 매장 선택 시 모달 열기
  const handleStoreSelect = (store) => {
    console.log('🔍 매장 선택:', store.name);
    
    // ✅ 선택 가능한 담당자가 없는 경우 경고
    if (!hasAvailableManagers(store)) {
      alert('이 매장의 모든 담당자가 출발지에서 이미 선택되었습니다.\n다른 매장을 선택해주세요.');
      return;
    }

    setSelectedStoreData(store);
    setShowModal(true);
    setModalStep(1);
    setSelectedDepartment(null);
    setSelectedManager(null);
    setModalSearchTerm('');
  };

  // 모달에서 부서 선택
  const handleModalDepartmentSelect = (department) => {
    console.log('🔍 부서 선택:', department);
    setSelectedDepartment(department);
    const managers = selectedStoreData.departments[department] || [];
    
    // ✅ 선택 가능한 담당자만 필터링
    const availableManagers = managers.filter(manager => {
      const isSelected = isSelectedInOrigin(selectedStoreData.id, department, manager.managerName);
      console.log(`🔍 ${manager.managerName} 필터링 - 선택됨: ${isSelected}`);
      return !isSelected;
    });
    
    console.log('🔍 선택 가능한 담당자들:', availableManagers);
    
    if (availableManagers.length === 0) {
      alert(`${department} 부서의 모든 담당자가 출발지에서 이미 선택되었습니다.\n다른 부서를 선택해주세요.`);
      return;
    }
    
    setDepartmentManagers(availableManagers);
    setModalStep(2);
    setModalSearchTerm('');
  };

  // 모달에서 담당자 선택
  const handleModalManagerSelect = (manager) => {
    console.log('🔍 담당자 선택 시도:', manager.managerName);
    
    // ✅ 이중 체크: 출발지에서 선택된 담당자인지 확인
    if (isSelectedInOrigin(selectedStoreData.id, selectedDepartment, manager.managerName)) {
      alert('이 담당자는 출발지에서 이미 선택되었습니다.\n다른 담당자를 선택해주세요.');
      return;
    }
    
    setSelectedManager(manager);
  };

  // 모달에서 선택 완료
  const handleModalConfirm = () => {
    if (selectedManager && selectedDepartment && selectedStoreData) {
      console.log('🔍 최종 선택 확인:', {
        store: selectedStoreData.name,
        department: selectedDepartment,
        manager: selectedManager.managerName
      });
      
      // ✅ 최종 검증: 출발지와 중복 체크
      if (isSelectedInOrigin(selectedStoreData.id, selectedDepartment, selectedManager.managerName)) {
        alert('선택하신 담당자는 출발지에서 이미 선택되었습니다.\n다른 담당자를 선택해주세요.');
        return;
      }

      const finalSelection = {
        store: selectedStoreData,
        department: selectedDepartment,
        manager: selectedManager,
        displayName: `${selectedStoreData.name} ${selectedDepartment} - ${selectedManager.managerName}`
      };
      
      setSelectedStore(finalSelection);
      dispatch({ type: 'SET_DESTINATION_STORE', payload: finalSelection });
      setShowModal(false);
    }
  };

  // 모달 닫기
  const handleModalClose = () => {
    setShowModal(false);
    setModalStep(1);
    setSelectedDepartment(null);
    setSelectedManager(null);
    setModalSearchTerm('');
  };

  // 모달 뒤로가기
  const handleModalBack = () => {
    if (modalStep === 2) {
      setModalStep(1);
      setSelectedManager(null);
      setModalSearchTerm('');
    } else {
      handleModalClose();
    }
  };

  const handleBack = () => {
    dispatch({ type: 'SET_STEP', payload: 2 });
  };

  const handleNext = () => {
    if (selectedStore && selectedStore.manager) {
      dispatch({ type: 'SET_DESTINATION_STORE', payload: selectedStore });
      dispatch({ type: 'SET_STEP', payload: 4 });
    }
  };

  const getPreviewContent = () => {
    if (selectedStore && selectedStore.displayName) {
      return `도착지: ${selectedStore.displayName}`;
    }
    return '';
  };

  const getDepartmentColor = (department) => {
    const colors = {
      '여성': 'bg-pink-100 text-pink-600 border-pink-200',
      '남성': 'bg-blue-100 text-blue-600 border-blue-200',
      '슈즈': 'bg-green-100 text-green-600 border-green-200',
      '일반': 'bg-gray-100 text-gray-600 border-gray-200'
    };
    return colors[department] || 'bg-gray-100 text-gray-600 border-gray-200';
  };

  // 모달 내 담당자 검색 필터링
  const filteredDepartmentManagers = departmentManagers.filter(manager => {
    if (!modalSearchTerm.trim()) return true;
    
    return (
      (manager && manager.managerName && manager.managerName.toLowerCase().includes(modalSearchTerm.toLowerCase())) ||
      (manager && manager.fullPhone && manager.fullPhone.includes(modalSearchTerm)) ||
      (manager && manager.phone && manager.phone.includes(modalSearchTerm))
    );
  });

  return (
    <>
      <div className="bg-gray-100 flex items-center justify-center min-h-screen font-sans">
        <div className="w-full max-w-sm mx-auto bg-white shadow-lg rounded-3xl overflow-hidden h-screen max-h-[812px] flex flex-col relative">
          
          <div className="relative z-30">
            <Header 
              title="도착지 선택"
              subtitle="도착지 매장을 선택해주세요"
              currentStep={3}
              onBack={handleBack}
            />
          </div>

          <main className="flex-1 bg-gray-50 p-6 space-y-4 overflow-y-auto pb-20">
            <SearchInput
              placeholder="매장명, 매장코드, 담당자명으로 검색"
              value={searchTerm}
              onChange={setSearchTerm}
            />

            {/* ✅ 디버깅 정보 표시 */}
            <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-3 mb-4 text-xs">
              <div className="font-bold mb-2">디버깅 정보:</div>
              {originStore ? (
                <div>
                  <div>출발지 매장: {originStore.store?.name || originStore.name || '없음'}</div>
                  <div>출발지 부서: {originStore.department || originStore.selectedDepartment || '없음'}</div>
                  <div>출발지 담당자: {originStore.manager?.managerName || originStore.selectedManager?.managerName || originStore.managerName || '없음'}</div>
                </div>
              ) : (
                <div>출발지가 선택되지 않음</div>
              )}
            </div>

            {/* 출발지 정보 표시 */}
            {originStore && (
              <div className="bg-blue-50 border border-blue-200 rounded-lg p-3 mb-4">
                <div className="flex items-center gap-2">
                  <i className="fas fa-info-circle text-blue-500 text-sm"></i>
                  <p className="text-sm text-blue-700">
                    출발지: {originStore.store?.name || originStore.name} {originStore.department || originStore.selectedDepartment} - {originStore.manager?.managerName || originStore.selectedManager?.managerName || originStore.managerName}
                  </p>
                </div>
                <p className="text-xs text-blue-600 mt-1">
                  선택된 담당자는 도착지에서 선택할 수 없습니다.
                </p>
              </div>
            )}

            {loading ? (
              <div className="text-center py-8">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-orange-500 mx-auto"></div>
                <p className="text-gray-500 mt-2">매장 정보를 불러오는 중...</p>
              </div>
            ) : error ? (
              <div className="text-center py-8">
                <p className="text-red-500 mb-2">{error}</p>
              </div>
            ) : (
              <div className="space-y-3">
                {filteredStores.length === 0 ? (
                  <div className="text-center py-8">
                    <p className="text-gray-500 mb-2">
                      {stores.length === 0 ? '매장 데이터가 없습니다.' : '검색 결과가 없습니다.'}
                    </p>
                  </div>
                ) : (
                  filteredStores.map((store, index) => {
                    // ✅ 출발지와 동일한 매장인지 체크
                    const isOriginStore = originStore && (originStore.store?.id === store.id || originStore.id === store.id);
                    // ✅ 선택 가능한 담당자가 있는지 체크
                    const hasAvailable = hasAvailableManagers(store);
                    
                    return (
                      <Card
                        key={`${store.id}-${index}`}
                        onClick={() => handleStoreSelect(store)}
                        className={`cursor-pointer transition-all ${
                          !hasAvailable 
                            ? 'opacity-50 cursor-not-allowed border-gray-200' 
                            : 'hover:border-orange-300 hover:shadow-lg'
                        } ${
                          isOriginStore ? 'border-yellow-300 bg-yellow-50' : ''
                        }`}
                      >
                        <div className="flex items-center justify-between">
                          <div className="flex-1">
                            <div className="flex items-center gap-2">
                              <h3 className="font-bold text-gray-800">{store.name}</h3>
                              {isOriginStore && (
                                <span className="text-xs bg-yellow-100 text-yellow-700 px-2 py-1 rounded-full">
                                  출발지 매장
                                </span>
                              )}
                              {!hasAvailable && (
                                <span className="text-xs bg-red-100 text-red-600 px-2 py-1 rounded-full">
                                  선택 불가
                                </span>
                              )}
                            </div>
                            <p className="text-sm text-gray-600">{store.storeCode}</p>
                            <p className="text-xs text-gray-500 mt-1">{store.address}</p>
                            
                            {/* 부서 표시 */}
                            <div className="flex gap-1 mt-2 flex-wrap">
                              {Object.entries(store.departments || {}).map(([dept, managers]) => {
                                // ✅ 해당 부서에서 선택 가능한 담당자 수 계산
                                const availableCount = managers.filter(manager => 
                                  !isSelectedInOrigin(store.id, dept, manager.managerName)
                                ).length;
                                
                                return (
                                  <span 
                                    key={dept}
                                    className={`px-2 py-1 text-xs rounded-full border ${
                                      availableCount === 0 
                                        ? 'bg-red-100 text-red-600 border-red-200' 
                                        : getDepartmentColor(dept)
                                    }`}
                                  >
                                    {dept} ({availableCount}/{managers.length}명)
                                  </span>
                                );
                              })}
                            </div>
                          </div>
                          <i className={`fa-solid ${hasAvailable ? 'fa-chevron-right' : 'fa-ban'} text-gray-400`}></i>
                        </div>
                      </Card>
                    );
                  })
                )}
              </div>
            )}
          </main>

          <FloatingPreview
            content={getPreviewContent()}
            onEdit={() => {
              setSelectedStore(null);
            }}
            show={!!selectedStore}
          />

          <footer className="p-6 border-t border-gray-200 bg-white">
            <Button 
              onClick={handleNext} 
              disabled={!selectedStore || loading}
            >
              다음 단계
            </Button>
          </footer>
        </div>
      </div>

      {/* 부서/담당자 선택 모달 - 기존과 동일하므로 생략 */}
      {showModal && selectedStoreData && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          {/* 모달 내용은 기존과 동일 */}
        </div>
      )}
    </>
  );
};

export default DestinationStoreSelector;
