import React, { createContext, useContext, useReducer, useMemo } from 'react';

const DeliveryContext = createContext(null);

const initialState = {
  isAuthenticated: false,
  user: null,
  deliveryType: '',
  originStore: null,
  destinationStore: null,
  deliveryInfo: null,
  currentStep: 1
};

const deliveryReducer = (state, action) => {
  switch (action.type) {
    case 'SET_DELIVERY_TYPE':
      return { ...state, deliveryType: action.payload, currentStep: 2 };

    case 'SET_ORIGIN_STORE':
      // üéØ Îß§Ïû• Ï†ïÎ≥¥ÏôÄ ÏÑ†ÌÉùÎêú Î∂ÄÏÑú Ï†ïÎ≥¥ Î™®Îëê Ï≤òÎ¶¨
      if (typeof action.payload === 'object' && action.payload.store) {
        // payloadÍ∞Ä { store, selectedDepartment, selectedDepartmentIndex } ÌòïÌÉúÏù∏ Í≤ΩÏö∞
        return { 
          ...state, 
          originStore: {
            ...action.payload.store,
            selectedDepartment: action.payload.selectedDepartment,
            selectedDepartmentIndex: action.payload.selectedDepartmentIndex
          }, 
          currentStep: 3 
        };
      } else {
        // payloadÍ∞Ä Îã®ÏàúÌûà store Í∞ùÏ≤¥Ïù∏ Í≤ΩÏö∞ (Í∏∞Ï°¥ Ìò∏ÌôòÏÑ±)
        return { ...state, originStore: action.payload, currentStep: 3 };
      }

    case 'SET_DESTINATION_STORE':
      // üéØ Îß§Ïû• Ï†ïÎ≥¥ÏôÄ ÏÑ†ÌÉùÎêú Î∂ÄÏÑú Ï†ïÎ≥¥ Î™®Îëê Ï≤òÎ¶¨
      if (typeof action.payload === 'object' && action.payload.store) {
        // payloadÍ∞Ä { store, selectedDepartment, selectedDepartmentIndex } ÌòïÌÉúÏù∏ Í≤ΩÏö∞
        return { 
          ...state, 
          destinationStore: {
            ...action.payload.store,
            selectedDepartment: action.payload.selectedDepartment,
            selectedDepartmentIndex: action.payload.selectedDepartmentIndex
          }, 
          currentStep: 4 
        };
      } else {
        // payloadÍ∞Ä Îã®ÏàúÌûà store Í∞ùÏ≤¥Ïù∏ Í≤ΩÏö∞ (Í∏∞Ï°¥ Ìò∏ÌôòÏÑ±)
        return { ...state, destinationStore: action.payload, currentStep: 4 };
      }

    case 'SET_DELIVERY_INFO':
      return { ...state, deliveryInfo: action.payload };

    case 'SET_STEP':
      return { ...state, currentStep: action.payload };

    case 'LOGIN_SUCCESS':
      return { ...state, isAuthenticated: true, user: action.payload };

    case 'LOGOUT':
      return { ...state, isAuthenticated: false, user: null };

    case 'RESET':
      return initialState;

    default:
      return state;
  }
};

export const DeliveryProvider = ({ children }) => {
  const [state, dispatch] = useReducer(deliveryReducer, initialState);

  const login = async (credentials) => {
    try {
      const response = await new Promise((resolve) => {
        setTimeout(() => {
          resolve({
            user: {
              id: 1,
              email: credentials.email,
              name: 'ÏÇ¨Ïö©Ïûê',
              role: 'business'
            },
            token: 'fake-jwt-token'
          });
        }, 1000);
      });

      if (credentials.rememberMe) {
        localStorage.setItem('token', response.token);
      } else {
        sessionStorage.setItem('token', response.token);
      }

      dispatch({ type: 'LOGIN_SUCCESS', payload: response.user });
      return response;
    } catch (error) {
      throw error;
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    sessionStorage.removeItem('token');
    dispatch({ type: 'LOGOUT' });
  };

  // üéØ Ìó¨Ìçº Ìï®ÏàòÎì§ Ï∂îÍ∞Ä
  const setOriginStore = (store, selectedDepartment = null, selectedDepartmentIndex = null) => {
    dispatch({
      type: 'SET_ORIGIN_STORE',
      payload: {
        store,
        selectedDepartment,
        selectedDepartmentIndex
      }
    });
  };

  const setDestinationStore = (store, selectedDepartment = null, selectedDepartmentIndex = null) => {
    dispatch({
      type: 'SET_DESTINATION_STORE',
      payload: {
        store,
        selectedDepartment,
        selectedDepartmentIndex
      }
    });
  };

  const contextValue = useMemo(() => ({
    ...state,
    dispatch,
    login,
    logout,
    setOriginStore,     // üëà Ìó¨Ìçº Ìï®Ïàò Ï∂îÍ∞Ä
    setDestinationStore // üëà Ìó¨Ìçº Ìï®Ïàò Ï∂îÍ∞Ä
  }), [state]);

  return (
    <DeliveryContext.Provider value={contextValue}>
      {children}
    </DeliveryContext.Provider>
  );
};

export const useDelivery = () => {
  const context = useContext(DeliveryContext);
  
  if (!context) {
    throw new Error(
      'üö® useDelivery must be used within DeliveryProvider!\n' +
      'üí° Wrap your component with <DeliveryProvider>...</DeliveryProvider>'
    );
  }
  
  return context;
};
