import React, { useState, useEffect } from 'react';
import { useDelivery } from '../../context/DeliveryContext';
import { useAuth } from '../../context/AuthContext';
import SearchInput from '../common/SearchInput';
import Card from '../ui/Card';

const OriginStoreSelector = ({ isOpen, onClose }) => {
  const { deliveryType, originStore, dispatch, setOriginStore } = useDelivery();
  const { user, token } = useAuth();
  
  const [selectedStore, setSelectedStore] = useState(originStore);
  const [searchTerm, setSearchTerm] = useState('');
  const [stores, setStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // 3ë‹¨ê³„ ì„ íƒ ìƒíƒœ
  const [step, setStep] = useState(1); // 1: ë§¤ì¥ì„ íƒ, 2: ë¶€ì„œì„ íƒ, 3: ë‹´ë‹¹ìì„ íƒ
  const [selectedStoreData, setSelectedStoreData] = useState(null);
  const [selectedDepartment, setSelectedDepartment] = useState(null);
  const [selectedManager, setSelectedManager] = useState(null);
  const [departmentManagers, setDepartmentManagers] = useState([]);

  // ëª¨ë‹¬ì´ ì—´ë¦´ ë•Œë§ˆë‹¤ ì´ˆê¸°í™”
  useEffect(() => {
    if (isOpen) {
      setStep(1);
      setSelectedStoreData(null);
      setSelectedDepartment(null);
      setSelectedManager(null);
      setSelectedStore(null);
      setSearchTerm('');
    }
  }, [isOpen]);

  // ë§¤ì¥ ë°ì´í„° ê°€ì ¸ì˜¤ê¸°
  useEffect(() => {
    const fetchStores = async () => {
      if (!isOpen || !token) return;
      
      try {
        setLoading(true);
        setError('');
        
        const response = await fetch('https://quinors-lv-backend.ngrok.io/api/auth/stores?active=true&limit=100', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
        });

        const result = await response.json();

        if (response.ok && result.success) {
          // ë§¤ì¥ë³„ë¡œ ê·¸ë£¹í™”ëœ ë°ì´í„° ì²˜ë¦¬
          const transformedStores = result.data.map(store => ({
            id: store._id,
            storeCode: store.storeCode,
            name: store.storeName,
            address: store.address || 'ì£¼ì†Œ ì •ë³´ ì—†ìŒ',
            isActive: store.isActive,
            createdAt: store.createdAt,
            // ë¶€ì„œë³„ ë‹´ë‹¹ì ì •ë³´ë¥¼ ê°ì²´ í˜•íƒœë¡œ ë³€í™˜
            departments: store.departments ? store.departments.reduce((acc, dept) => {
              if (!acc[dept.department]) {
                acc[dept.department] = [];
              }
              acc[dept.department].push({
                id: `${store._id}_${dept.department}_${dept.managerName}`,
                managerName: dept.managerName,
                phone: dept.managerPhone,
                fullPhone: dept.fullPhone || (dept.managerPhone ? dept.managerPhone.replace(/(\d{3})(\d{4})(\d{4})/, '$1-$2-$3') : '')
              });
              return acc;
            }, {}) : {}
          }));

          setStores(transformedStores);
          console.log('âœ… ì¶œë°œì§€ ë§¤ì¥ ë°ì´í„° ë¡œë“œ ì™„ë£Œ:', transformedStores);
        } else {
          throw new Error(result.message || 'ë§¤ì¥ ë°ì´í„°ë¥¼ ë¶ˆëŸ¬ì˜¤ëŠ”ë° ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.');
        }
      } catch (error) {
        console.error('âŒ ì¶œë°œì§€ ë§¤ì¥ ë°ì´í„° ë¡œë“œ ì‹¤íŒ¨:', error);
        setError(error.message);
        
        // ì—ëŸ¬ ë°œìƒ ì‹œ ìƒ˜í”Œ ë°ì´í„° ì‚¬ìš©
        const sampleStores = [
          {
            id: 'sample-1',
            storeCode: 'QN01',
            name: 'ë£¨ì´ë¹„í†µ í€´ë…¸ìŠ¤ì  ì—¬ì„±',
            address: 'ì„œìš¸ ê°•ë‚¨êµ¬ ì••êµ¬ì •ë™ 454',
            isActive: true,
            departments: {
              'ì—¬ì„±': [
                { id: 'qn01-women-1', managerName: 'ì•ˆíƒœìš±', phone: '01012345678', fullPhone: '010-1234-5678' },
                { id: 'qn01-women-2', managerName: 'í™ê¸¸ë™', phone: '01087654321', fullPhone: '010-8765-4321' }
              ]
            }
          },
          {
            id: 'sample-2',
            storeCode: 'ST002',
            name: 'ë¡¯ë°ì›”ë“œíƒ€ì›Œì ',
            address: 'ì„œìš¸ ì†¡íŒŒêµ¬ ì˜¬ë¦¼í”½ë¡œ 300',
            isActive: true,
            departments: {
              'ì—¬ì„±': [
                { id: 'st002-women-1', managerName: 'ë°•ì—¬ì„±', phone: '01033333333', fullPhone: '010-3333-3333' }
              ],
              'ë‚¨ì„±': [
                { id: 'st002-men-1', managerName: 'ì´ë‚¨ì„±', phone: '01044444444', fullPhone: '010-4444-4444' }
              ],
              'ìŠˆì¦ˆ': [
                { id: 'st002-shoes-1', managerName: 'ìµœìŠˆì¦ˆ', phone: '01055555555', fullPhone: '010-5555-5555' }
              ]
            }
          }
        ];
        
        setStores(sampleStores);
      } finally {
        setLoading(false);
      }
    };

    fetchStores();
  }, [isOpen, token]);

  // ê²€ìƒ‰ í•„í„°ë§
  const filteredStores = stores.filter(store =>
    store.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    store.storeCode.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // 1ë‹¨ê³„: ë§¤ì¥ ì„ íƒ
  const handleStoreSelect = (store) => {
    console.log('ë§¤ì¥ ì„ íƒ:', store);
    setSelectedStoreData(store);
    setStep(2);
  };

  // 2ë‹¨ê³„: ë¶€ì„œ ì„ íƒ
  const handleDepartmentSelect = (department) => {
    console.log('ë¶€ì„œ ì„ íƒ:', department);
    setSelectedDepartment(department);
    setDepartmentManagers(selectedStoreData.departments[department] || []);
    setStep(3);
  };

  // 3ë‹¨ê³„: ë‹´ë‹¹ì ì„ íƒ
  const handleManagerSelect = (manager) => {
    console.log('ë‹´ë‹¹ì ì„ íƒ:', manager);
    setSelectedManager(manager);
    
    // ìµœì¢… ì„ íƒ ë°ì´í„° êµ¬ì„±
    const finalSelection = {
      store: selectedStoreData,
      department: selectedDepartment,
      manager: manager,
      displayName: `${selectedStoreData.name} ${selectedDepartment} - ${manager.managerName}`
    };
    
    setSelectedStore(finalSelection);
  };

  // ë’¤ë¡œê°€ê¸°
  const handleBack = () => {
    if (step === 3) {
      setStep(2);
      setSelectedManager(null);
      setSelectedStore(null);
    } else if (step === 2) {
      setStep(1);
      setSelectedDepartment(null);
      setDepartmentManagers([]);
      setSelectedStoreData(null);
    } else {
      onClose();
    }
  };

  // ì„ íƒ ì™„ë£Œ
  const handleConfirm = () => {
    if (selectedStore && selectedManager) {
      dispatch({ type: 'SET_ORIGIN_STORE', payload: selectedStore });
      onClose();
    }
  };

  // ë¶€ì„œë³„ ìƒ‰ìƒ
  const getDepartmentColor = (department) => {
    const colors = {
      'ì—¬ì„±': 'bg-pink-100 text-pink-600 border-pink-200',
      'ë‚¨ì„±': 'bg-blue-100 text-blue-600 border-blue-200',
      'ìŠˆì¦ˆ': 'bg-green-100 text-green-600 border-green-200'
    };
    return colors[department] || 'bg-gray-100 text-gray-600 border-gray-200';
  };

  // ë¶€ì„œ ì•„ì´ì½˜
  const getDepartmentIcon = (department) => {
    const icons = {
      'ì—¬ì„±': 'ğŸ‘©',
      'ë‚¨ì„±': 'ğŸ‘¨',
      'ìŠˆì¦ˆ': 'ğŸ‘Ÿ'
    };
    return icons[department] || 'ğŸª';
  };

  // ëª¨ë‹¬ì´ ì—´ë ¤ìˆì§€ ì•Šìœ¼ë©´ ë Œë”ë§í•˜ì§€ ì•ŠìŒ
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-2xl w-full max-w-md h-[600px] flex flex-col overflow-hidden shadow-2xl">
        
        {/* ëª¨ë‹¬ í—¤ë” */}
        <div className="bg-gradient-to-r from-orange-500 to-orange-600 text-white p-4">
          <div className="flex items-center justify-between mb-3">
            <button 
              onClick={handleBack}
              className="w-8 h-8 bg-white/20 rounded-full flex items-center justify-center hover:bg-white/30 transition-colors"
            >
              {step === 1 ? (
                <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
                  <path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd"/>
                </svg>
              ) : (
                <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
                  <path fillRule="evenodd" d="M12.707 5.293a1 1 0 010 1.414L9.414 10l3.293 3.293a1 1 0 01-1.414 1.414l-4-4a1 1 0 010-1.414l4-4a1 1 0 011.414 0z" clipRule="evenodd"/>
                </svg>
              )}
            </button>
            
            <h2 className="text-lg font-bold">ì¶œë°œì§€ ì„ íƒ</h2>
            
            <button 
              onClick={onClose}
              className="w-8 h-8 bg-white/20 rounded-full flex items-center justify-center hover:bg-white/30 transition-colors"
            >
              <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd"/>
              </svg>
            </button>
          </div>
          
          <p className="text-white/90 text-sm">
            {step === 1 ? "ì¶œë°œì§€ ë§¤ì¥ì„ ì„ íƒí•´ì£¼ì„¸ìš”" :
             step === 2 ? "ë¶€ì„œë¥¼ ì„ íƒí•´ì£¼ì„¸ìš”" :
             "ë‹´ë‹¹ìë¥¼ ì„ íƒí•´ì£¼ì„¸ìš”"}
          </p>

          {/* ì§„í–‰ ìƒíƒœ í‘œì‹œ */}
          <div className="mt-4">
            <div className="flex items-center justify-between text-xs mb-2">
              <span className={step >= 1 ? 'text-white font-medium' : 'text-white/60'}>
                1. ë§¤ì¥
              </span>
              <span className={step >= 2 ? 'text-white font-medium' : 'text-white/60'}>
                2. ë¶€ì„œ
              </span>
              <span className={step >= 3 ? 'text-white font-medium' : 'text-white/60'}>
                3. ë‹´ë‹¹ì
              </span>
            </div>
            <div className="w-full bg-white/20 rounded-full h-1">
              <div 
                className="bg-white h-1 rounded-full transition-all duration-300"
                style={{ width: `${(step / 3) * 100}%` }}
              ></div>
            </div>
          </div>
        </div>

        {/* ëª¨ë‹¬ ë‚´ìš© */}
        <div className="flex-1 p-4 overflow-y-auto">
          {/* 1ë‹¨ê³„: ë§¤ì¥ ì„ íƒ */}
          {step === 1 && (
            <>
              <SearchInput
                placeholder="ë§¤ì¥ëª… ë˜ëŠ” ë§¤ì¥ì½”ë“œë¡œ ê²€ìƒ‰"
                value={searchTerm}
                onChange={setSearchTerm}
              />

              {loading ? (
                <div className="text-center py-8">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-orange-500 mx-auto"></div>
                  <p className="text-gray-500 mt-2">ë§¤ì¥ ì •ë³´ë¥¼ ë¶ˆëŸ¬ì˜¤ëŠ” ì¤‘...</p>
                </div>
              ) : error ? (
                <div className="text-center py-8">
                  <p className="text-red-500 mb-2">{error}</p>
                  <p className="text-gray-500 text-sm">ìƒ˜í”Œ ë°ì´í„°ë¡œ ì§„í–‰í•©ë‹ˆë‹¤.</p>
                </div>
              ) : (
                <div className="space-y-3 mt-4">
                  {filteredStores.length === 0 ? (
                    <div className="text-center py-8">
                      <p className="text-gray-500">ê²€ìƒ‰ ê²°ê³¼ê°€ ì—†ìŠµë‹ˆë‹¤.</p>
                    </div>
                  ) : (
                    filteredStores.map((store) => (
                      <Card
                        key={store.id}
                        onClick={() => handleStoreSelect(store)}
                        className="cursor-pointer hover:border-orange-300 hover:shadow-lg transition-all"
                      >
                        <div className="flex items-center justify-between">
                          <div className="flex-1">
                            <h3 className="font-bold text-gray-800 text-sm">{store.name}</h3>
                            <p className="text-xs text-gray-600">{store.storeCode}</p>
                            <p className="text-xs text-gray-500 mt-1">{store.address}</p>
                            
                            {/* ë¶€ì„œ í‘œì‹œ */}
                            <div className="flex gap-1 mt-2">
                              {Object.keys(store.departments).map(dept => (
                                <span 
                                  key={dept}
                                  className={`px-2 py-1 text-xs rounded-full border ${getDepartmentColor(dept)}`}
                                >
                                  {getDepartmentIcon(dept)} {dept} ({store.departments[dept].length})
                                </span>
                              ))}
                            </div>
                          </div>
                          <i className="fa-solid fa-chevron-right text-gray-400"></i>
                        </div>
                      </Card>
                    ))
                  )}
                </div>
              )}
            </>
          )}

          {/* 2ë‹¨ê³„: ë¶€ì„œ ì„ íƒ */}
          {step === 2 && selectedStoreData && (
            <>
              <div className="bg-gray-50 rounded-lg p-3 border mb-4">
                <h3 className="font-bold text-gray-800 text-sm">{selectedStoreData.name}</h3>
                <p className="text-xs text-gray-600">{selectedStoreData.storeCode}</p>
              </div>

              <div className="space-y-3">
                {Object.entries(selectedStoreData.departments).map(([dept, managers]) => (
                  <Card
                    key={dept}
                    onClick={() => handleDepartmentSelect(dept)}
                    className="cursor-pointer hover:border-orange-300 hover:shadow-lg transition-all"
                  >
                    <div className="flex items-center justify-between">
                      <div className="flex items-center gap-3">
                        <div className={`w-10 h-10 rounded-lg flex items-center justify-center border ${getDepartmentColor(dept)}`}>
                          <span className="text-sm">
                            {getDepartmentIcon(dept)}
                          </span>
                        </div>
                        <div>
                          <h3 className="font-bold text-gray-800 text-sm">{dept} ë§¤ì¥</h3>
                          <p className="text-xs text-gray-600">ë‹´ë‹¹ì {managers.length}ëª…</p>
                        </div>
                      </div>
                      <i className="fa-solid fa-chevron-right text-gray-400"></i>
                    </div>
                  </Card>
                ))}
              </div>
            </>
          )}

          {/* 3ë‹¨ê³„: ë‹´ë‹¹ì ì„ íƒ */}
          {step === 3 && selectedDepartment && (
            <>
              <div className="bg-gray-50 rounded-lg p-3 border mb-4">
                <h3 className="font-bold text-gray-800 text-sm">{selectedStoreData.name}</h3>
                <span className={`inline-block px-2 py-1 text-xs rounded-full border mt-1 ${getDepartmentColor(selectedDepartment)}`}>
                  {getDepartmentIcon(selectedDepartment)} {selectedDepartment} ë§¤ì¥
                </span>
              </div>

              <div className="space-y-3">
                {departmentManagers.map((manager, index) => (
                  <Card
                    key={manager.id || index}
                    onClick={() => handleManagerSelect(manager)}
                    className={`cursor-pointer transition-all ${
                      selectedManager?.id === manager.id
                        ? 'border-orange-500 bg-orange-50'
                        : 'hover:border-orange-300 hover:shadow-lg'
                    }`}
                  >
                    <div className="flex items-center justify-between">
                      <div className="flex items-center gap-3">
                        <div className="w-10 h-10 bg-gradient-to-br from-orange-400 to-orange-500 rounded-lg flex items-center justify-center text-white font-bold text-sm">
