import React from 'react';

const Header = ({ title, subtitle, currentStep, onBack, onClose }) => {
  const steps = [
    { number: 1, label: '배송유형' },
    { number: 2, label: currentStep === 2 ? '출발지' : '카테고리' },
    { number: 3, label: currentStep === 3 ? '도착지' : '지역' }
  ];

  return (
    <header className="bg-gradient-to-br from-orange-400 via-orange-500 to-orange-500 text-white p-6 pt-12 rounded-b-2xl shadow-md relative">
      <div className="flex items-center justify-between mb-4">
        <button 
          onClick={onBack}
          className="text-white opacity-90 hover:opacity-100 transition-opacity"
        >
          <i className="fa-solid fa-arrow-left fa-lg"></i>
        </button>
        <h1 className="text-2xl font-bold">{title}</h1>
        <button 
          onClick={onClose}
          className="text-white opacity-90 hover:opacity-100 transition-opacity"
        >
          <i className="fa-solid fa-xmark fa-lg"></i>
        </button>
      </div>
      <p className="text-center text-sm font-light opacity-80 mb-6">
        {subtitle}
      </p>
      
      <nav className="flex items-center justify-center space-x-2 text-xs">
        {steps.map((step, index) => (
          <React.Fragment key={step.number}>
            <div className="flex items-center">
              <span className={`font-bold rounded-full py-1 px-4 ${
                currentStep === step.number 
                  ? 'bg-white text-orange-500' 
                  : 'text-white/60 font-medium'
              }`}>
                {step.number}. {step.label}
              </span>
            </div>
            {index < steps.length - 1 && (
              <i className="fa-solid fa-arrow-right text-white/50"></i>
            )}
          </React.Fragment>
        ))}
      </nav>
    </header>
  );
};

export default Header;
