import React, { useState, useEffect } from 'react';
import { useDelivery } from '../../context/DeliveryContext';
import { useAuth } from '../../context/AuthContext';
import Header from '../common/Header';
import FloatingPreview from '../common/FloatingPreview';
import Button from '../ui/Button';
import SearchInput from '../common/SearchInput';
import Card from '../ui/Card';

const OriginStoreSelector = () => {
  const { deliveryType, originStore, dispatch, setOriginStore } = useDelivery();
  const { user, token } = useAuth();
  
  const [selectedStore, setSelectedStore] = useState(originStore);
  const [searchTerm, setSearchTerm] = useState('');
  const [stores, setStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // ê¸°ì¡´ 3ë‹¨ê³„ ì„ íƒ ìƒíƒœ (ëª¨ë‹¬ì—ì„œ ì‚¬ìš©)
  const [step, setStep] = useState(1);
  const [selectedStoreData, setSelectedStoreData] = useState(null);
  const [selectedDepartment, setSelectedDepartment] = useState(null);
  const [selectedManager, setSelectedManager] = useState(null);
  const [departmentManagers, setDepartmentManagers] = useState([]);

  // ëª¨ë‹¬ ìƒíƒœ
  const [showModal, setShowModal] = useState(false);
  const [modalStep, setModalStep] = useState(1);

  // ëª¨ë‹¬ ë‚´ ê²€ìƒ‰ ìƒíƒœ
  const [modalSearchTerm, setModalSearchTerm] = useState('');

  // í† í°ê³¼ ì‚¬ìš©ì ì •ë³´ í™•ì¸
  useEffect(() => {
    console.log('ğŸ” í˜„ì¬ í† í°:', token ? 'ìˆìŒ' : 'ì—†ìŒ');
    console.log('ğŸ” í˜„ì¬ ì‚¬ìš©ì:', user);
  }, [token, user]);

  // ë§¤ì¥ ë°ì´í„° ê°€ì ¸ì˜¤ê¸°
  useEffect(() => {
    const fetchStores = async () => {
      try {
        setLoading(true);
        setError('');
        
        console.log('ğŸ” API í˜¸ì¶œ ì‹œì‘');
        const response = await fetch('https://quinors-lv-backend.ngrok.io/api/auth/stores?active=true&limit=100', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
        });

        console.log('ğŸ” API ì‘ë‹µ ìƒíƒœ:', response.status);
        const result = await response.json();
        console.log('ğŸ” API ì‘ë‹µ ë°ì´í„°:', result);

        if (response.ok && result.success && result.data && result.data.length > 0) {
          console.log('ğŸ” ì›ë³¸ ë°ì´í„° ê°œìˆ˜:', result.data.length);
          console.log('ğŸ” ì›ë³¸ ë°ì´í„° ì „ì²´:', result.data);
          
          // âœ… ì‹¤ì œ ë°ì´í„°ë² ì´ìŠ¤ í•„ë“œë¥¼ ì‚¬ìš©í•œ ì²˜ë¦¬
          const storeGroups = {};
          
          result.data.forEach((store, index) => {
            console.log(`ğŸ” ë§¤ì¥ ${index} ì›ë³¸ ë°ì´í„°:`, {
              storeName: store.storeName,
              department: store.department,
              managerName: store.managerName,
              managerPhone: store.managerPhone,
              storeCode: store.storeCode,
              address: store.address
            });
            
            // ë§¤ì¥ëª…ì´ ì—†ìœ¼ë©´ ê±´ë„ˆë›°ê¸°
            if (!store.storeName) {
              console.log('âŒ ë§¤ì¥ëª…ì´ ì—†ì–´ì„œ ê±´ë„ˆëœ€');
              return;
            }

            const storeName = store.storeName;
            
            // ë§¤ì¥ ê·¸ë£¹ì´ ì—†ìœ¼ë©´ ìƒì„±
            if (!storeGroups[storeName]) {
              storeGroups[storeName] = {
                id: store._id,
                storeCode: store.storeCode || '',
                name: store.storeName,
                address: store.address || 'ì£¼ì†Œ ì •ë³´ ì—†ìŒ',
                isActive: store.isActive !== false,
                createdAt: store.createdAt,
                departments: {}
              };
            }
            
            // âœ… ì‹¤ì œ ë°ì´í„°ë² ì´ìŠ¤ í•„ë“œ ì‚¬ìš©
            const department = store.department || 'ì¼ë°˜';
            const managerName = store.managerName || 'ë‹´ë‹¹ì';
            const managerPhone = store.managerPhone || '';
            
            console.log(`ğŸ” ë§¤ì¥ ${storeName} - ë¶€ì„œ: ${department}, ë‹´ë‹¹ì: ${managerName}`);
            
            // ë¶€ì„œë³„ ë‹´ë‹¹ì ë°°ì—´ ì´ˆê¸°í™”
            if (!storeGroups[storeName].departments[department]) {
              storeGroups[storeName].departments[department] = [];
            }
            
            // ë‹´ë‹¹ì ì¶”ê°€
            storeGroups[storeName].departments[department].push({
              id: `${store._id}_${department}_${managerName}`,
              managerName: managerName,
              phone: managerPhone,
              fullPhone: managerPhone ? managerPhone.replace(/(\d{3})(\d{4})(\d{4})/, '$1-$2-$3') : ''
            });
          });

          console.log('ğŸ” ê·¸ë£¹í™”ëœ ë§¤ì¥ ë°ì´í„°:', storeGroups);

          // ë§¤ì¥ ë°°ì—´ë¡œ ë³€í™˜ ë° ë¹ˆ ë¶€ì„œ ì œê±°
          const transformedStores = Object.values(storeGroups).map(store => {
            // ë¹ˆ ë¶€ì„œ ì œê±°
            const validDepartments = {};
            Object.entries(store.departments).forEach(([dept, managers]) => {
              if (managers && managers.length > 0) {
                validDepartments[dept] = managers;
              }
            });
            
            return {
              ...store,
              departments: validDepartments
            };
          }).filter(store => Object.keys(store.departments).length > 0);

          console.log('ğŸ” ìµœì¢… ë³€í™˜ëœ ë§¤ì¥ ë°ì´í„°:', transformedStores);
          console.log('ğŸ” ìµœì¢… ë§¤ì¥ ê°œìˆ˜:', transformedStores.length);
          
          setStores(transformedStores);
          
          if (transformedStores.length === 0) {
            throw new Error('ìœ íš¨í•œ ë§¤ì¥ ë°ì´í„°ê°€ ì—†ìŠµë‹ˆë‹¤.');
          }
          
          console.log('âœ… ë§¤ì¥ ë°ì´í„° ì„¤ì • ì™„ë£Œ');
        } else {
          throw new Error('ë§¤ì¥ ë°ì´í„°ê°€ ì—†ê±°ë‚˜ í˜•ì‹ì´ ì˜¬ë°”ë¥´ì§€ ì•ŠìŠµë‹ˆë‹¤.');
        }
      } catch (error) {
        console.error('âŒ ë§¤ì¥ ë°ì´í„° ë¡œë“œ ì‹¤íŒ¨:', error);
        setError(error.message);
        
        // âœ… ì‹¤ì œ ë°ì´í„° êµ¬ì¡°ì™€ ì¼ì¹˜í•˜ëŠ” ìƒ˜í”Œ ë°ì´í„°
        console.log('ğŸ” ìƒ˜í”Œ ë°ì´í„°ë¡œ ëŒ€ì²´');
        const sampleStores = [
          {
            id: 'sample-1',
            storeCode: 'QN01',
            name: 'ë£¨ì´ë¹„í†µ í€´ë…¸ìŠ¤ì ',
            address: 'ì„œìš¸ ì¤‘ë‘êµ¬ ì‚¬ê°€ì •ë¡œ54ê¸¸ 72 (ë©´ëª©ë™) 1ì¸µ',
            isActive: true,
            departments: {
              'ì—¬ì„±': [
                { 
                  id: 'sample-1-women-1', 
                  managerName: 'ì•ˆíƒœìš±', 
                  phone: '01044315524', 
                  fullPhone: '010-4431-5524' 
                }
              ]
            }
          },
          {
            id: 'sample-2',
            storeCode: 'ADMIN001',
            name: 'ê´€ë¦¬ì ê³„ì • ì‹œìŠ¤í…œ',
            address: 'ì„œìš¸ì‹œ ê°•ë‚¨êµ¬ í…Œí—¤ë€ë¡œ 123 ê´€ë¦¬ì‚¬ë¬´ì†Œ',
            isActive: true,
            departments: {
              'ì—¬ì„±': [
                { 
                  id: 'sample-2-women-1', 
                  managerName: 'ì‹œìŠ¤í…œê´€ë¦¬ì', 
                  phone: '01012345678', 
                  fullPhone: '010-1234-5678' 
                }
              ]
            }
          }
        ];
        
        setStores(sampleStores);
        console.log('âœ… ìƒ˜í”Œ ë°ì´í„° ì„¤ì •:', sampleStores);
      } finally {
        setLoading(false);
        console.log('ğŸ” ë¡œë”© ì™„ë£Œ');
      }
    };

