import React, { useState, useEffect } from 'react';
import addressService from '../../services/addressService';

const AddressConverter = ({ 
  originalAddress, 
  onAddressConverted, 
  showOriginal = true,
  className = "" 
}) => {
  const [convertedData, setConvertedData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    if (originalAddress && originalAddress.trim()) {
      convertAddress(originalAddress);
    } else {
      setConvertedData(null);
      setError('');
    }
  }, [originalAddress]);

  const convertAddress = async (address) => {
    setLoading(true);
    setError('');

    try {
      const result = await addressService.convertToAdminDong(address);
      
      if (result.success) {
        setConvertedData(result);
        onAddressConverted && onAddressConverted(result);
      } else {
        setError(result.error || '주소 변환에 실패했습니다');
        setConvertedData(null);
      }
    } catch (error) {
      setError('주소 변환 중 오류가 발생했습니다');
      setConvertedData(null);
    } finally {
      setLoading(false);
    }
  };

  if (!originalAddress) return null;

  return (
    <div className={`space-y-2 ${className}`}>
      {/* 원본 주소 */}
      {showOriginal && (
        <div className="text-xs text-gray-600">
          <span className="font-medium">도로명:</span> {originalAddress}
        </div>
      )}

      {/* 로딩 상태 */}
      {loading && (
        <div className="flex items-center gap-2 text-xs text-blue-600">
          <div className="animate-spin w-3 h-3 border border-blue-500 border-t-transparent rounded-full"></div>
          <span>카카오 API로 주소 변환 중...</span>
        </div>
      )}

      {/* 에러 상태 */}
      {error && (
        <div className="text-xs text-red-600 bg-red-50 px-2 py-1 rounded border border-red-200">
          <i className="fa-solid fa-exclamation-triangle mr-1"></i>
          {error}
        </div>
      )}

      {/* 변환 성공 */}
      {convertedData && convertedData.success && (
        <div className="text-xs bg-green-50 px-2 py-1 rounded border border-green-200">
          <div className="flex items-center gap-1 mb-1">
            <i className="fa-solid fa-map-marker-alt text-green-600"></i>
            <span className="font-medium text-green-700">행정동 주소:</span>
          </div>
          <div className="space-y-0.5 ml-4">
            <div className="text-green-800 font-medium">
              {convertedData.dongAddress}
            </div>
            {convertedData.details && (
              <div className="text-green-600 space-y-0.5">
                <div>시도: {convertedData.details.sido}</div>
                <div>시군구: {convertedData.details.sigungu}</div>
                <div>행정동: {convertedData.details.adminDong}</div>
                {convertedData.details.legalDong && convertedData.details.legalDong !== convertedData.details.adminDong && (
                  <div>법정동: {convertedData.details.legalDong}</div>
                )}
              </div>
            )}
          </div>
          
          {/* 카카오 데이터 정보 */}
          {convertedData.kakaoData && (
            <div className="mt-2 pt-2 border-t border-green-200">
              <div className="text-xs text-green-600">
                <div>지번: {convertedData.kakaoData.jibunAddress}</div>
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default AddressConverter;
