import React, { useState } from 'react';
import DeliveryTypeSelector from '../components/forms/DeliveryTypeSelector';
import OriginStoreSelector from '../components/forms/OriginStoreSelector';
import DestinationStoreSelector from '../components/forms/DestinationStoreSelector';
import DeliveryInfoForm from '../components/forms/DeliveryInfoForm';

const DeliveryFlow = () => {
  // Context 대신 로컬 state 사용
  const [currentStep, setCurrentStep] = useState(1);
  const [deliveryData, setDeliveryData] = useState({
    deliveryType: '',
    originStore: null,
    destinationStore: null,
    deliveryInfo: null
  });

  const handleNext = (data) => {
    setDeliveryData(prev => ({ ...prev, ...data }));
    setCurrentStep(prev => prev + 1);
  };

  const handleBack = () => {
    setCurrentStep(prev => prev - 1);
  };

  const handleStepChange = (step) => {
    setCurrentStep(step);
  };

  const renderCurrentStep = () => {
    switch (currentStep) {
      case 1:
        return (
          <DeliveryTypeSelector 
            onNext={(type) => handleNext({ deliveryType: type })}
            onBack={handleBack}
            currentStep={currentStep}
            selectedType={deliveryData.deliveryType}
          />
        );
      case 2:
        return (
          <OriginStoreSelector 
            onNext={(store) => handleNext({ originStore: store })}
            onBack={handleBack}
            currentStep={currentStep}
            selectedStore={deliveryData.originStore}
            deliveryType={deliveryData.deliveryType}
          />
        );
      case 3:
        return (
          <DestinationStoreSelector 
            onNext={(store) => handleNext({ destinationStore: store })}
            onBack={handleBack}
            currentStep={currentStep}
            selectedStore={deliveryData.destinationStore}
            deliveryType={deliveryData.deliveryType}
            originStore={deliveryData.originStore}
          />
        );
      case 4:
        return (
          <DeliveryInfoForm 
            onSubmit={(info) => {
              console.log('배송 요청:', {
                ...deliveryData,
                deliveryInfo: info
              });
              alert('배송 요청이 완료되었습니다!');
            }}
            onBack={handleBack}
            currentStep={currentStep}
            deliveryType={deliveryData.deliveryType}
            originStore={deliveryData.originStore}
            destinationStore={deliveryData.destinationStore}
          />
        );
      default:
        return (
          <DeliveryTypeSelector 
            onNext={(type) => handleNext({ deliveryType: type })}
            onBack={handleBack}
            currentStep={1}
            selectedType=""
          />
        );
    }
  };

  return (
    <div>
      {renderCurrentStep()}
    </div>
  );
};

export default DeliveryFlow;
