import React, { useState } from 'react';
import { useDelivery } from '../../context/DeliveryContext';

const FloatingPreview = ({ content, onEdit, show = true }) => {
  const [isExpanded, setIsExpanded] = useState(false);
  const { deliveryType, originStore, destinationStore, currentStep } = useDelivery();

  if (!show) return null;

  const toggleExpanded = () => {
    setIsExpanded(!isExpanded);
  };

  // 단계별 정보 구성
  const getStepInfo = () => {
    const steps = [];
    
    // 1단계: 배송 유형
    if (deliveryType) {
      steps.push({
        step: 1,
        title: '배송 유형',
        content: deliveryType,
        icon: 'fa-truck',
        completed: true
      });
    }
    
    // 2단계: 출발지
    if (originStore) {
      steps.push({
        step: 2,
        title: '출발지',
        content: originStore.name,
        detail: originStore.address,
        icon: 'fa-store',
        completed: true
      });
    }
    
    // 3단계: 도착지
    if (destinationStore) {
      steps.push({
        step: 3,
        title: '도착지',
        content: destinationStore.name,
        detail: destinationStore.address,
        icon: 'fa-map-marker-alt',
        completed: true
      });
    }

    // 현재 단계 표시 (미완료 단계)
    if (currentStep > steps.length) {
      const stepTitles = ['', '배송 유형', '출발지', '도착지', '배송 정보'];
      steps.push({
        step: currentStep,
        title: stepTitles[currentStep],
        content: '선택 중...',
        icon: 'fa-clock',
        completed: false,
        current: true
      });
    }

    return steps;
  };

  const stepInfo = getStepInfo();

  return (
    <>
      {/* 기본 프리뷰 (항상 표시) */}
      <div className="fixed bottom-20 left-4 right-4 max-w-sm mx-auto">
        <div className="bg-white/95 backdrop-blur-sm p-3 rounded-xl border border-orange-500/20 shadow-lg">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-2 flex-1">
              <div className="w-3 h-3 rounded-full bg-gradient-to-br from-orange-400 to-orange-500 flex items-center justify-center">
                <i className="fa-solid fa-check text-white text-[6px]"></i>
              </div>
              <span className="text-xs text-gray-700 font-medium truncate">
                {content}
              </span>
            </div>
            
            <div className="flex items-center gap-2">
              {/* 확장/축소 버튼 */}
              <button 
                onClick={toggleExpanded}
                className="text-gray-400 hover:text-orange-500 transition-colors p-1"
                title={isExpanded ? '축소' : '단계별 정보 보기'}
              >
                <i className={`fa-solid ${isExpanded ? 'fa-chevron-down' : 'fa-chevron-up'} text-xs`}></i>
              </button>
              
              {/* 편집 버튼 */}
              <button 
                onClick={onEdit}
                className="text-gray-400 hover:text-orange-500 transition-colors p-1"
                title="수정"
              >
                <i className="fa-solid fa-edit text-[8px]"></i>
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* 확장된 프리뷰 (조건부 표시) */}
      {isExpanded && (
        <div className="fixed bottom-32 left-4 right-4 max-w-sm mx-auto">
          <div className="bg-white/98 backdrop-blur-md rounded-2xl border border-orange-500/20 shadow-2xl overflow-hidden animate-slideUp">
            
            {/* 헤더 */}
            <div className="bg-gradient-to-r from-orange-400 to-orange-500 text-white p-4">
              <div className="flex items-center justify-between">
                <h3 className="font-bold text-sm">선택 정보</h3>
                <button 
                  onClick={toggleExpanded}
                  className="text-white/80 hover:text-white transition-colors"
                >
                  <i className="fa-solid fa-times text-sm"></i>
                </button>
              </div>
            </div>

            {/* 단계별 정보 */}
            <div className="p-4 space-y-3 max-h-64 overflow-y-auto">
              {stepInfo.map((step, index) => (
                <div key={step.step} className="flex items-start gap-3">
                  
                  {/* 단계 아이콘 */}
                  <div className={`w-8 h-8 rounded-full flex items-center justify-center flex-shrink-0 ${
                    step.completed 
                      ? 'bg-gradient-to-br from-orange-400 to-orange-500 text-white' 
                      : step.current
                      ? 'bg-blue-100 text-blue-600'
                      : 'bg-gray-100 text-gray-400'
                  }`}>
                    <i className={`fa-solid ${step.icon} text-xs`}></i>
                  </div>

                  {/* 단계 정보 */}
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center gap-2 mb-1">
                      <span className={`text-xs font-semibold ${
                        step.completed 
                          ? 'text-gray-800' 
                          : step.current
                          ? 'text-blue-600'
                          : 'text-gray-400'
                      }`}>
                        {step.step}. {step.title}
                      </span>
                      {step.completed && (
                        <i className="fa-solid fa-check-circle text-green-500 text-xs"></i>
                      )}
                      {step.current && (
                        <i className="fa-solid fa-clock text-blue-500 text-xs"></i>
                      )}
                    </div>
                    
                    <p className={`text-sm font-medium truncate ${
                      step.completed 
                        ? 'text-gray-700' 
                        : step.current
                        ? 'text-blue-600'
                        : 'text-gray-400'
                    }`}>
                      {step.content}
                    </p>
                    
                    {step.detail && (
                      <p className="text-xs text-gray-500 truncate mt-1">
                        {step.detail}
                      </p>
                    )}
                  </div>

                  {/* 연결선 */}
                  {index < stepInfo.length - 1 && (
                    <div className="absolute left-7 mt-8 w-0.5 h-4 bg-gray-200"></div>
                  )}
                </div>
              ))}

              {/* 진행률 표시 */}
              <div className="mt-4 pt-3 border-t border-gray-100">
                <div className="flex items-center justify-between mb-2">
                  <span className="text-xs text-gray-600 font-medium">진행률</span>
                  <span className="text-xs text-orange-600 font-bold">
                    {Math.round((stepInfo.filter(s => s.completed).length / 4) * 100)}%
                  </span>
                </div>
                <div className="w-full bg-gray-200 rounded-full h-2">
                  <div 
                    className="bg-gradient-to-r from-orange-400 to-orange-500 h-2 rounded-full transition-all duration-500"
                    style={{ 
                      width: `${(stepInfo.filter(s => s.completed).length / 4) * 100}%` 
                    }}
                  ></div>
                </div>
              </div>
            </div>

            {/* 하단 액션 */}
            <div className="p-4 bg-gray-50 border-t border-gray-100">
              <div className="flex gap-2">
                <button 
                  onClick={onEdit}
                  className="flex-1 bg-white border border-gray-200 text-gray-700 py-2 px-3 rounded-lg text-xs font-medium hover:bg-gray-50 transition-colors"
                >
                  <i className="fa-solid fa-edit mr-1"></i>
                  처음부터
                </button>
                <button 
                  onClick={toggleExpanded}
                  className="flex-1 bg-gradient-to-r from-orange-400 to-orange-500 text-white py-2 px-3 rounded-lg text-xs font-medium hover:shadow-md transition-all"
                >
                  <i className="fa-solid fa-check mr-1"></i>
                  확인
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default FloatingPreview;
