import React, { useState, useEffect } from 'react';
import { useDelivery } from '../../context/DeliveryContext';
import Header from '../common/Header';
import FloatingPreview from '../common/FloatingPreview';
import SearchInput from '../common/SearchInput';
import Card from '../ui/Card';
import Button from '../ui/Button';

const DestinationStoreSelector = () => {
  const { state, dispatch } = useDelivery();
  const [selectedStore, setSelectedStore] = useState(state.destinationStore);
  const [searchTerm, setSearchTerm] = useState('');
  const [stores, setStores] = useState([]);

  const storeData = [
    {
      id: 5,
      name: '루이비통 명동점',
      address: '서울 중구 명동길 20',
      status: '영업중',
      hours: '10:30 - 20:00',
      isOpen: true
    },
    {
      id: 6,
      name: '루이비통 잠실점',
      address: '서울 송파구 잠실로 240',
      status: '영업중',
      hours: '10:30 - 20:00',
      isOpen: true
    },
    {
      id: 7,
      name: '루이비통 여의도점',
      address: '서울 영등포구 여의도동 23',
      status: '영업중',
      hours: '10:30 - 20:00',
      isOpen: true
    },
    {
      id: 8,
      name: '루이비통 홍대점',
      address: '서울 마포구 홍익로 3길 20',
      status: '휴무',
      hours: '월요일',
      isOpen: false
    },
    {
      id: 9,
      name: '루이비통 신촌점',
      address: '서울 서대문구 신촌로 83',
      status: '영업중',
      hours: '10:30 - 20:00',
      isOpen: true
    }
  ];

  useEffect(() => {
    const filteredStores = storeData.filter(store =>
      store.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setStores(filteredStores);
  }, [searchTerm]);

  const handleStoreSelect = (store) => {
    setSelectedStore(store);
  };

  const handleNext = () => {
    if (selectedStore) {
      dispatch({ type: 'SET_DESTINATION_STORE', payload: selectedStore });
      // 여기서 배송 요청 API 호출
      console.log('배송 요청:', {
        type: state.deliveryType,
        origin: state.originStore,
        destination: selectedStore
      });
    }
  };

  const handleBack = () => {
    dispatch({ type: 'SET_STEP', payload: 2 });
  };

  const handleClose = () => {
    console.log('닫기');
  };

  const handleEditPreview = () => {
    setSelectedStore(null);
  };

  const getPreviewContent = () => {
    if (selectedStore && state.originStore) {
      return `${state.deliveryType} • ${state.originStore.name} → ${selectedStore.name}`;
    } else if (state.originStore) {
      return `${state.deliveryType} • ${state.originStore.name}`;
    }
    return state.deliveryType;
  };

  return (
    <div className="bg-gray-100 flex items-center justify-center min-h-screen font-sans">
      <div className="w-full max-w-sm mx-auto bg-white shadow-lg rounded-3xl overflow-hidden h-screen max-h-[812px] flex flex-col relative">
        
        <Header 
          title="도착지 매장 선택"
          subtitle="도착지 매장을 선택해주세요"
          currentStep={3}
          onBack={handleBack}
          onClose={handleClose}
        />

        <main className="flex-1 bg-gray-50 p-6 space-y-4 overflow-y-auto pb-20">
          <SearchInput 
            placeholder="매장명으로 검색"
            value={searchTerm}
            onChange={setSearchTerm}
          />

          <div className="flex flex-col gap-4">
            {stores.map((store) => (
              <Card
                key={store.id}
                selected={selectedStore?.id === store.id}
                onClick={() => handleStoreSelect(store)}
              >
                <div className="flex items-center justify-between">
                  <div className="flex items-center gap-4">
                    <div className="w-14 h-14 rounded-xl bg-gradient-to-br from-orange-400 to-orange-500 flex items-center justify-center text-white">
                      <i className="fa-solid fa-store fa-lg"></i>
                    </div>
                    <div>
                      <h3 className="text-lg font-bold text-gray-800">
                        {store.name}
                      </h3>
                      <p className="text-sm text-gray-500">
                        {store.address}
                      </p>
                      <p className={`text-xs rounded-md px-2 py-1 mt-1 inline-block ${
                        store.isOpen 
                          ? 'text-orange-500 bg-orange-50' 
                          : 'text-gray-400 bg-gray-100'
                      }`}>
                        {store.status} • {store.hours}
                      </p>
                    </div>
                  </div>
                  <i className="fa-solid fa-arrow-right text-gray-400"></i>
                </div>
              </Card>
            ))}
          </div>
        </main>

        <FloatingPreview 
          content={getPreviewContent()}
          onEdit={handleEditPreview}
          show={true}
        />

        <footer className="p-6 border-t border-gray-200 bg-white">
          <Button onClick={handleNext} disabled={!selectedStore}>
            배송 요청하기
          </Button>
        </footer>
      </div>
    </div>
  );
};

export default DestinationStoreSelector;
