import React, { useState, useEffect, useRef } from 'react';
import AccountManage from './AccountManage';

const AdminPanel = () => {
  const [activeTab, setActiveTab] = useState('dashboard'); // 'dashboard', 'store-manage'
  const [stats, setStats] = useState({
    stores: { total: 0, active: 0, inactive: 0, notIssued: 0, issued: 0 },
    requests: { total: 0, pending: 0, auto_approved: 0, rejected: 0 }
  });
  const [requests, setRequests] = useState([]);
  const [loading, setLoading] = useState(false);
  const [processingIds, setProcessingIds] = useState(new Set());
  const [recentActivities, setRecentActivities] = useState([]);
  const [error, setError] = useState(null);

  // 컴포넌트 마운트 상태 추적
  const isMountedRef = useRef(true);
  const intervalRef = useRef(null);

  // 안전한 상태 업데이트 함수
  const safeSetState = (setter) => {
    if (isMountedRef.current) {
      setter();
    }
  };

  // API 호출 함수들
  const fetchStats = async () => {
    try {
      console.log('📊 통계 API 호출 시작...');
      const response = await fetch('https://quinors-lv-backend.ngrok.io/api/admin/stats');
      const result = await response.json();
      console.log('📊 통계 API 응답:', result);

      if (response.ok && result.success) {
        console.log('📊 ✅ 통계 데이터 처리 성공:', result.data);
        if (isMountedRef.current) {
          setStats(result.data);
          setError(null);
        }
      } else {
        console.error('📊 ❌ 통계 API 실패:', result);
        setError('통계 조회 실패: ' + (result.message || 'Unknown error'));
      }
    } catch (error) {
      console.error('📊 ❌ 통계 조회 네트워크 오류:', error);
      setError('통계 조회 중 네트워크 오류: ' + error.message);
    }
  };

  const fetchRequests = async (status = 'all') => {
    try {
      console.log('📋 신청 목록 API 호출 시작... status:', status);
      setLoading(true);
      const response = await fetch(`https://quinors-lv-backend.ngrok.io/api/admin/requests?status=${status}`);
      const result = await response.json();
      console.log('📋 신청 목록 API 응답:', result);

      if (response.ok && result.success) {
        console.log('📋 ✅ 신청 목록 데이터 처리 성공:', result.data);
        if (isMountedRef.current) {
          const requestsData = result.data || [];
          setRequests(requestsData);
          updateRecentActivities(requestsData);
          setError(null);
        }
      } else {
        console.error('📋 ❌ 신청 목록 API 실패:', result);
        setError('신청 목록 조회 실패: ' + (result.message || 'Unknown error'));
        if (isMountedRef.current) {
          setRequests([]);
        }
      }
    } catch (error) {
      console.error('📋 ❌ 신청 목록 조회 네트워크 오류:', error);
      setError('신청 목록 조회 중 네트워크 오류: ' + error.message);
      if (isMountedRef.current) {
        setRequests([]);
      }
    } finally {
      if (isMountedRef.current) {
        setLoading(false);
      }
    }
  };

  const updateRecentActivities = (requestsData) => {
    if (!isMountedRef.current || !Array.isArray(requestsData)) {
      console.log('🔄 최근 활동 업데이트 중단 - 마운트 상태 또는 데이터 문제');
      return;
    }

    console.log('🔄 최근 활동 업데이트 시작, 전체 데이터:', requestsData.length);

    const processedRequests = requestsData.filter(req => {
      const hasProcessedDate = req.processedDate;
      const isProcessed = req.status === 'auto_approved' || req.status === 'rejected';
      return hasProcessedDate && isProcessed;
    });

    console.log('🔄 처리된 신청 필터링:', processedRequests.length);

    const activities = processedRequests
      .sort((a, b) => new Date(b.processedDate) - new Date(a.processedDate))
      .slice(0, 10)
      .map(req => ({
        id: req._id,
        name: req.managerName,
        store: req.storeCode,
        status: req.status,
        notes: req.notes || req.validationResult?.failureReason,
        time: formatTimeAgo(new Date(req.processedDate)),
        processType: req.processType || 'auto'
      }));

    console.log('🔄 최근 활동 생성:', activities.length);
    setRecentActivities(activities);
  };

  const formatTimeAgo = (date) => {
    const now = new Date();
    const diffInMinutes = Math.floor((now - date) / (1000 * 60));
    
    if (diffInMinutes < 1) return '방금 전';
    if (diffInMinutes < 60) return `${diffInMinutes}분 전`;
    
    const diffInHours = Math.floor(diffInMinutes / 60);
    if (diffInHours < 24) return `${diffInHours}시간 전`;
    
    const diffInDays = Math.floor(diffInHours / 24);
    if (diffInDays < 30) return `${diffInDays}일 전`;
    
    return date.toLocaleDateString('ko-KR');
  };

  // 컴포넌트 마운트 시 데이터 로드
  useEffect(() => {
    console.log('🚀 관리자 패널 컴포넌트 마운트 - 데이터 로드 시작');
    isMountedRef.current = true;
    
    fetchStats();
    fetchRequests();

    intervalRef.current = setInterval(() => {
      if (isMountedRef.current && activeTab === 'dashboard') {
        console.log('🔄 자동 새로고침 실행');
        fetchStats();
        fetchRequests();
      }
    }, 30000);

    return () => {
      console.log('🧹 관리자 패널 컴포넌트 언마운트 - 정리 작업');
      isMountedRef.current = false;
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, [activeTab]);

  // 매장 관리 컴포넌트로 이동
  if (activeTab === 'store-manage') {
    return <AccountManage onBackToAdmin={() => setActiveTab('dashboard')} />;
  }

  return (
    <div className="bg-gray-100 font-sans">
      <div id="mobile-container" className="w-full max-w-sm mx-auto bg-white shadow-lg min-h-screen">
        
        {/* 에러 표시 (개발용) */}
        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded m-4">
            <strong className="font-bold">오류: </strong>
            <span className="block sm:inline">{error}</span>
          </div>
        )}

        {/* 디버깅 정보 (개발용) */}
        <div className="bg-blue-100 p-2 text-xs text-blue-800 m-4 rounded">
          <div>🏪 Stores: 전체 {stats.stores?.total || 0}개, 활성 {stats.stores?.active || 0}개</div>
          <div>📋 Requests: 전체 {stats.requests?.total || 0}개, 대기 {stats.requests?.pending || 0}개</div>
          <div>🔄 Activities: {recentActivities.length}개</div>
          {loading && <div>⏳ 로딩 중...</div>}
        </div>

        {/* Header */}
        <header id="header" className="bg-gradient-to-br from-orange-400 via-orange-500 to-orange-500 text-white p-4 pt-8">
          <div className="flex items-center justify-between mb-6">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 bg-white/20 rounded-lg flex items-center justify-center">
                <svg className="w-5 h-5 text-white" fill="currentColor" viewBox="0 0 512 512">
                  <path d="M0 96l320 0c35.3 0 64 28.7 64 64l0 320c0 35.3-28.7 64-64 64L64 544c-35.3 0-64-28.7-64-64L0 96zM64 128l0 320 256 0 0-320L64 128zM320 32c0-17.7 14.3-32 32-32s32 14.3 32 32l0 96-64 0 0-96zM448 160l0 320c0 17.7-14.3 32-32 32s-32-14.3-32-32l0-320 64 0z"/>
                </svg>
              </div>
              <div>
                <h1 className="text-lg font-bold">관리자 대시보드</h1>
                <p className="text-white/80 text-xs">매장 및 계정 발급 관리</p>
              </div>
            </div>
            <div className="flex items-center gap-2">
              <button className="w-8 h-8 bg-white/20 rounded-lg flex items-center justify-center">
                <svg className="w-4 h-4 text-white" fill="currentColor" viewBox="0 0 512 512">
                  <path d="M416 208c0 45.9-14.9 88.3-40 122.7L502.6 457.4c12.5 12.5 12.5 32.8 0 45.3s-32.8 12.5-45.3 0L330.7 376c-34.4 25.2-76.8 40-122.7 40C93.1 416 0 322.9 0 208S93.1 0 208 0S416 93.1 416 208zM208 352a144 144 0 1 0 0-288 144 144 0 1 0 0 288z"/>
                </svg>
              </button>
              <img
                src="https://storage.googleapis.com/uxpilot-auth.appspot.com/avatars/avatar-2.jpg"
                className="w-8 h-8 rounded-lg"
                alt="Admin Avatar"
              />
            </div>
          </div>

          {/* Stats Grid */}
          <div id="stats-grid" className="grid grid-cols-2 gap-3">
            <div className="bg-white/10 rounded-xl p-3">
              <div className="text-white/80 text-xs mb-1">등록된 매장</div>
              <div className="text-lg font-bold text-white">{stats.stores?.total || 0}</div>
            </div>
            <div className="bg-white/10 rounded-xl p-3">
              <div className="text-white/80 text-xs mb-1">활성 매장</div>
              <div className="text-lg font-bold text-white">{stats.stores?.active || 0}</div>
            </div>
            <div className="bg-white/10 rounded-xl p-3">
              <div className="text-white/80 text-xs mb-1">계정 발급 대기</div>
              <div className="text-lg font-bold text-white">{stats.requests?.pending || 0}</div>
            </div>
            <div className="bg-white/10 rounded-xl p-3">
              <div className="text-white/80 text-xs mb-1">자동 발급 완료</div>
              <div className="text-lg font-bold text-white">{stats.requests?.auto_approved || 0}</div>
            </div>
          </div>
        </header>

        {/* Main Content */}
        <main id="main-content" className="p-4 pb-20">
          {/* Quick Actions */}
          <div id="quick-actions" className="mb-6">
            <h2 className="text-sm font-semibold text-gray-800 mb-3">빠른 작업</h2>
            <div className="grid grid-cols-2 gap-3">
              <button 
                className="bg-blue-50 border border-blue-200 rounded-xl p-4 text-left hover:bg-blue-100 transition-colors"
                onClick={() => setActiveTab('store-manage')}
              >
                <div className="w-8 h-8 bg-blue-100 rounded-lg flex items-center justify-center mb-2">
                  <svg className="w-4 h-4 text-blue-600" fill="currentColor" viewBox="0 0 576 512">
                    <path d="M547.6 103.8L490.3 13.1C485.2 5 476.1 0 466.4 0H109.6C99.9 0 90.8 5 85.7 13.1L28.3 103.8c-29.6 46.8-3.4 111.9 51.9 119.4c4 .5 8.1 .8 12.1 .8c26.1 0 49.3-11.4 65.2-29c15.9 17.6 39.1 29 65.2 29c26.1 0 49.3-11.4 65.2-29c15.9 17.6 39.1 29 65.2 29c26.2 0 49.3-11.4 65.2-29c16 17.6 39.1 29 65.2 29c4.1 0 8.1-.3 12.1-.8c55.5-7.4 81.8-72.5 52.1-119.4zM499.7 254.9l-.1 0c-5.3 .7-10.7 1.1-16.2 1.1c-12.4 0-24.3-1.9-35.4-5.3V384H128V250.6c-11.2 3.5-23.2 5.4-35.6 5.4c-5.5 0-11-.4-16.3-1.1l-.1 0c-4.1-.6-8.1-1.3-12-2.3V384v64c0 35.3 28.7 64 64 64H448c35.3 0 64-28.7 64-64V384 252.6c-4 1-8 1.8-12.3 2.3z"/>
                  </svg>
                </div>
                <div className="text-sm font-medium text-blue-800">매장 관리</div>
                <div className="text-xs text-blue-600 mt-1">매장 등록 및 정보 관리</div>
              </button>

              <button className="bg-green-50 border border-green-200 rounded-xl p-4 text-left hover:bg-green-100 transition-colors">
                <div className="w-8 h-8 bg-green-100 rounded-lg flex items-center justify-center mb-2">
                  <svg className="w-4 h-4 text-green-600" fill="currentColor" viewBox="0 0 640 512">
                    <path d="M96 128a128 128 0 1 1 256 0A128 128 0 1 1 96 128zM0 482.3C0 383.8 79.8 304 178.3 304h91.4C368.2 304 448 383.8 448 482.3c0 16.4-13.3 29.7-29.7 29.7H29.7C13.3 512 0 498.7 0 482.3zM504 312V248H440c-13.3 0-24-10.7-24-24s10.7-24 24-24h64V136c0-13.3 10.7-24 24-24s24 10.7 24 24v64h64c13.3 0 24 10.7 24 24s-10.7 24-24 24H552v64c0 13.3-10.7 24-24 24s-24-10.7-24-24z"/>
                  </svg>
                </div>
                <div className="text-sm font-medium text-green-800">계정 현황</div>
                <div className="text-xs text-green-600 mt-1">발급된 계정 현황 확인</div>
              </button>
            </div>
          </div>

          {/* Recent Activities */}
          <div id="recent-activities">
            <div className="flex items-center justify-between mb-3">
              <h2 className="text-sm font-semibold text-gray-800">최근 계정 발급 현황</h2>
              <button 
                className="text-xs text-orange-500 hover:text-orange-600"
                onClick={() => fetchRequests()}
              >
                새로고침
              </button>
            </div>
            <div className="bg-white rounded-xl border">
              {recentActivities.length > 0 ? recentActivities.map((activity, index) => (
                <div key={activity.id} className={`p-3 flex items-center gap-3 ${index !== recentActivities.length - 1 ? 'border-b border-gray-100' : ''}`}>
                  <div className={`w-8 h-8 rounded-full flex items-center justify-center ${
                    activity.status === 'auto_approved' ? 'bg-green-100' : 'bg-red-100'
                  }`}>
                    {activity.status === 'auto_approved' ? (
                      <svg className="w-3 h-3 text-green-500" fill="currentColor" viewBox="0 0 448 512">
                        <path d="M438.6 105.4c12.5 12.5 12.5 32.8 0 45.3l-256 256c-12.5 12.5-32.8 12.5-45.3 0l-128-128c-12.5-12.5-12.5-32.8 0-45.3s32.8-12.5 45.3 0L160 338.7 393.4 105.4c12.5-12.5 32.8-12.5 45.3 0z"/>
                      </svg>
                    ) : (
                      <svg className="w-3 h-3 text-red-500" fill="currentColor" viewBox="0 0 384 512">
                        <path d="M342.6 150.6c12.5-12.5 12.5-32.8 0-45.3s-32.8-12.5-45.3 0L192 210.7 86.6 105.4c-12.5-12.5-32.8-12.5-45.3 0s-12.5 32.8 0 45.3L146.7 256 41.4 361.4c-12.5 12.5-12.5 32.8 0 45.3s32.8 12.5 45.3 0L192 301.3 297.4 406.6c12.5 12.5 32.8 12.5 45.3 0s12.5-32.8 0-45.3L237.3 256 342.6 150.6z"/>
                      </svg>
                    )}
                  </div>
                  <div className="flex-1">
                    <div className="text-sm font-medium text-gray-800">
                      {activity.name} ({activity.store})
                    </div>
                    <div className="text-xs text-gray-500">
                      {activity.status === 'auto_approved' 
                        ? `자동 계정 발급 완료 - ${activity.time}` 
                        : `자동 거부: ${activity.notes || '매장 정보 불일치'} - ${activity.time}`}
                    </div>
                  </div>
                  <div className={`px-2 py-1 text-xs rounded-full ${
                    activity.processType === 'auto' 
                      ? 'bg-blue-100 text-blue-600' 
                      : 'bg-gray-100 text-gray-600'
                  }`}>
                    {activity.processType === 'auto' ? '자동' : '수동'}
                  </div>
                </div>
              )) : (
                <div className="text-center py-4 text-gray-500 text-sm">
                  최근 계정 발급 내역이 없습니다.
                  <br/>
                  <small className="text-gray-400">자동 발급이나 거부 처리 후 여기에 표시됩니다</small>
                </div>
              )}
            </div>
          </div>
        </main>

        {/* Bottom Navigation - 통합된 네비게이션 */}
        <nav id="bottom-nav" className="fixed bottom-0 left-1/2 transform -translate-x-1/2 w-full max-w-sm bg-white border-t border-gray-200 p-4">
          <div className="grid grid-cols-3 gap-1">
            <button
              className={`flex flex-col items-center gap-1 py-2 ${
                activeTab === 'dashboard' ? 'text-orange-500' : 'text-gray-400'
              }`}
              onClick={() => setActiveTab('dashboard')}
            >
              <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 576 512">
                <path d="M575.8 255.5c0 18-15 32.1-32 32.1h-32l.7 160.2c0 2.7-.2 5.4-.5 8.1V472c0 22.1-17.9 40-40 40H456c-1.1 0-2.2 0-3.3-.1c-1.4 .1-2.8 .1-4.2 .1H416 392c-22.1 0-40-17.9-40-40V448 384c0-17.7-14.3-32-32-32H256c-17.7 0-32 14.3-32 32v64 24c0 22.1-17.9 40-40 40H160 128.1c-1.5 0-3-.1-4.5-.2c-1.2 .1-2.4 .2-3.6 .2H104c-22.1 0-40-17.9-40-40V360c0-.9 0-1.9 .1-2.8V287.6H32c-18 0-32-14-32-32.1c0-9 3-17 10-24L266.4 8c7-7 15-8 22-8s15 2 21 7L564.8 231.5c8 7 12 15 11 24z"/>
              </svg>
              <span className="text-xs">홈</span>
            </button>
            
            <button
              className={`flex flex-col items-center gap-1 py-2 relative ${
                activeTab === 'store-manage' ? 'text-orange-500' : 'text-gray-400'
              }`}
              onClick={() => {
                console.log('🏪 매장관리 탭 클릭');
                setActiveTab('store-manage');
              }}
            >
              <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 576 512">
                <path d="M547.6 103.8L490.3 13.1C485.2 5 476.1 0 466.4 0H109.6C99.9 0 90.8 5 85.7 13.1L28.3 103.8c-29.6 46.8-3.4 111.9 51.9 119.4c4 .5 8.1 .8 12.1 .8c26.1 0 49.3-11.4 65.2-29c15.9 17.6 39.1 29 65.2 29c26.1 0 49.3-11.4 65.2-29c15.9 17.6 39.1 29 65.2 29c26.2 0 49.3-11.4 65.2-29c16 17.6 39.1 29 65.2 29c4.1 0 8.1-.3 12.1-.8c55.5-7.4 81.8-72.5 52.1-119.4zM499.7 254.9l-.1 0c-5.3 .7-10.7 1.1-16.2 1.1c-12.4 0-24.3-1.9-35.4-5.3V384H128V250.6c-11.2 3.5-23.2 5.4-35.6 5.4c-5.5 0-11-.4-16.3-1.1l-.1 0c-4.1-.6-8.1-1.3-12-2.3V384v64c0 35.3 28.7 64 64 64H448c35.3 0 64-28.7 64-64V384 252.6c-4 1-8 1.8-12.3 2.3z"/>
              </svg>
              <span className="text-xs">매장관리</span>
            </button>
            
            <button className="flex flex-col items-center gap-1 py-2 text-gray-400">
              <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 512 512">
                <path d="M495.9 166.6c3.2 8.7 .5 18.4-6.4 24.6l-43.3 39.4c1.1 8.3 1.7 16.8 1.7 25.4s-.6 17.1-1.7 25.4l43.3 39.4c6.9 6.2 9.6 15.9 6.4 24.6c-4.4 11.9-9.7 23.3-15.8 34.3l-4.7 8.1c-6.6 11-14 21.4-22.1 31.2c-5.9 7.2-15.7 9.6-24.5 6.8l-55.7-17.7c-13.4 10.3-28.2 18.9-44 25.4l-12.5 57.1c-2 9.1-9 16.3-18.2 17.8c-13.8 2.3-28 3.5-42.5 3.5s-28.7-1.2-42.5-3.5c-9.2-1.5-16.2-8.7-18.2-17.8l-12.5-57.1c-15.8-6.5-30.6-15.1-44-25.4L83.1 425.9c-8.8 2.8-18.6 .3-24.5-6.8c-8.1-9.8-15.5-20.2-22.1-31.2l-4.7-8.1c-6.1-11-11.4-22.4-15.8-34.3c-3.2-8.7-.5-18.4 6.4-24.6l43.3-39.4C64.6 273.1 64 264.6 64 256s.6-17.1 1.7-25.4L22.4 191.2c-6.9-6.2-9.6-15.9-6.4-24.6c4.4-11.9 9.7-23.3 15.8-34.3l4.7-8.1c6.6-11 14-21.4 22.1-31.2c5.9-7.2 15.7-9.6 24.5-6.8l55.7 17.7c13.4-10.3 28.2-18.9 44-25.4l12.5-57.1c2-9.1 9-16.3 18.2-17.8C227.3 1.2 241.5 0 256 0s28.7 1.2 42.5 3.5c9.2 1.5 16.2 8.7 18.2 17.8l12.5 57.1c15.8 6.5 30.6 15.1 44 25.4l55.7-17.7c8.8-2.8 18.6-.3 24.5 6.8c8.1 9.8 15.5 20.2 22.1 31.2l4.7 8.1c6.1 11 11.4 22.4 15.8 34.3zM256 336a80 80 0 1 0 0-160 80 80 0 1 0 0 160z"/>
              </svg>
              <span className="text-xs">설정</span>
            </button>
          </div>
        </nav>
      </div>
    </div>
  );
};

export default AdminPanel;
