  const calculateStats = (storeData) => {
    const total = storeData.length;
    const inactive = storeData.filter(store => !store.isActive).length;
    const active = storeData.filter(store => store.isActive).length;
    
    let notIssued = 0;
    let issued = 0;
    
    storeData.forEach(store => {
      if (!store.isActive) return;
      
      if (store.departments && store.departments.length > 0) {
        // ÏÉàÎ°úÏö¥ Î∂ÄÏÑúÎ≥Ñ Íµ¨Ï°∞ - Ïã§Ï†ú User Í≥ÑÏ†ï Ï°¥Ïû¨ Ïó¨Î∂Ä ÌôïÏù∏
        const hasUnissuedDept = store.departments.some(dept => 
          dept.hasUserAccount === false || !dept.accountIssued
        );
        const hasIssuedDept = store.departments.some(dept => 
          dept.hasUserAccount === true || dept.accountIssued
        );
        
        if (hasUnissuedDept) notIssued++;
        if (hasIssuedDept) issued++;
      } else {
        // Í∏∞Ï°¥ Íµ¨Ï°∞ - hasUserAccount Ïö∞ÏÑ†, ÏóÜÏúºÎ©¥ accountIssued ÏÇ¨Ïö©
        const actualStatus = store.hasUserAccount !== undefined ? 
