import React, { useState, useRef, useEffect } from 'react';
import { useDelivery } from '../../context/DeliveryContext';
import { useAuth } from '../../context/AuthContext';
import Header from '../common/Header';
import FloatingPreview from '../common/FloatingPreview';
import Button from '../ui/Button';
import AddressConverter from '../common/AddressConverter';

const DeliveryInfoForm = () => {
  const { deliveryType, originStore, destinationStore, dispatch } = useDelivery();
  const { token } = useAuth();

  // ê¸°ì¡´ ë¬¼í’ˆ ì •ë³´ ìƒíƒœ
  const [selectedItem, setSelectedItem] = useState('');
  const [customItem, setCustomItem] = useState('');

  // ê¸°ì¡´ ë°°ì†¡ ì˜µì…˜ ìƒíƒœ
  const [deliveryMethod, setDeliveryMethod] = useState('');
  const [needsCarrier, setNeedsCarrier] = useState(false);
  const [deliveryRoute, setDeliveryRoute] = useState('');
  const [specialOption, setSpecialOption] = useState('');
  const [scheduleDate, setScheduleDate] = useState('');
  const [scheduleTime, setScheduleTime] = useState('');

  // ê¸°ì¡´ ìƒí’ˆ ìƒì„¸ ì •ë³´ ìƒíƒœ
  const [productDetails, setProductDetails] = useState({
    productNumber: '',
    orderNumber: '',
    ticketNumber: '',
    register: '',
    requestNotes: ''
  });
  const [noProductNumber, setNoProductNumber] = useState(false);

  // ðŸ†• ì£¼ì†Œ ë³€í™˜ ìƒíƒœ (AddressConverterì—ì„œ ì½œë°±ìœ¼ë¡œ ë°›ìŒ)
  const [originAddressData, setOriginAddressData] = useState(null);
  const [destinationAddressData, setDestinationAddressData] = useState(null);

  // ðŸ†• API ê´€ë ¨ ìƒíƒœ ì¶”ê°€
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitError, setSubmitError] = useState('');
  const [submitSuccess, setSubmitSuccess] = useState(false);

  // ê¸°ì¡´ OCR ê´€ë ¨ ìƒíƒœ
  const [showCameraModal, setShowCameraModal] = useState(false);
  const [showResultModal, setShowResultModal] = useState(false);
  const [ocrResults, setOcrResults] = useState([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const [cameraError, setCameraError] = useState('');
  const [stream, setStream] = useState(null);
  const [videoReady, setVideoReady] = useState(false);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  // API ê¸°ë³¸ URL ì„¤ì •
  const API_BASE_URL = process.env.NODE_ENV === 'production'
    ? 'https://quinors-lv-backend.ngrok.io/api'
    : 'https://quinors-lv-backend.ngrok.io/api';

  // ê¸°ì¡´ ì˜µì…˜ë“¤
  const itemOptions = [
    { id: 'bag', label: 'ì‡¼í•‘ë°±', icon: 'fa-shopping-bag' },
    { id: 'clothes', label: 'ì˜·,ìœ ë‹ˆí¼', icon: 'fa-shirt' },
    { id: 'etc', label: 'ê¸°íƒ€', icon: 'fa-box' }
  ];

  const deliveryMethods = [
    { id: 'motorcycle', label: 'ì˜¤í† ë°”ì´', icon: 'fa-motorcycle', weight: 'ìµœëŒ€ 5kg' },
    { id: 'damas', label: 'ë‹¤ë§ˆìŠ¤', icon: 'fa-truck', weight: 'ìµœëŒ€ 350kg' }
  ];

  const routeOptions = [
    { id: 'oneway', label: 'íŽ¸ë„', icon: 'fa-arrow-right' },
    { id: 'roundtrip', label: 'ì™•ë³µ', icon: 'fa-arrows-left-right' }
  ];

  const specialOptions = [
    { id: 'none', label: 'ì—†ìŒ', icon: 'fa-check' },
    { id: 'express', label: 'ê¸‰ì†¡', icon: 'fa-bolt' },
    { id: 'scheduled', label: 'ì˜ˆì•½', icon: 'fa-calendar' }
  ];

  // ðŸ†• API ë§¤í•‘ í•¨ìˆ˜ë“¤
  const mapItemToApiFormat = (item) => {
    const itemMapping = {
      'bag': '2',        // ì†Œë°•ìŠ¤
      'clothes': '1',    // ì„œë¥˜ë´‰íˆ¬  
      'etc': '3'         // ì¤‘ë°•ìŠ¤
    };
    return itemMapping[item] || '2';
  };

  const mapDeliveryMethodToApiFormat = (method) => {
    const methodMapping = {
      'motorcycle': '1', // ì˜¤í† ë°”ì´
      'damas': '2'       // ë‹¤ë§ˆìŠ¤
    };
    return methodMapping[method] || '1';
  };

  const mapRouteToApiFormat = (route) => {
    const routeMapping = {
      'oneway': '1',     // íŽ¸ë„
      'roundtrip': '3'   // ì™•ë³µ
    };
    return routeMapping[route] || '1';
  };

  const mapSpecialOptionToApiFormat = (option) => {
    const optionMapping = {
      'none': '1',       // ì¼ë°˜
      'express': '3',    // ê¸‰ì†¡
      'scheduled': '1'   // ì˜ˆì•½ì€ ì¼ë°˜ìœ¼ë¡œ (ì˜ˆì•½ ì‹œê°„ì€ ë³„ë„ ì²˜ë¦¬)
    };
    return optionMapping[option] || '1';
  };

  // ðŸ†• ë©”ëª¨ ìžë™ ìƒì„± í•¨ìˆ˜
  const generateMemo = () => {
    const memoItems = [];
    
    if (productDetails.productNumber && !noProductNumber) {
      memoItems.push(`ì œí’ˆë²ˆí˜¸: ${productDetails.productNumber}`);
    }
    if (productDetails.orderNumber) {
      memoItems.push(`ì˜¤ë”ë²ˆí˜¸: ${productDetails.orderNumber}`);
    }
    if (productDetails.ticketNumber) {
      memoItems.push(`í‹°ì¼“ë²ˆí˜¸: ${productDetails.ticketNumber}`);
    }
    if (productDetails.register) {
      memoItems.push(`ë ˆì§€ìŠ¤í„°: ${productDetails.register}`);
    }
    if (productDetails.requestNotes) {
      memoItems.push(`ìš”ì²­ì‚¬í•­: ${productDetails.requestNotes}`);
    }

    return memoItems.join('\n');
  };

  // ðŸ†• í”½ì—… ë‚ ì§œ/ì‹œê°„ í¬ë§·íŒ… í•¨ìˆ˜ (14ìžë¦¬ í˜•ì‹)
  const formatPickupDateTime = () => {
  let targetDate;
  
  if (specialOption === 'scheduled' && scheduleDate && scheduleTime) {
    // ì‚¬ìš©ìžê°€ ì„¤ì •í•œ ì˜ˆì•½ ë‚ ì§œ/ì‹œê°„ ì‚¬ìš©
    const [year, month, day] = scheduleDate.split('-');
    const [hour, minute] = scheduleTime.split(':');
    
    targetDate = new Date(year, month - 1, day, hour, minute, 0);
  } else {
    // ê¸°ë³¸ê°’: í˜„ìž¬ ì‹œê°„ + 1ì‹œê°„
    targetDate = new Date();
    targetDate.setHours(targetDate.getHours() + 1);
    targetDate.setSeconds(0);
  }

  // ðŸŽ¯ 8ìžë¦¬ í˜•ì‹ìœ¼ë¡œ ìˆ˜ì •: YYYYMMDD
  const year = targetDate.getFullYear();
  const month = String(targetDate.getMonth() + 1).padStart(2, '0');
  const day = String(targetDate.getDate()).padStart(2, '0');
  const hour = String(targetDate.getHours()).padStart(2, '0');
  const minute = String(targetDate.getMinutes()).padStart(2, '0');
  const second = String(targetDate.getSeconds()).padStart(2, '0');

  // ðŸ†• 8ìžë¦¬ ë‚ ì§œ í˜•ì‹ (ì‹œê°„ì€ ë³„ë„)
  const pickup_date = `${year}${month}${day}`;

  console.log('ðŸ“… í”½ì—… ë‚ ì§œ/ì‹œê°„ í¬ë§·íŒ… (8ìžë¦¬):', {
    targetDate: targetDate.toISOString(),
    pickup_date, // ì˜ˆ: 20250902 (2025ë…„ 09ì›” 02ì¼)
    pick_hour: hour,
    pick_min: minute,
    pick_sec: second
  });

  return {
    pickup_date,
    pick_hour: hour,
    pick_min: minute,
    pick_sec: second
  };
};

  // ðŸ†• AddressConverter ì½œë°± í•¨ìˆ˜ë“¤
  const handleOriginAddressConverted = (convertedData) => {
    console.log('ðŸ”„ ì¶œë°œì§€ ì£¼ì†Œ ë³€í™˜ ì™„ë£Œ:', convertedData);
    setOriginAddressData(convertedData);
  };

  const handleDestinationAddressConverted = (convertedData) => {
    console.log('ðŸ”„ ë„ì°©ì§€ ì£¼ì†Œ ë³€í™˜ ì™„ë£Œ:', convertedData);
    setDestinationAddressData(convertedData);
  };

  // ðŸ†• ë°°ì†¡ ì ‘ìˆ˜ API í˜¸ì¶œ í•¨ìˆ˜ (ìˆ˜ì •ë¨)
  const handleDeliverySubmit = async () => {
    setIsSubmitting(true);
    setSubmitError('');
    setSubmitSuccess(false);

    try {
      // í•„ìˆ˜ ë°ì´í„° ê²€ì¦
      if (!originStore || !destinationStore) {
        throw new Error('ì¶œë°œì§€ì™€ ë„ì°©ì§€ë¥¼ ì„ íƒí•´ì£¼ì„¸ìš”.');
      }

      if (!selectedItem) {
        throw new Error('ë¬¼í’ˆ ì¢…ë¥˜ë¥¼ ì„ íƒí•´ì£¼ì„¸ìš”.');
      }

      if (!deliveryMethod) {
        throw new Error('ë°°ì†¡ ìˆ˜ë‹¨ì„ ì„ íƒí•´ì£¼ì„¸ìš”.');
      }

      if (!deliveryRoute) {
        throw new Error('ë°°ì†¡ ê²½ë¡œë¥¼ ì„ íƒí•´ì£¼ì„¸ìš”.');
      }

      if (!specialOption) {
        throw new Error('íŠ¹ìˆ˜ ì˜µì…˜ì„ ì„ íƒí•´ì£¼ì„¸ìš”.');
      }

      // ì˜ˆì•½ ì˜µì…˜ ì„ íƒ ì‹œ ë‚ ì§œ/ì‹œê°„ ê²€ì¦
      if (specialOption === 'scheduled') {
        if (!scheduleDate || !scheduleTime) {
          throw new Error('ì˜ˆì•½ ë‚ ì§œì™€ ì‹œê°„ì„ ì„ íƒí•´ì£¼ì„¸ìš”.');
        }
      }

      console.log('ðŸšš ë°°ì†¡ ì ‘ìˆ˜ ì‹œìž‘');

      // í”½ì—… ë‚ ì§œ/ì‹œê°„ ì •ë³´
      const pickupInfo = formatPickupDateTime();

      // ðŸŽ¯ ë°°ì†¡ ìƒì„¸ ì •ë³´ êµ¬ì„±
      const deliveryDetails = {
        kind: mapDeliveryMethodToApiFormat(deliveryMethod),
        item_type: mapItemToApiFormat(selectedItem),
        doc: mapRouteToApiFormat(deliveryRoute),
        sfast: mapSpecialOptionToApiFormat(specialOption),
        pay_gbn: '1', // ì„ ë¶ˆ (ê¸°ë³¸ê°’)
        ...pickupInfo,
        memo: generateMemo(),
        reason_desc: 'ë§¤ìž¥ê°„ ë°°ì†¡',
        order_memo: 'APPì£¼ë¬¸'
      };

      // ðŸŽ¯ API ìš”ì²­ ë°ì´í„° êµ¬ì„±
      const requestData = {
        deliveryType: deliveryType,
        originStore: originStore,
        destinationStore: destinationStore,
        originConvertedAddress: originAddressData,
        destinationConvertedAddress: destinationAddressData,
        deliveryDetails: deliveryDetails
      };

      console.log('ðŸ“‹ API ìš”ì²­ ë°ì´í„°:', requestData);

      // API í˜¸ì¶œ
      const response = await fetch(`${API_BASE_URL}/delivery/submit`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(requestData)
      });

      const result = await response.json();

      if (response.ok && result.success) {
        console.log('âœ… ë°°ì†¡ ì ‘ìˆ˜ ì„±ê³µ:', result);
        setSubmitSuccess(true);
        
        // ì„±ê³µ ì•Œë¦¼
        alert('ë°°ì†¡ ì ‘ìˆ˜ê°€ ì™„ë£Œë˜ì—ˆìŠµë‹ˆë‹¤!');
        
      } else {
        throw new Error(result.message || 'ë°°ì†¡ ì ‘ìˆ˜ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.');
      }

    } catch (error) {
      console.error('âŒ ë°°ì†¡ ì ‘ìˆ˜ ì‹¤íŒ¨:', error);
      setSubmitError(error.message);
