import React, { useState, useEffect } from 'react';
import { useDelivery } from '../../context/DeliveryContext';
import { useAuth } from '../../context/AuthContext';
import AddressConverter from './AddressConverter';

const FloatingPreview = ({ content, onEdit, show = true }) => {
  const [isExpanded, setIsExpanded] = useState(false);
  const { deliveryType, originStore, destinationStore, currentStep } = useDelivery();
  const { user, requesterInfo, updateUserInfo } = useAuth(); // updateUserInfo Ï∂îÍ∞Ä
  
  // Ï£ºÏÜå Î≥ÄÌôò Í≤∞Í≥º Ï†ÄÏû•
  const [originConvertedAddress, setOriginConvertedAddress] = useState(null);
  const [destinationConvertedAddress, setDestinationConvertedAddress] = useState(null);

  // üîÑ Ïª¥Ìè¨ÎÑåÌä∏ ÎßàÏö¥Ìä∏ Ïãú ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ ÏóÖÎç∞Ïù¥Ìä∏
  useEffect(() => {
    if (user && (!user.tel_no || user.tel_no === '')) {
      console.log('üîÑ FloatingPreview: tel_noÍ∞Ä ÏóÜÏñ¥ÏÑú ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ ÏóÖÎç∞Ïù¥Ìä∏ ÏãúÎèÑ');
      updateUserInfo();
    }
  }, [user, updateUserInfo]);

  if (!show) return null;

  const toggleExpanded = () => {
    setIsExpanded(!isExpanded);
  };

  // Ï†ÑÌôîÎ≤àÌò∏ Ìè¨Îß∑ÌåÖ Ìï®Ïàò - ÏàòÏ†ïÎê®
  const formatPhoneNumber = (phoneNumber) => {
    console.log('üìû Ï†ÑÌôîÎ≤àÌò∏ Ìè¨Îß∑ÌåÖ ÏûÖÎ†•:', phoneNumber, typeof phoneNumber);
    
    if (!phoneNumber || phoneNumber === '' || phoneNumber === 'Ïó∞ÎùΩÏ≤ò ÏóÜÏùå') {
      return 'Ïó∞ÎùΩÏ≤ò ÏóÜÏùå';
    }
    
    // Î¨∏ÏûêÏó¥Î°ú Î≥ÄÌôò ÌõÑ Ïà´ÏûêÎßå Ï∂îÏ∂ú
    const phoneStr = String(phoneNumber);
    const numbers = phoneStr.replace(/[^0-9]/g, '');
    
    console.log('üìû Ïà´ÏûêÎßå Ï∂îÏ∂ú:', numbers);
    
    // 11ÏûêÎ¶¨ Ìú¥ÎåÄÌè∞ Î≤àÌò∏ Ìè¨Îß∑ÌåÖ
    if (numbers.length === 11) {
      const formatted = numbers.replace(/(\d{3})(\d{4})(\d{4})/, '$1-$2-$3');
      console.log('üìû 11ÏûêÎ¶¨ Ìè¨Îß∑ÌåÖ Í≤∞Í≥º:', formatted);
      return formatted;
    }
    // 10ÏûêÎ¶¨ Ï†ÑÌôîÎ≤àÌò∏ Ìè¨Îß∑ÌåÖ  
    else if (numbers.length === 10) {
      const formatted = numbers.replace(/(\d{3})(\d{3})(\d{4})/, '$1-$2-$3');
      console.log('üìû 10ÏûêÎ¶¨ Ìè¨Îß∑ÌåÖ Í≤∞Í≥º:', formatted);
      return formatted;
    }
    
    console.log('üìû Ìè¨Îß∑ÌåÖ ÏïàÎê®, ÏõêÎ≥∏ Î∞òÌôò:', phoneStr);
    return phoneStr; // ÏõêÎ≥∏ Î∞òÌôò
  };

  // Îß§Ïû• Ï†ïÎ≥¥ Ìè¨Îß∑ÌåÖ Ìï®Ïàò - Í∞úÏÑ†Îêú Î≤ÑÏ†Ñ
  const formatStoreInfo = (store) => {
    if (!store) return null;

    let managerInfo = {
      name: 'Îã¥ÎãπÏûê ÏóÜÏùå',
      phone: 'Ïó∞ÎùΩÏ≤ò ÏóÜÏùå',
      department: null
    };

    // üéØ 1. selectedManagerInfoÍ∞Ä ÏûàÎäî Í≤ΩÏö∞ (ÏÉàÎ°úÏö¥ Î∞©Ïãù - Ïö∞ÏÑ†ÏàúÏúÑ ÎÜíÏùå)
    if (store.selectedManagerInfo) {
      console.log('‚úÖ selectedManagerInfo Î∞úÍ≤¨:', store.selectedManagerInfo);
      managerInfo = {
        name: store.selectedManagerInfo.name || 'Îã¥ÎãπÏûê ÏóÜÏùå',
        phone: store.selectedManagerInfo.phone || 'Ïó∞ÎùΩÏ≤ò ÏóÜÏùå',
        department: store.selectedManagerInfo.department || null
      };
    }
    // üéØ 2. departments Î∞∞Ïó¥ÏóêÏÑú ÏÑ†ÌÉùÎêú Î∂ÄÏÑú Îã¥ÎãπÏûê Ï†ïÎ≥¥ Ï∞æÍ∏∞
    else if (store.departments && store.departments.length > 0) {
      let selectedDepartment = null;

      // ÏÑ†ÌÉùÎêú Î∂ÄÏÑú Ï†ïÎ≥¥ ÌôïÏù∏ Î∞©Î≤ïÎì§
      if (store.selectedDepartment) {
        selectedDepartment = store.departments.find(dept =>
          dept.department === store.selectedDepartment
        );
      } else if (store.selectedDepartmentIndex !== undefined) {
        selectedDepartment = store.departments[store.selectedDepartmentIndex];
      } else if (store.departmentId) {
        selectedDepartment = store.departments.find(dept =>
          dept._id && dept._id.toString() === store.departmentId
        );
      }

      // ÏÑ†ÌÉùÎêú Î∂ÄÏÑúÍ∞Ä ÏóÜÎäî Í≤ΩÏö∞ ÎîîÎ≤ÑÍπÖ
      if (!selectedDepartment) {
        console.warn('‚ö†Ô∏è ÏÑ†ÌÉùÎêú Î∂ÄÏÑú Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§:', {
          storeName: store.storeName || store.name,
          selectedDepartment: store.selectedDepartment,
          selectedDepartmentIndex: store.selectedDepartmentIndex,
          departmentId: store.departmentId,
          availableDepartments: store.departments.map(d => d.department)
        });
        selectedDepartment = store.departments[0];
      }

      // Îã¥ÎãπÏûê Ï†ïÎ≥¥ ÏÑ§Ï†ï
      if (selectedDepartment) {
        managerInfo = {
          name: selectedDepartment.managerName || 'Îã¥ÎãπÏûê ÏóÜÏùå',
          phone: selectedDepartment.fullPhone || selectedDepartment.managerPhone || 'Ïó∞ÎùΩÏ≤ò ÏóÜÏùå',
          department: selectedDepartment.department || ''
        };
      }
    }
