  };

  const stepInfo = getStepInfo();

  return (
    <>
      {/* ÌôïÏû•Îêú ÎØ∏Î¶¨Î≥¥Í∏∞ Ìå®ÎÑê */}
      <div className={`fixed inset-y-0 left-0 z-50 w-full max-w-sm bg-white shadow-2xl transform transition-transform duration-300 ease-in-out ${
