import React, { useState, useEffect, useCallback } from 'react';
import { useDelivery } from '../../context/DeliveryContext';
import { useAuth } from '../../context/AuthContext';
import Header from '../common/Header';
import FloatingPreview from '../common/FloatingPreview';
import Button from '../ui/Button';
import SearchInput from '../common/SearchInput';
import Card from '../ui/Card';

const DestinationStoreSelector = () => {
  const { deliveryType, originStore, destinationStore, dispatch, setDestinationStore } = useDelivery();
  const { user, token } = useAuth();
  
  const [selectedStore, setSelectedStore] = useState(destinationStore);
  const [searchTerm, setSearchTerm] = useState('');
  const [stores, setStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [forceUpdate, setForceUpdate] = useState(0);

  // ëª¨ë‹¬ ìƒíƒœ
  const [showModal, setShowModal] = useState(false);
  const [modalStep, setModalStep] = useState(1);
  const [selectedStoreData, setSelectedStoreData] = useState(null);
  const [selectedDepartment, setSelectedDepartment] = useState(null);
  const [selectedManager, setSelectedManager] = useState(null);
  const [departmentManagers, setDepartmentManagers] = useState([]);
  const [modalSearchTerm, setModalSearchTerm] = useState('');

  // ê°•ì œ ë¦¬ë Œë”ë§ í•¨ìˆ˜
  const triggerUpdate = useCallback(() => {
    setForceUpdate(prev => prev + 1);
  }, []);

  // ë§¤ìž¥ ë°ì´í„° ê°€ì ¸ì˜¤ê¸°
  useEffect(() => {
    const fetchStores = async () => {
      if (!token) {
        setLoading(false);
        setError('ë¡œê·¸ì¸ì´ í•„ìš”í•©ë‹ˆë‹¤');
        setStores([]);
        return;
      }

      try {
        setLoading(true);
        setError('');
        
        const response = await fetch('https://quinors-lv-backend.ngrok.io/api/auth/stores?active=true&limit=100', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
        });

        if (!response.ok) {
          throw new Error(`API í˜¸ì¶œ ì‹¤íŒ¨: ${response.status}`);
        }

        const result = await response.json();

        if (!result.success || !result.data || result.data.length === 0) {
          throw new Error('ë§¤ìž¥ ë°ì´í„°ê°€ ì—†ìŠµë‹ˆë‹¤');
        }

        // ì„œë²„ ì‘ë‹µ êµ¬ì¡°ì— ë§žê²Œ ë°ì´í„° ì²˜ë¦¬
        const finalStores = result.data.map((store) => {
          // ë¶€ì„œë³„ë¡œ ë‹´ë‹¹ìž ê·¸ë£¹í™”
          const departmentGroups = {};
          
          if (Array.isArray(store.departments)) {
            store.departments.forEach((dept, deptIndex) => {
              const department = dept.department || 'ì¼ë°˜';
              const managerName = dept.managerName || 'ë‹´ë‹¹ìž';
              
              if (!departmentGroups[department]) {
                departmentGroups[department] = [];
              }
              
              departmentGroups[department].push({
                id: `${store._id}_${department}_${managerName}_${deptIndex}`,
                managerName: managerName,
                phone: dept.managerPhone || '',
                fullPhone: dept.fullPhone || (dept.managerPhone ? 
                  dept.managerPhone.replace(/(\d{3})(\d{4})(\d{4})/, '$1-$2-$3') : '')
              });
            });
          }
          
          return {
            id: store._id,
            storeCode: store.storeCode || '',
            name: store.storeName || '',
            address: store.address || '',
            isActive: store.isActive !== false,
            createdAt: store.createdAt,
            departments: departmentGroups
          };
        });
        
        setStores(finalStores);
        
        setTimeout(() => {
          triggerUpdate();
        }, 100);
        
      } catch (error) {
        setError(error.message);
        setStores([]);
      } finally {
        setLoading(false);
      }
    };

    fetchStores();
  }, [token, triggerUpdate]);

  // ê²€ìƒ‰ í•„í„°ë§
  const filteredStores = React.useMemo(() => {
    if (!Array.isArray(stores) || stores.length === 0) {
      return [];
    }

    if (!searchTerm.trim()) {
      return stores;
    }
    
    const searchLower = searchTerm.toLowerCase();
    return stores.filter(store => {
      const matchStore = (store.name && store.name.toLowerCase().includes(searchLower)) ||
                        (store.storeCode && store.storeCode.toLowerCase().includes(searchLower)) ||
                        (store.address && store.address.toLowerCase().includes(searchLower));
      
      const matchManager = Object.values(store.departments || {}).some(managers =>
        Array.isArray(managers) && managers.some(manager => 
          manager && manager.managerName && manager.managerName.toLowerCase().includes(searchLower)
        )
      );
      
      return matchStore || matchManager;
    });
  }, [stores, searchTerm, forceUpdate]);

  // âœ… ì¶œë°œì§€ ì„ íƒ ë‹´ë‹¹ìž ì²´í¬ í•¨ìˆ˜ (ë‹¨ìˆœí™”)
  const isSelectedInOrigin = (storeId, department, managerName) => {
    if (!originStore || !originStore.store || !originStore.department || !originStore.manager) {
      return false;
    }
    
    return (
      originStore.store.id === storeId &&
      originStore.department === department &&
      originStore.manager.managerName === managerName
    );
  };

  // âœ… í•´ë‹¹ ë§¤ìž¥ì—ì„œ ì„ íƒ ê°€ëŠ¥í•œ ë‹´ë‹¹ìžê°€ ìžˆëŠ”ì§€ ì²´í¬í•˜ëŠ” í•¨ìˆ˜
  const hasAvailableManagers = (store) => {
    if (!store.departments) return true; // ë¶€ì„œ ì •ë³´ê°€ ì—†ìœ¼ë©´ ì¼ë‹¨ ì„ íƒ ê°€ëŠ¥ìœ¼ë¡œ ì²˜ë¦¬
    
    return Object.entries(store.departments).some(([dept, managers]) => 
      managers.some(manager => 
        !isSelectedInOrigin(store.id, dept, manager.managerName)
      )
    );
  };

  // âœ… ë§¤ìž¥ ì„ íƒ ì‹œ ëª¨ë‹¬ ì—´ê¸° (ê°•í™”ëœ ë””ë²„ê¹…)
  const handleStoreSelect = (store) => {
    console.log('ðŸ” ë§¤ìž¥ í´ë¦­ë¨:', store.name);
    console.log('ðŸ” ë§¤ìž¥ ë°ì´í„°:', store);
    
    // ì„ íƒ ê°€ëŠ¥í•œ ë‹´ë‹¹ìžê°€ ìžˆëŠ”ì§€ ì²´í¬
    const hasAvailable = hasAvailableManagers(store);
    console.log('ðŸ” ì„ íƒ ê°€ëŠ¥í•œ ë‹´ë‹¹ìž ìžˆìŒ:', hasAvailable);
    
    if (!hasAvailable) {
      console.log('âŒ ì„ íƒ ë¶ˆê°€ëŠ¥í•œ ë§¤ìž¥');
      alert('ì´ ë§¤ìž¥ì˜ ëª¨ë“  ë‹´ë‹¹ìžê°€ ì¶œë°œì§€ì—ì„œ ì´ë¯¸ ì„ íƒë˜ì—ˆìŠµë‹ˆë‹¤.\në‹¤ë¥¸ ë§¤ìž¥ì„ ì„ íƒí•´ì£¼ì„¸ìš”.');
      return;
    }

    console.log('âœ… ëª¨ë‹¬ ì—´ê¸° ì‹œìž‘');
    setSelectedStoreData(store);
    setShowModal(true);
    setModalStep(1);
    setSelectedDepartment(null);
    setSelectedManager(null);
    setModalSearchTerm('');
    
    console.log('âœ… ëª¨ë‹¬ ìƒíƒœ ì„¤ì • ì™„ë£Œ');
  };

  // ëª¨ë‹¬ì—ì„œ ë¶€ì„œ ì„ íƒ
  const handleModalDepartmentSelect = (department) => {
    console.log('ðŸ” ë¶€ì„œ ì„ íƒ:', department);
    setSelectedDepartment(department);
    const managers = selectedStoreData.departments[department] || [];
    
    // ì„ íƒ ê°€ëŠ¥í•œ ë‹´ë‹¹ìžë§Œ í•„í„°ë§
    const availableManagers = managers.filter(manager => 
      !isSelectedInOrigin(selectedStoreData.id, department, manager.managerName)
    );
    
    if (availableManagers.length === 0) {
      alert(`${department} ë¶€ì„œì˜ ëª¨ë“  ë‹´ë‹¹ìžê°€ ì¶œë°œì§€ì—ì„œ ì´ë¯¸ ì„ íƒë˜ì—ˆìŠµë‹ˆë‹¤.\në‹¤ë¥¸ ë¶€ì„œë¥¼ ì„ íƒí•´ì£¼ì„¸ìš”.`);
      return;
    }
    
    setDepartmentManagers(availableManagers);
    setModalStep(2);
    setModalSearchTerm('');
  };

  // ëª¨ë‹¬ì—ì„œ ë‹´ë‹¹ìž ì„ íƒ
  const handleModalManagerSelect = (manager) => {
    // ì´ì¤‘ ì²´í¬
    if (isSelectedInOrigin(selectedStoreData.id, selectedDepartment, manager.managerName)) {
      alert('ì´ ë‹´ë‹¹ìžëŠ” ì¶œë°œì§€ì—ì„œ ì´ë¯¸ ì„ íƒë˜ì—ˆìŠµë‹ˆë‹¤.\në‹¤ë¥¸ ë‹´ë‹¹ìžë¥¼ ì„ íƒí•´ì£¼ì„¸ìš”.');
      return;
    }
    
    setSelectedManager(manager);
  };

  // ëª¨ë‹¬ì—ì„œ ì„ íƒ ì™„ë£Œ
