import React, { useState, useEffect } from 'react';
import { useDelivery } from '../../context/DeliveryContext';
import { useAuth } from '../../context/AuthContext';
import Header from '../common/Header';
import FloatingPreview from '../common/FloatingPreview';
import Button from '../ui/Button';
import SearchInput from '../common/SearchInput';
import Card from '../ui/Card';

const OriginStoreSelector = () => {
  const { deliveryType, originStore, dispatch, setOriginStore } = useDelivery();
  const { user, token } = useAuth();
  
  const [selectedStore, setSelectedStore] = useState(originStore);
  const [searchTerm, setSearchTerm] = useState('');
  const [stores, setStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // ê¸°ì¡´ 3ë‹¨ê³„ ì„ íƒ ìƒíƒœ (ëª¨ë‹¬ì—ì„œ ì‚¬ìš©)
  const [step, setStep] = useState(1);
  const [selectedStoreData, setSelectedStoreData] = useState(null);
  const [selectedDepartment, setSelectedDepartment] = useState(null);
  const [selectedManager, setSelectedManager] = useState(null);
  const [departmentManagers, setDepartmentManagers] = useState([]);

  // ëª¨ë‹¬ ìƒíƒœ
  const [showModal, setShowModal] = useState(false);
  const [modalStep, setModalStep] = useState(1);

  // ëª¨ë‹¬ ë‚´ ê²€ìƒ‰ ìƒíƒœ
  const [modalSearchTerm, setModalSearchTerm] = useState('');

  // í† í°ê³¼ ì‚¬ìš©ì ì •ë³´ í™•ì¸
  useEffect(() => {
    console.log('ğŸ” í˜„ì¬ í† í°:', token ? 'ìˆìŒ' : 'ì—†ìŒ');
    console.log('ğŸ” í˜„ì¬ ì‚¬ìš©ì:', user);
  }, [token, user]);

  // ë§¤ì¥ ë°ì´í„° ê°€ì ¸ì˜¤ê¸°
  useEffect(() => {
    const fetchStores = async () => {
      try {
        setLoading(true);
        setError('');
        
        console.log('ğŸ” API í˜¸ì¶œ ì‹œì‘');
        const response = await fetch('https://quinors-lv-backend.ngrok.io/api/auth/stores?active=true&limit=100', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
        });

        console.log('ğŸ” API ì‘ë‹µ ìƒíƒœ:', response.status);
        const result = await response.json();
        console.log('ğŸ” API ì‘ë‹µ ë°ì´í„°:', result);

        if (response.ok && result.success) {
          console.log('ğŸ” ì›ë³¸ ë°ì´í„° ê°œìˆ˜:', result.data?.length);
          console.log('ğŸ” ì›ë³¸ ë°ì´í„° ìƒ˜í”Œ:', result.data?.slice(0, 2));
          
          // âœ… ìˆ˜ì •: API ì‘ë‹µ êµ¬ì¡°ì— ë§ê²Œ ë°ì´í„° ì²˜ë¦¬
          const storeGroups = {};
          
          result.data.forEach((store, index) => {
            console.log(`ğŸ” ì²˜ë¦¬ ì¤‘ì¸ ë§¤ì¥ ${index}:`, store);
            
            if (!store.storeName) {
              console.log('âŒ ë§¤ì¥ëª…ì´ ì—†ì–´ì„œ ê±´ë„ˆëœ€:', store);
              return;
            }

            const storeName = store.storeName;
            
            if (!storeGroups[storeName]) {
              storeGroups[storeName] = {
                id: store._id,
                storeCode: store.storeCode || '',
                name: store.storeName || '',
                address: store.address || 'ì£¼ì†Œ ì •ë³´ ì—†ìŒ',
                isActive: store.isActive,
                createdAt: store.createdAt,
                departments: {}
              };
            }
            
            // âœ… ìˆ˜ì •: ì‹¤ì œ ë°ì´í„° êµ¬ì¡°ì— ë§ê²Œ ì²˜ë¦¬
            // DBì—ì„œ departmentì™€ managerNameì´ undefinedì´ë¯€ë¡œ ê¸°ë³¸ê°’ ì‚¬ìš©
            let dept = 'ì¼ë°˜';
            let managerName = 'ë‹´ë‹¹ì';
            
            // ë§Œì•½ ë°ì´í„°ë² ì´ìŠ¤ì— department í•„ë“œê°€ ìˆë‹¤ë©´ ì‚¬ìš©
            if (store.department && store.department.trim()) {
              dept = store.department.trim();
            }
            
            // ë§Œì•½ ë°ì´í„°ë² ì´ìŠ¤ì— managerName í•„ë“œê°€ ìˆë‹¤ë©´ ì‚¬ìš©
            if (store.managerName && store.managerName.trim()) {
              managerName = store.managerName.trim();
            }
            
            // notes í•„ë“œì—ì„œ ë‹´ë‹¹ì ì •ë³´ ì¶”ì¶œ ì‹œë„
            if (store.notes) {
              // notesì—ì„œ ë‹´ë‹¹ì ì •ë³´ê°€ ìˆëŠ”ì§€ í™•ì¸
              const notesLower = store.notes.toLowerCase();
              if (notesLower.includes('ì—¬ì„±') || notesLower.includes('woman')) {
                dept = 'ì—¬ì„±';
              } else if (notesLower.includes('ë‚¨ì„±') || notesLower.includes('man')) {
                dept = 'ë‚¨ì„±';
              } else if (notesLower.includes('ìŠˆì¦ˆ') || notesLower.includes('shoes')) {
                dept = 'ìŠˆì¦ˆ';
              }
              
              // notesì—ì„œ ë‹´ë‹¹ìëª… ì¶”ì¶œ ì‹œë„ (ì˜ˆ: "ë‹´ë‹¹ì: í™ê¸¸ë™" í˜•íƒœ)
              const managerMatch = store.notes.match(/ë‹´ë‹¹ì[:\s]*([ê°€-í£a-zA-Z]+)/);
              if (managerMatch) {
                managerName = managerMatch[1];
              }
            }
            
            if (!storeGroups[storeName].departments[dept]) {
              storeGroups[storeName].departments[dept] = [];
            }
            
            storeGroups[storeName].departments[dept].push({
              id: `${store._id}_${dept}_${managerName}`,
              managerName: managerName,
              phone: store.managerPhone || '',
              fullPhone: store.managerPhone ? store.managerPhone.replace(/(\d{3})(\d{4})(\d{4})/, '$1-$2-$3') : ''
            });
          });

          console.log('ğŸ” ê·¸ë£¹í™”ëœ ë§¤ì¥ ë°ì´í„°:', storeGroups);

          // ë¹ˆ ë¶€ì„œ ì œê±° ë° ìœ íš¨í•œ ë§¤ì¥ë§Œ í¬í•¨
          const transformedStores = Object.values(storeGroups).map(store => {
            // ë¹ˆ ë¶€ì„œ ì œê±°
            const validDepartments = {};
            Object.entries(store.departments).forEach(([dept, managers]) => {
              if (managers && managers.length > 0) {
                validDepartments[dept] = managers;
              }
            });
            
            return {
              ...store,
              departments: validDepartments
            };
          }).filter(store => Object.keys(store.departments).length > 0);
          
          console.log('ğŸ” ìµœì¢… ë³€í™˜ëœ ë§¤ì¥ ë°ì´í„°:', transformedStores);
          console.log('ğŸ” ìµœì¢… ë§¤ì¥ ê°œìˆ˜:', transformedStores.length);
          
          setStores(transformedStores);
          console.log('âœ… ì¶œë°œì§€ ë§¤ì¥ ë°ì´í„° ë¡œë“œ ì™„ë£Œ:', transformedStores);
        } else {
          throw new Error(result.message || 'ë§¤ì¥ ë°ì´í„°ë¥¼ ë¶ˆëŸ¬ì˜¤ëŠ”ë° ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.');
        }
      } catch (error) {
        console.error('âŒ ì¶œë°œì§€ ë§¤ì¥ ë°ì´í„° ë¡œë“œ ì‹¤íŒ¨:', error);
        setError(error.message);
        
        // ì—ëŸ¬ ë°œìƒ ì‹œ ë¬´ì¡°ê±´ ìƒ˜í”Œ ë°ì´í„° ì‚¬ìš©
        console.log('ğŸ” ìƒ˜í”Œ ë°ì´í„° ì‚¬ìš©');
        const sampleStores = [
          {
            id: 'sample-1',
            storeCode: 'QN01',
            name: 'ë£¨ì´ë¹„í†µ í€´ë…¸ìŠ¤ì ',
            address: 'ì„œìš¸ ì¤‘ë‘êµ¬ ì‚¬ê°€ì •ë¡œ54ê¸¸ 72 (ë©´ëª©ë™) 1ì¸µ',
            isActive: true,
            departments: {
              'ì—¬ì„±': [
                { id: 'qn01-women-1', managerName: 'ì•ˆíƒœìš±', phone: '01012345678', fullPhone: '010-1234-5678' },
                { id: 'qn01-women-2', managerName: 'í™ê¸¸ë™', phone: '01087654321', fullPhone: '010-8765-4321' }
              ],
              'ë‚¨ì„±': [
                { id: 'qn01-men-1', managerName: 'ê¹€ë‚¨ì„±', phone: '01011111111', fullPhone: '010-1111-1111' }
              ],
              'ìŠˆì¦ˆ': [
                { id: 'qn01-shoes-1', managerName: 'ë°•ìŠˆì¦ˆ', phone: '01022222222', fullPhone: '010-2222-2222' }
              ]
            }
          },
          {
            id: 'sample-2',
            storeCode: 'ADMIN001',
