import React, { useState, useEffect, useRef } from 'react';

const AccountManage = ({ onBackToAdmin }) => {
  const [stats, setStats] = useState({
    total: 0,
    active: 0,
    inactive: 0,
    pending: 0,
    approved: 0,
    rejected: 0
  });
  const [allAccounts, setAllAccounts] = useState([]);
  const [filteredAccounts, setFilteredAccounts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [processingIds, setProcessingIds] = useState(new Set());
  const [searchTerm, setSearchTerm] = useState('');
  const [activeFilter, setActiveFilter] = useState('전체');
  const [error, setError] = useState(null);
  
  // 컴포넌트 마운트 상태 추적
  const isMountedRef = useRef(true);
  const intervalRef = useRef(null);

  // API 호출 함수들
  const fetchAllAccounts = async () => {
    try {
      console.log('👥 전체 계정 정보 API 호출 시작...');
      setLoading(true);
      
      // 승인된 사용자 목록과 승인 대기 신청 목록을 동시에 가져오기
      const [usersResponse, requestsResponse] = await Promise.all([
        fetch('http://localhost:5480/api/admin/users'),
        fetch('http://localhost:5480/api/admin/requests?status=all')
      ]);
      
      const usersResult = await usersResponse.json();
      const requestsResult = await requestsResponse.json();
      
      console.log('👥 사용자 목록 API 응답:', usersResult);
      console.log('📋 신청 목록 API 응답:', requestsResult);
      
      if (usersResponse.ok && usersResult.success && requestsResponse.ok && requestsResult.success) {
        const users = usersResult.data || [];
        const requests = requestsResult.data || [];
        
        // 승인된 사용자들을 계정 형태로 변환
        const approvedAccounts = users.map(user => ({
          ...user,
          type: 'user', // 승인된 사용자
          accountType: 'approved'
        }));
        
        // 신청 목록을 계정 형태로 변환
        const requestAccounts = requests.map(request => ({
          ...request,
          type: 'request', // 신청
          accountType: request.status, // pending, approved, rejected
          isActive: request.status === 'approved' ? true : false
        }));
        
        // 중복 제거 (승인된 신청은 사용자 목록에서 우선)
        const userRequestIds = new Set(users.map(user => user.requestId).filter(Boolean));
        const uniqueRequestAccounts = requestAccounts.filter(req => !userRequestIds.has(req._id));
        
        const combinedAccounts = [...approvedAccounts, ...uniqueRequestAccounts];
        
        console.log('👥 ✅ 전체 계정 데이터 처리 성공:', combinedAccounts);
        
        if (isMountedRef.current) {
          setAllAccounts(combinedAccounts);
          setFilteredAccounts(combinedAccounts);
          calculateStats(combinedAccounts);
          setError(null);
        }
      } else {
        console.error('👥 ❌ 계정 정보 API 실패');
        setError('계정 정보 조회 실패');
        if (isMountedRef.current) {
          setAllAccounts([]);
          setFilteredAccounts([]);
        }
      }
    } catch (error) {
      console.error('👥 ❌ 계정 정보 조회 네트워크 오류:', error);
      setError('계정 정보 조회 중 네트워크 오류: ' + error.message);
      if (isMountedRef.current) {
        setAllAccounts([]);
        setFilteredAccounts([]);
      }
    } finally {
      if (isMountedRef.current) {
        setLoading(false);
      }
    }
  };

  const calculateStats = (accountData) => {
    const total = accountData.length;
    const pending = accountData.filter(acc => acc.accountType === 'pending').length;
    const approved = accountData.filter(acc => acc.accountType === 'approved').length;
    const rejected = accountData.filter(acc => acc.accountType === 'rejected').length;
    const active = accountData.filter(acc => acc.isActive === true).length;
    const inactive = accountData.filter(acc => acc.isActive === false && acc.accountType === 'approved').length;
    
    setStats({ total, active, inactive, pending, approved, rejected });
    console.log('📊 통계 계산:', { total, active, inactive, pending, approved, rejected });
  };

  // 승인 처리
  const handleApprove = async (requestId, buttonElement) => {
    if (!isMountedRef.current) return;
    
    try {
      console.log('✅ 승인 처리 시작:', requestId);
      
      if (buttonElement) {
        buttonElement.innerHTML = '처리중...';
        buttonElement.disabled = true;
      }
      
      setProcessingIds(prev => new Set([...prev, requestId]));
      
      const response = await fetch(`http://localhost:5480/api/admin/approve/${requestId}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          notes: '관리자 승인 완료'
        }),
      });

      const result = await response.json();
      console.log('✅ 승인 API 응답:', result);
      
      if (response.ok && result.success) {
        setTimeout(() => {
          if (buttonElement) {
            const parentCard = buttonElement.closest('.bg-white');
            if (parentCard) {
              parentCard.style.opacity = '0.5';
            }
            buttonElement.innerHTML = '승인완료';
            buttonElement.className = 'px-3 py-1 bg-green-600 text-white text-xs rounded-md';
          }
        }, 1500);
        
        alert(`계정 승인 완료!\n사용자 ID: ${result.data.userId}\n임시 비밀번호: ${result.data.tempPassword}`);
        await fetchAllAccounts();
      } else {
        console.error('✅ 승인 실패:', result);
        alert(`승인 실패: ${result.message}`);
        if (buttonElement) {
          buttonElement.innerHTML = '승인';
          buttonElement.disabled = false;
        }
      }
    } catch (error) {
      console.error('✅ 승인 처리 실패:', error);
      alert('승인 처리 중 오류가 발생했습니다.');
      if (buttonElement) {
        buttonElement.innerHTML = '승인';
        buttonElement.disabled = false;
      }
    } finally {
      setProcessingIds(prev => {
        const newSet = new Set(prev);
        newSet.delete(requestId);
        return newSet;
      });
    }
  };

  // 거부 처리
  const handleReject = async (requestId, buttonElement) => {
    if (!isMountedRef.current) return;
    
    try {
      const notes = prompt('거부 사유를 입력해주세요:', '서류 미비');
      if (!notes) return;

      console.log('❌ 거부 처리 시작:', requestId);

      if (buttonElement) {
        buttonElement.innerHTML = '처리중...';
        buttonElement.disabled = true;
      }

      setProcessingIds(prev => new Set([...prev, requestId]));

      const response = await fetch(`http://localhost:5480/api/admin/reject/${requestId}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ notes }),
      });

      const result = await response.json();
      console.log('❌ 거부 API 응답:', result);

      if (response.ok && result.success) {
        setTimeout(() => {
          if (buttonElement) {
            const parentCard = buttonElement.closest('.bg-white');
            if (parentCard) {
              parentCard.style.opacity = '0.5';
            }
            buttonElement.innerHTML = '반려완료';
            buttonElement.className = 'px-3 py-1 bg-red-500 text-white text-xs rounded-md';
          }
        }, 1500);

        alert('신청이 거부되었습니다.');
        await fetchAllAccounts();
      } else {
        console.error('❌ 거부 실패:', result);
        alert(`거부 실패: ${result.message}`);
        if (buttonElement) {
          buttonElement.innerHTML = '반려';
          buttonElement.disabled = false;
        }
      }
    } catch (error) {
      console.error('❌ 거부 처리 실패:', error);
      alert('거부 처리 중 오류가 발생했습니다.');
      if (buttonElement) {
        buttonElement.innerHTML = '반려';
        buttonElement.disabled = false;
      }
    } finally {
      setProcessingIds(prev => {
        const newSet = new Set(prev);
        newSet.delete(requestId);
        return newSet;
      });
    }
  };

  // 계정 활성화/비활성화 처리
  const handleStatusToggle = async (userId, currentStatus, buttonElement) => {
    if (!isMountedRef.current) return;
    
    try {
      const newStatus = !currentStatus;
      console.log(`🔄 계정 상태 변경 시작: ${userId}, ${currentStatus} -> ${newStatus}`);
      
      if (buttonElement) {
        buttonElement.innerHTML = '처리중...';
        buttonElement.disabled = true;
      }
      
      setProcessingIds(prev => new Set([...prev, userId]));
      
      const response = await fetch(`http://localhost:5480/api/admin/users/${userId}/status`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          isActive: newStatus,
          notes: `관리자에 의한 ${newStatus ? '활성화' : '비활성화'}`
        }),
      });

      const result = await response.json();
      console.log('🔄 상태 변경 API 응답:', result);
      
      if (response.ok && result.success) {
        setTimeout(() => {
          if (buttonElement) {
            const parentCard = buttonElement.closest('.bg-white');
            const statusBadge = parentCard.querySelector('span[class*="rounded-full"]');
            
            if (newStatus) {
              buttonElement.innerHTML = '비활성화';
              buttonElement.className = 'px-4 py-2 bg-red-100 text-red-600 text-sm rounded-lg hover:bg-red-200 transition-colors';
              if (statusBadge) {
                statusBadge.className = 'px-2 py-1 bg-green-100 text-green-600 text-xs rounded-full';
                statusBadge.textContent = '활성';
              }
            } else {
              buttonElement.innerHTML = '활성화';
              buttonElement.className = 'px-4 py-2 bg-green-100 text-green-600 text-sm rounded-lg hover:bg-green-200 transition-colors';
              if (statusBadge) {
                statusBadge.className = 'px-2 py-1 bg-gray-100 text-gray-600 text-xs rounded-full';
                statusBadge.textContent = '비활성';
              }
            }
            buttonElement.disabled = false;
          }
        }, 1500);
        
        await fetchAllAccounts();
      } else {
        console.error('🔄 상태 변경 실패:', result);
        alert(`상태 변경 실패: ${result.message}`);
        if (buttonElement) {
          buttonElement.innerHTML = currentStatus ? '비활성화' : '활성화';
          buttonElement.disabled = false;
        }
      }
    } catch (error) {
      console.error('🔄 상태 변경 중 오류:', error);
      alert('상태 변경 중 오류가 발생했습니다.');
      if (buttonElement) {
        buttonElement.innerHTML = currentStatus ? '비활성화' : '활성화';
        buttonElement.disabled = false;
      }
    } finally {
      setProcessingIds(prev => {
        const newSet = new Set(prev);
        newSet.delete(userId);
        return newSet;
      });
    }
  };

  const handleSearch = (term) => {
    setSearchTerm(term);
    filterAccounts(term, activeFilter);
  };

  const handleFilterChange = (filter) => {
    setActiveFilter(filter);
    filterAccounts(searchTerm, filter);
  };

  const filterAccounts = (searchTerm, filter) => {
    let filtered = [...allAccounts];
    
    // 검색 필터링
    if (searchTerm.trim()) {
      filtered = filtered.filter(account => 
        account.managerName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        account.storeCode.toLowerCase().includes(searchTerm.toLowerCase()) ||
        (account.userId && account.userId.toLowerCase().includes(searchTerm.toLowerCase()))
      );
    }
    
    // 상태 필터링
    if (filter === '승인대기') {
      filtered = filtered.filter(account => account.accountType === 'pending');
    } else if (filter === '활성') {
      filtered = filtered.filter(account => account.isActive === true);
    } else if (filter === '비활성') {
      filtered = filtered.filter(account => account.isActive === false && account.accountType === 'approved');
    } else if (filter === '거부됨') {
      filtered = filtered.filter(account => account.accountType === 'rejected');
    }
    
    setFilteredAccounts(filtered);
    console.log('🔍 필터링 결과:', { searchTerm, filter, count: filtered.length });
  };

  const getTimeAgo = (dateString) => {
    try {
      const now = new Date();
      const date = new Date(dateString);
      const diffMs = now - date;
      const diffMins = Math.floor(diffMs / 60000);
      const diffHours = Math.floor(diffMs / 3600000);
      const diffDays = Math.floor(diffMs / 86400000);

      if (diffMins < 1) return '방금 전';
      if (diffMins < 60) return `${diffMins}분 전`;
      if (diffHours < 24) return `${diffHours}시간 전`;
      return `${diffDays}일 전`;
    } catch (error) {
      return '알 수 없음';
    }
  };

  const getStoreIcon = (storeCode) => {
    const hash = storeCode.split('').reduce((a, b) => {
      a = ((a << 5) - a) + b.charCodeAt(0);
      return a & a;
    }, 0);
    
    const colors = [
      'bg-green-100 text-green-600',
      'bg-blue-100 text-blue-600',
      'bg-orange-100 text-orange-600',
      'bg-purple-100 text-purple-600',
      'bg-indigo-100 text-indigo-600',
      'bg-pink-100 text-pink-600',
      'bg-yellow-100 text-yellow-600',
      'bg-red-100 text-red-600'
    ];
    
    return colors[Math.abs(hash) % colors.length];
  };

  const getStatusBadge = (account) => {
    switch (account.accountType) {
      case 'pending':
        return 'px-2 py-1 bg-yellow-100 text-yellow-600 text-xs rounded-full';
      case 'approved':
        return account.isActive 
          ? 'px-2 py-1 bg-green-100 text-green-600 text-xs rounded-full'
          : 'px-2 py-1 bg-gray-100 text-gray-600 text-xs rounded-full';
      case 'rejected':
        return 'px-2 py-1 bg-red-100 text-red-600 text-xs rounded-full';
      default:
        return 'px-2 py-1 bg-gray-100 text-gray-600 text-xs rounded-full';
    }
  };

  const getStatusText = (account) => {
    switch (account.accountType) {
      case 'pending':
        return '승인대기';
      case 'approved':
        return account.isActive ? '활성' : '비활성';
      case 'rejected':
        return '거부됨';
      default:
        return '알 수 없음';
    }
  };

  const renderActionButtons = (account) => {
    const accountId = account._id || account.userId;
    const isProcessing = processingIds.has(accountId);

    if (account.accountType === 'pending') {
      // 승인 대기 중인 계정 - 승인/거부 버튼
      return (
        <div className="flex gap-2">
          <button 
            className="px-3 py-1 bg-green-500 text-white text-xs rounded-md hover:bg-green-600 transition-colors"
            onClick={(e) => handleApprove(accountId, e.target)}
            disabled={isProcessing}
          >
            {isProcessing ? '처리중...' : '승인'}
          </button>
          <button 
            className="px-3 py-1 bg-gray-300 text-gray-600 text-xs rounded-md hover:bg-gray-400 transition-colors"
            onClick={(e) => handleReject(accountId, e.target)}
            disabled={isProcessing}
          >
            {isProcessing ? '처리중...' : '반려'}
          </button>
        </div>
      );
    } else if (account.accountType === 'approved' && account.type === 'user') {
      // 승인된 계정 - 활성화/비활성화 버튼
      return (
        <div className="flex gap-2">
          <button className="flex-1 py-2 bg-gray-100 text-gray-600 text-sm rounded-lg hover:bg-gray-200 transition-colors">
            상세보기
          </button>
          <button 
            className={`px-4 py-2 text-sm rounded-lg transition-colors ${
              account.isActive
                ? 'bg-red-100 text-red-600 hover:bg-red-200'
                : 'bg-green-100 text-green-600 hover:bg-green-200'
            }`}
            onClick={(e) => handleStatusToggle(accountId, account.isActive, e.target)}
            disabled={isProcessing}
          >
            {isProcessing ? '처리중...' : (account.isActive ? '비활성화' : '활성화')}
          </button>
        </div>
      );
    } else {
      // 거부된 계정 또는 기타 - 상세보기만
      return (
        <button className="w-full py-2 bg-gray-100 text-gray-600 text-sm rounded-lg hover:bg-gray-200 transition-colors">
          상세보기
        </button>
      );
    }
  };

  // 컴포넌트 마운트 시 데이터 로드
  useEffect(() => {
    console.log('🚀 계정 관리 컴포넌트 마운트 - 데이터 로드 시작');
    isMountedRef.current = true;
    
    fetchAllAccounts();

    intervalRef.current = setInterval(() => {
      if (isMountedRef.current) {
        console.log('🔄 자동 새로고침 실행');
        fetchAllAccounts();
      }
    }, 60000);

    return () => {
      console.log('🧹 계정 관리 컴포넌트 언마운트 - 정리 작업');
      isMountedRef.current = false;
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, []);

  return (
    <div className="bg-gray-100 font-sans">
      <div id="mobile-container" className="w-full max-w-sm mx-auto bg-white shadow-lg min-h-screen">
        
        {/* 뒤로가기 버튼 */}
        {onBackToAdmin && (
          <div className="fixed top-4 left-4 z-50">
            <button
              onClick={onBackToAdmin}
              className="bg-blue-500 text-white px-3 py-2 rounded-lg text-sm hover:bg-blue-600 transition-colors shadow-lg"
              title="신청관리로 돌아가기"
            >
              ← 신청관리
            </button>
          </div>
        )}

        {/* 에러 표시 (개발용) */}
        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded m-4">
            <strong className="font-bold">오류: </strong>
            <span className="block sm:inline">{error}</span>
          </div>
        )}

        {/* 디버깅 정보 (개발용) */}
        <div className="bg-blue-100 p-2 text-xs text-blue-800 m-4 rounded">
          <div>👥 Accounts: 전체 {allAccounts.length}개, 필터링 {filteredAccounts.length}개</div>
          <div>📊 Stats: 대기 {stats.pending}, 활성 {stats.active}, 비활성 {stats.inactive}, 거부 {stats.rejected}</div>
          <div>🔍 Filter: "{searchTerm}" / {activeFilter}</div>
          {loading && <div>⏳ 로딩 중...</div>}
        </div>
        
        {/* Header */}
        <header id="header" className="bg-gradient-to-br from-orange-400 via-orange-500 to-orange-500 text-white p-4 pt-12">
          <div className="flex items-center justify-between mb-6">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 bg-white/20 rounded-lg flex items-center justify-center">
                <svg className="w-5 h-5 text-white" fill="currentColor" viewBox="0 0 640 512">
                  <path d="M224 256A128 128 0 1 0 224 0a128 128 0 1 0 0 256zm-45.7 48C79.8 304 0 383.8 0 482.3C0 498.7 13.3 512 29.7 512H418.3c16.4 0 29.7-13.3 29.7-29.7C448 383.8 368.2 304 269.7 304H178.3zM504 312v-64h64c13.3 0 24-10.7 24-24s-10.7-24-24-24h-64v-64c0-13.3-10.7-24-24-24s-24 10.7-24 24v64h-64c-13.3 0-24 10.7-24 24s10.7 24 24 24h64v64c0 13.3 10.7 24 24 24s24-10.7 24-24z"/>
                </svg>
              </div>
              <div>
                <h1 className="text-lg font-bold">계정 관리</h1>
                <p className="text-white/80 text-xs">전체 계정 관리</p>
              </div>
            </div>
            <div className="flex items-center gap-2">
              <button className="w-8 h-8 bg-white/20 rounded-lg flex items-center justify-center">
                <svg className="w-4 h-4 text-white" fill="currentColor" viewBox="0 0 512 512">
                  <path d="M416 208c0 45.9-14.9 88.3-40 122.7L502.6 457.4c12.5 12.5 12.5 32.8 0 45.3s-32.8 12.5-45.3 0L330.7 376c-34.4 25.2-76.8 40-122.7 40C93.1 416 0 322.9 0 208S93.1 0 208 0S416 93.1 416 208zM208 352a144 144 0 1 0 0-288 144 144 0 1 0 0 288z"/>
                </svg>
              </button>
              <img 
                src="https://storage.googleapis.com/uxpilot-auth.appspot.com/avatars/avatar-2.jpg" 
                className="w-8 h-8 rounded-lg"
                alt="Admin Avatar"
              />
            </div>
          </div>
          
          {/* Account Stats - 업데이트된 통계 */}
          <div id="account-stats" className="grid grid-cols-4 gap-2">
            <div className="bg-white/10 rounded-xl p-2">
              <div className="text-white/80 text-xs mb-1">승인대기</div>
              <div className="text-sm font-bold text-white">{stats.pending}</div>
            </div>
            <div className="bg-white/10 rounded-xl p-2">
              <div className="text-white/80 text-xs mb-1">활성</div>
              <div className="text-sm font-bold text-white">{stats.active}</div>
            </div>
            <div className="bg-white/10 rounded-xl p-2">
              <div className="text-white/80 text-xs mb-1">비활성</div>
              <div className="text-sm font-bold text-white">{stats.inactive}</div>
            </div>
            <div className="bg-white/10 rounded-xl p-2">
              <div className="text-white/80 text-xs mb-1">거부됨</div>
              <div className="text-sm font-bold text-white">{stats.rejected}</div>
            </div>
          </div>
        </header>

        {/* Main Content */}
        <main id="main-content" className="p-4 pb-20">
          {/* Search & Filter */}
          <div id="search-filter" className="mb-4">
            <div className="relative mb-3">
              <input 
                type="text" 
                placeholder="계정명, 사업자번호로 검색" 
                className="w-full pl-10 pr-4 py-3 bg-gray-50 border border-gray-200 rounded-xl text-sm focus:outline-none focus:ring-2 focus:ring-orange-500 focus:border-orange-500"
                value={searchTerm}
                onChange={(e) => handleSearch(e.target.value)}
              />
              <svg className="w-4 h-4 text-gray-400 absolute left-3 top-1/2 transform -translate-y-1/2" fill="currentColor" viewBox="0 0 512 512">
                <path d="M416 208c0 45.9-14.9 88.3-40 122.7L502.6 457.4c12.5 12.5 12.5 32.8 0 45.3s-32.8 12.5-45.3 0L330.7 376c-34.4 25.2-76.8 40-122.7 40C93.1 416 0 322.9 0 208S93.1 0 208 0S416 93.1 416 208zM208 352a144 144 0 1 0 0-288 144 144 0 1 0 0 288z"/>
              </svg>
            </div>
            <div className="flex gap-1 overflow-x-auto">
              {['전체', '승인대기', '활성', '비활성', '거부됨'].map(filter => (
                <button
                  key={filter}
                  className={`px-3 py-2 text-xs rounded-lg whitespace-nowrap transition-colors ${
                    activeFilter === filter 
                      ? 'bg-orange-500 text-white' 
                      : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                  }`}
                  onClick={() => handleFilterChange(filter)}
                >
                  {filter}
                </button>
              ))}
            </div>
          </div>

          {/* Account List */}
          <div id="account-list" className="space-y-3">
            {loading ? (
              <div className="text-center py-8 text-gray-500 text-sm">
                ⏳ 계정 목록을 불러오는 중...
              </div>
            ) : filteredAccounts.length > 0 ? filteredAccounts.map((account, index) => (
              <div key={account._id || account.userId || index} className="bg-white rounded-xl p-4 shadow-sm border">
                <div className="flex items-start justify-between mb-3">
                  <div className="flex items-center gap-3">
                    <div className={`w-12 h-12 rounded-xl flex items-center justify-center ${getStoreIcon(account.storeCode)}`}>
                      <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 576 512">
                        <path d="M547.6 103.8L490.3 13.1C485.2 5 476.1 0 466.4 0H109.6C99.9 0 90.8 5 85.7 13.1L28.3 103.8c-29.6 46.8-3.4 111.9 51.9 119.4c4 .5 8.1 .8 12.1 .8c26.1 0 49.3-11.4 65.2-29c15.9 17.6 39.1 29 65.2 29c26.1 0 49.3-11.4 65.2-29c15.9 17.6 39.1 29 65.2 29c26.2 0 49.3-11.4 65.2-29c16 17.6 39.1 29 65.2 29c4.1 0 8.1-.3 12.1-.8c55.5-7.4 81.8-72.5 52.1-119.4zM499.7 254.9l-.1 0c-5.3 .7-10.7 1.1-16.2 1.1c-12.4 0-24.3-1.9-35.4-5.3V384H128V250.6c-11.2 3.5-23.2 5.4-35.6 5.4c-5.5 0-11-.4-16.3-1.1l-.1 0c-4.1-.6-8.1-1.3-12-2.3V384v64c0 35.3 28.7 64 64 64H448c35.3 0 64-28.7 64-64V384 252.6c-4 1-8 1.8-12.3 2.3z"/>
                      </svg>
                    </div>
                    <div>
                      <h3 className="font-semibold text-gray-800">{account.storeCode}</h3>
                      <p className="text-sm text-gray-500">{account.managerName}</p>
                      <p className="text-xs text-gray-400">
                        {account.userId || `연락처: ****${account.phoneLast4}`}
                      </p>
                    </div>
                  </div>
                  <span className={getStatusBadge(account)}>
                    {getStatusText(account)}
                  </span>
                </div>

                <div className="grid grid-cols-2 gap-4 text-xs text-gray-500 mb-3">
                  <div>
                    <span className="block">신청일</span>
                    <span className="text-gray-800">
                      {account.createdAt ? new Date(account.createdAt).toLocaleDateString('ko-KR') : '알 수 없음'}
                    </span>
                  </div>
                  <div>
                    <span className="block">
                      {account.accountType === 'approved' && account.lastLogin ? '최근 접속' : '처리일'}
                    </span>
                    <span className="text-gray-800">
                      {account.accountType === 'approved' && account.lastLogin 
                        ? getTimeAgo(account.lastLogin)
                        : account.processedDate 
                          ? new Date(account.processedDate).toLocaleDateString('ko-KR')
                          : '처리 대기'}
                    </span>
                  </div>
                </div>

                {/* 거부 사유 표시 */}
                {account.accountType === 'rejected' && account.notes && (
                  <div className="bg-red-50 p-3 rounded-lg mb-3">
                    <div className="text-xs text-red-600 font-medium">거부 사유</div>
                    <div className="text-sm text-red-800">{account.notes}</div>
                  </div>
                )}

                {/* 승인된 계정 정보 표시 */}
                {account.accountType === 'approved' && account.generatedUserId && (
                  <div className="bg-green-50 p-3 rounded-lg mb-3">
                    <div className="text-xs text-green-600 font-medium">생성된 계정 정보</div>
                    <div className="text-sm text-green-800">ID: {account.generatedUserId}</div>
                  </div>
                )}

                {/* 액션 버튼 */}
                {renderActionButtons(account)}
              </div>
            )) : (
              <div className="text-center py-8 text-gray-500">
                <div className="text-sm">조건에 맞는 계정이 없습니다.</div>
                <small className="text-gray-400 mt-1 block">검색어나 필터를 확인해보세요</small>
              </div>
            )}
          </div>
        </main>

        {/* Bottom Navigation */}
        <nav id="bottom-nav" className="fixed bottom-0 left-1/2 transform -translate-x-1/2 w-full max-w-sm bg-white border-t border-gray-200 p-4">
          <div className="grid grid-cols-3 gap-1">
            <button 
              className="flex flex-col items-center gap-1 py-2 text-gray-400 hover:text-gray-600 transition-colors"
              onClick={() => {
                if (onBackToAdmin) {
                  onBackToAdmin();
                }
              }}
            >
              <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 576 512">
                <path d="M575.8 255.5c0 18-15 32.1-32 32.1h-32l.7 160.2c0 2.7-.2 5.4-.5 8.1V472c0 22.1-17.9 40-40 40H456c-1.1 0-2.2 0-3.3-.1c-1.4 .1-2.8 .1-4.2 .1H416 392c-22.1 0-40-17.9-40-40V448 384c0-17.7-14.3-32-32-32H256c-17.7 0-32 14.3-32 32v64 24c0 22.1-17.9 40-40 40H160 128.1c-1.5 0-3-.1-4.5-.2c-1.2 .1-2.4 .2-3.6 .2H104c-22.1 0-40-17.9-40-40V360c0-.9 0-1.9 .1-2.8V287.6H32c-18 0-32-14-32-32.1c0-9 3-17 10-24L266.4 8c7-7 15-8 22-8s15 2 21 7L564.8 231.5c8 7 12 15 11 24z"/>
              </svg>
              <span className="text-xs">홈</span>
            </button>
            <button className="flex flex-col items-center gap-1 py-2 text-orange-500">
              <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 640 512">
                <path d="M96 128a128 128 0 1 1 256 0A128 128 0 1 1 96 128zM0 482.3C0 383.8 79.8 304 178.3 304h91.4C368.2 304 448 383.8 448 482.3c0 16.4-13.3 29.7-29.7 29.7H29.7C13.3 512 0 498.7 0 482.3zM504 312V248H440c-13.3 0-24-10.7-24-24s10.7-24 24-24h64V136c0-13.3 10.7-24 24-24s24 10.7 24 24v64h64c13.3 0 24 10.7 24 24s-10.7 24-24 24H552v64c0 13.3-10.7 24-24 24s-24-10.7-24-24z"/>
              </svg>
              <span className="text-xs">계정관리</span>
            </button>
            <button className="flex flex-col items-center gap-1 py-2 text-gray-400 hover:text-gray-600 transition-colors">
              <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 512 512">
                <path d="M495.9 166.6c3.2 8.7 .5 18.4-6.4 24.6l-43.3 39.4c1.1 8.3 1.7 16.8 1.7 25.4s-.6 17.1-1.7 25.4l43.3 39.4c6.9 6.2 9.6 15.9 6.4 24.6c-4.4 11.9-9.7 23.3-15.8 34.3l-4.7 8.1c-6.6 11-14 21.4-22.1 31.2c-5.9 7.2-15.7 9.6-24.5 6.8l-55.7-17.7c-13.4 10.3-28.2 18.9-44 25.4l-12.5 57.1c-2 9.1-9 16.3-18.2 17.8c-13.8 2.3-28 3.5-42.5 3.5s-28.7-1.2-42.5-3.5c-9.2-1.5-16.2-8.7-18.2-17.8l-12.5-57.1c-15.8-6.5-30.6-15.1-44-25.4L83.1 425.9c-8.8 2.8-18.6 .3-24.5-6.8c-8.1-9.8-15.5-20.2-22.1-31.2l-4.7-8.1c-6.1-11-11.4-22.4-15.8-34.3c-3.2-8.7-.5-18.4 6.4-24.6l43.3-39.4C64.6 273.1 64 264.6 64 256s.6-17.1 1.7-25.4L22.4 191.2c-6.9-6.2-9.6-15.9-6.4-24.6c4.4-11.9 9.7-23.3 15.8-34.3l4.7-8.1c6.6-11 14-21.4 22.1-31.2c5.9-7.2 15.7-9.6 24.5-6.8l55.7 17.7c13.4-10.3 28.2-18.9 44-25.4l12.5-57.1c2-9.1 9-16.3 18.2-17.8C227.3 1.2 241.5 0 256 0s28.7 1.2 42.5 3.5c9.2 1.5 16.2 8.7 18.2 17.8l12.5 57.1c15.8 6.5 30.6 15.1 44 25.4l55.7-17.7c8.8-2.8 18.6-.3 24.5 6.8c8.1 9.8 15.5 20.2 22.1 31.2l4.7 8.1c6.1 11 11.4 22.4 15.8 34.3zM256 336a80 80 0 1 0 0-160 80 80 0 1 0 0 160z"/>
              </svg>
              <span className="text-xs">설정</span>
            </button>
          </div>
        </nav>
      </div>
    </div>
  );
};

export default AccountManage;
