import React, { useState } from 'react';

const AccountIssue = ({ isOpen, onClose }) => {
  const [formData, setFormData] = useState({
    managerName: '',
    storeCode: '',
    phoneLast4: ''
  });
  const [showResult, setShowResult] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [requestResult, setRequestResult] = useState(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // ÏûÖÎ†• Îç∞Ïù¥ÌÑ∞ Í≤ÄÏ¶ù Ìï®Ïàò
  const validateFormData = () => {
    // ÌïÑÏàò ÌïÑÎìú Í≤ÄÏ¶ù
    if (!formData.managerName.trim()) {
      alert('Îã¥ÎãπÏûêÎ™ÖÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return false;
    }

    if (!formData.storeCode.trim()) {
      alert('Îß§Ïû•ÏΩîÎìúÎ•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return false;
    }

    if (!formData.phoneLast4.trim()) {
      alert('Ïó∞ÎùΩÏ≤ò Îí∑ 4ÏûêÎ¶¨Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return false;
    }

    // Îã¥ÎãπÏûêÎ™Ö Í≤ÄÏ¶ù (ÌïúÍ∏Ä, ÏòÅÎ¨∏Îßå ÌóàÏö©, 2-10Ïûê)
    const nameRegex = /^[Í∞Ä-Ìû£a-zA-Z\s]{2,10}$/;
    if (!nameRegex.test(formData.managerName.trim())) {
      alert('Îã¥ÎãπÏûêÎ™ÖÏùÄ ÌïúÍ∏Ä ÎòêÎäî ÏòÅÎ¨∏ÏúºÎ°ú 2-10Ïûê Ïù¥ÎÇ¥Î°ú ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return false;
    }

    // Îß§Ïû•ÏΩîÎìú Í≤ÄÏ¶ù (ÏòÅÎ¨∏ ÎåÄÎ¨∏ÏûêÏôÄ Ïà´ÏûêÎßå ÌóàÏö©, 3-20Ïûê)
    const storeCodeRegex = /^[A-Z0-9]{3,20}$/;
    if (!storeCodeRegex.test(formData.storeCode.trim().toUpperCase())) {
      alert('Îß§Ïû•ÏΩîÎìúÎäî ÏòÅÎ¨∏ ÎåÄÎ¨∏ÏûêÏôÄ Ïà´ÏûêÎ°ú 3-20Ïûê Ïù¥ÎÇ¥Î°ú ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return false;
    }

    // Ïó∞ÎùΩÏ≤ò Îí∑ 4ÏûêÎ¶¨ Í≤ÄÏ¶ù
    if (formData.phoneLast4.length !== 4 || !/^\d{4}$/.test(formData.phoneLast4)) {
      alert('Ïó∞ÎùΩÏ≤ò Îí∑ 4ÏûêÎ¶¨Î•º Ï†ïÌôïÌûà ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî. (Ïà´Ïûê 4ÏûêÎ¶¨)');
      return false;
    }

    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // ÏûÖÎ†• Í≤ÄÏ¶ù
    if (!validateFormData()) {
      return;
    }

    setIsLoading(true);

    try {
      console.log('üîÑ Í≥ÑÏ†ï Î∞úÍ∏â Ïã†Ï≤≠ ÏãúÏûë:', formData);

      // ÏÑúÎ≤Ñ ÏÉÅÌÉú ÌôïÏù∏
      const healthResponse = await fetch('https://quinors-lv-backend.ngrok.io/api/health');
      if (!healthResponse.ok) {
        throw new Error('ÏÑúÎ≤ÑÏóê Ïó∞Í≤∞Ìï† Ïàò ÏóÜÏäµÎãàÎã§. Ïû†Ïãú ÌõÑ Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.');
      }

      // Í≥ÑÏ†ï Î∞úÍ∏â Ïã†Ï≤≠
      const response = await fetch('https://quinors-lv-backend.ngrok.io/api/account-requests', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
        },
        body: JSON.stringify({
          managerName: formData.managerName.trim(),
          storeCode: formData.storeCode.trim().toUpperCase(),
          phoneLast4: formData.phoneLast4.trim()
        }),
      });

      console.log('üì® ÏÑúÎ≤Ñ ÏùëÎãµ ÏÉÅÌÉú:', response.status);

      // ÏùëÎãµÏù¥ JSONÏù∏ÏßÄ ÌôïÏù∏
      const contentType = response.headers.get('content-type');
      if (!contentType || !contentType.includes('application/json')) {
        throw new Error('ÏÑúÎ≤ÑÏóêÏÑú Ïò¨Î∞îÎ•¥ÏßÄ ÏïäÏùÄ ÏùëÎãµÏùÑ Î∞õÏïòÏäµÎãàÎã§.');
      }

      const result = await response.json();
      console.log('üì® ÏÑúÎ≤Ñ ÏùëÎãµ Îç∞Ïù¥ÌÑ∞:', result);

      if (result.success) {
        // ÏûêÎèô ÏäπÏù∏Îêú Í≤ΩÏö∞
        if (result.data.status === 'auto_approved') {
          setRequestResult({
            ...formData,
            requestId: result.data.requestId,
            requestDate: new Date(result.data.requestDate).toLocaleString('ko-KR'),
            status: 'auto_approved',
            userId: result.data.userId,
            tempPassword: result.data.tempPassword,
            storeName: result.data.storeName,
            processType: result.data.processType
          });
        } else {
          // ÏàòÎèô ÏäπÏù∏ ÎåÄÍ∏∞ ÏÉÅÌÉú
          setRequestResult({
            ...formData,
            requestId: result.data.requestId,
            requestDate: new Date(result.data.requestDate).toLocaleString('ko-KR'),
            status: 'pending'
          });
        }
        setShowResult(true);
      } else {
        // ÏûêÎèô Í±∞Î∂ÄÎêú Í≤ΩÏö∞ ÎòêÎäî Í∏∞ÌÉÄ Ïò§Î•ò
        console.error('‚ùå Ïã†Ï≤≠ Ïã§Ìå®:', result);
        
        if (result.data && result.data.status === 'rejected') {
          setRequestResult({
            ...formData,
            requestId: result.data.requestId,
            requestDate: new Date(result.data.requestDate).toLocaleString('ko-KR'),
            status: 'rejected',
            reason: result.data.reason || result.message
          });
          setShowResult(true);
        } else {
          alert(result.message || 'Ïã†Ï≤≠ Ï≤òÎ¶¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
        }
      }
    } catch (error) {
      console.error('‚ùå Ïã†Ï≤≠ Ïò§Î•ò:', error);
      
      // Íµ¨Ï≤¥Ï†ÅÏù∏ ÏóêÎü¨ Î©îÏãúÏßÄ Ï†úÍ≥µ
      if (error.message.includes('Failed to fetch') || error.message.includes('ERR_CONNECTION_REFUSED')) {
        alert('ÏÑúÎ≤ÑÏóê Ïó∞Í≤∞Ìï† Ïàò ÏóÜÏäµÎãàÎã§.\n\n‚Ä¢ Ïù∏ÌÑ∞ÎÑ∑ Ïó∞Í≤∞ÏùÑ ÌôïÏù∏Ìï¥Ï£ºÏÑ∏Ïöî\n‚Ä¢ ÏÑúÎ≤ÑÍ∞Ä Ï†ïÏÉÅ ÏûëÎèô Ï§ëÏù∏ÏßÄ ÌôïÏù∏Ìï¥Ï£ºÏÑ∏Ïöî\n‚Ä¢ Ïû†Ïãú ÌõÑ Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî');
      } else if (error.message.includes('JSON')) {
        alert('ÏÑúÎ≤Ñ ÏùëÎãµ Ï≤òÎ¶¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.\nÍ≥†Í∞ùÏÑºÌÑ∞Ïóê Î¨∏ÏùòÌï¥Ï£ºÏÑ∏Ïöî.');
      } else {
        alert(`Ïã†Ï≤≠ Ï≤òÎ¶¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.\n\nÏò§Î•ò: ${error.message}`);
      }
    } finally {
      setIsLoading(false);
    }
  };

  const resetForm = () => {
    setFormData({
      managerName: '',
      storeCode: '',
      phoneLast4: ''
    });
    setShowResult(false);
    setRequestResult(null);
  };

  const handleClose = () => {
    resetForm();
    onClose();
  };

  const handlePhoneInput = (e) => {
    const value = e.target.value.replace(/\D/g, '').slice(0, 4);
    setFormData(prev => ({
      ...prev,
      phoneLast4: value
    }));
  };

  // Îß§Ïû•ÏΩîÎìú ÏûÖÎ†• Ï≤òÎ¶¨ (ÎåÄÎ¨∏Ïûê Î≥ÄÌôò)
  const handleStoreCodeInput = (e) => {
    const value = e.target.value.toUpperCase().replace(/[^A-Z0-9]/g, '').slice(0, 20);
    setFormData(prev => ({
      ...prev,
      storeCode: value
    }));
  };

  // Îã¥ÎãπÏûêÎ™Ö ÏûÖÎ†• Ï≤òÎ¶¨ (ÌïúÍ∏Ä, ÏòÅÎ¨∏, Í≥µÎ∞±Îßå ÌóàÏö©)
  const handleManagerNameInput = (e) => {
    const value = e.target.value.replace(/[^Í∞Ä-Ìû£a-zA-Z\s]/g, '').slice(0, 10);
    setFormData(prev => ({
      ...prev,
      managerName: value
    }));
  };

  // Í≤∞Í≥º ÏÉÅÌÉúÏóê Îî∞Î•∏ Ïä§ÌÉÄÏùº Î∞è Î©îÏãúÏßÄ
  const getResultStyle = () => {
    switch (requestResult?.status) {
      case 'auto_approved':
        return {
          bgColor: 'bg-green-50',
          borderColor: 'border-green-200',
          iconColor: 'text-green-500',
          titleColor: 'text-green-900',
          statusColor: 'text-green-600',
          statusText: 'ÏûêÎèô ÏäπÏù∏ ÏôÑÎ£å'
        };
      case 'rejected':
        return {
          bgColor: 'bg-red-50',
          borderColor: 'border-red-200',
          iconColor: 'text-red-500',
          titleColor: 'text-red-900',
          statusColor: 'text-red-600',
          statusText: 'Ïã†Ï≤≠ Í±∞Î∂Ä'
        };
      default:
        return {
          bgColor: 'bg-blue-50',
          borderColor: 'border-blue-200',
          iconColor: 'text-blue-500',
          titleColor: 'text-blue-900',
          statusColor: 'text-yellow-600',
          statusText: 'ÏäπÏù∏ ÎåÄÍ∏∞Ï§ë'
        };
    }
  };

  if (!isOpen) return null;

  const resultStyle = getResultStyle();

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-3xl w-full max-w-sm max-h-[90vh] overflow-hidden flex flex-col animate-in fade-in slide-in-from-bottom-4 duration-300">
        
        {/* Header */}
        <header className="bg-white border-b border-gray-200 px-4 py-3 flex-shrink-0">
          <div className="flex items-center justify-between">
            <button 
              onClick={handleClose}
              className="p-2 -ml-2 hover:bg-gray-100 rounded-full transition-colors"
            >
              <svg className="w-5 h-5 text-gray-600" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M12.707 5.293a1 1 0 010 1.414L9.414 10l3.293 3.293a1 1 0 01-1.414 1.414l-4-4a1 1 0 010-1.414l4-4a1 1 0 011.414 0z" clipRule="evenodd" />
              </svg>
            </button>
            <h1 className="text-lg font-semibold text-gray-900">Í≥ÑÏ†ï Î∞úÍ∏â</h1>
            <button 
              onClick={handleClose}
              className="p-2 -mr-2 hover:bg-gray-100 rounded-full transition-colors"
            >
              <svg className="w-5 h-5 text-gray-600" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd" />
              </svg>
            </button>
          </div>
        </header>

        {/* Main Content */}
        <main className="flex-1 overflow-y-auto">
          <div className="px-4 py-6">
            
            {/* Info Section */}
            <section className="mb-6">
              <div className="bg-orange-50 border border-orange-200 rounded-lg p-4 mb-4">
                <div className="flex items-start">
                  <svg className="w-5 h-5 text-orange-500 mt-0.5 mr-3 flex-shrink-0" fill="currentColor" viewBox="0 0 20 20">
                    <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7-4a1 1 0 11-2 0 1 1 0 012 0zM9 9a1 1 0 000 2v3a1 1 0 001 1h1a1 1 0 100-2v-3a1 1 0 00-1-1H9z" clipRule="evenodd" />
                  </svg>
                  <div>
                    <h3 className="font-medium text-orange-900 mb-1">Í≥ÑÏ†ï Î∞úÍ∏â Í¥ÄÎ†®</h3>
                    <p className="text-sm text-orange-700">
                      {!showResult ? 
                        'Îì±Î°ùÎêú Îß§Ïû• Ï†ïÎ≥¥ÏôÄ ÏùºÏπòÌïòÎäî Í≤ΩÏö∞ Ï¶âÏãú Í≥ÑÏ†ïÏù¥ Î∞úÍ∏âÎê©ÎãàÎã§. Î∂àÏùºÏπò Ïãú Í¥ÄÎ¶¨Ïûê ÏäπÏù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.' :
                        'Ïã†Ï≤≠Ïù¥ ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§. ÏïÑÎûò Í≤∞Í≥ºÎ•º ÌôïÏù∏Ìï¥Ï£ºÏÑ∏Ïöî.'
                      }
                    </p>
                  </div>
                </div>
              </div>
            </section>

            {!showResult ? (
              <>
                {/* Form Section */}
                <section className="mb-6">
                  <form onSubmit={handleSubmit} className="space-y-4">
                    <div className="form-group">
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        Îã¥ÎãπÏûêÎ™Ö <span className="text-red-500">*</span>
                      </label>
                      <input 
                        type="text" 
                        name="managerName"
                        value={formData.managerName}
                        onChange={handleManagerNameInput}
                        className="w-full px-3 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent outline-none transition-all" 
                        placeholder="Îã¥ÎãπÏûê Ïù¥Î¶Ñ (ÌïúÍ∏Ä/ÏòÅÎ¨∏, 2-10Ïûê)"
                        required
                        disabled={isLoading}
                        maxLength="10"
                      />
                      <small className="text-gray-500 text-xs mt-1">ÌïúÍ∏Ä ÎòêÎäî ÏòÅÎ¨∏ÏúºÎ°ú ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî</small>
                    </div>
                    
                    <div className="form-group">
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        Îß§Ïû•ÏΩîÎìú <span className="text-red-500">*</span>
                      </label>
                      <input 
                        type="text" 
                        name="storeCode"
                        value={formData.storeCode}
                        onChange={handleStoreCodeInput}
                        className="w-full px-3 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent outline-none transition-all" 
                        placeholder="Îß§Ïû•ÏΩîÎìú (ÏòÅÎ¨∏ ÎåÄÎ¨∏Ïûê/Ïà´Ïûê, 3-20Ïûê)"
                        required
                        disabled={isLoading}
                        maxLength="20"
                      />
                      <small className="text-gray-500 text-xs mt-1">ÏòÅÎ¨∏ ÎåÄÎ¨∏ÏûêÏôÄ Ïà´ÏûêÎßå ÏûÖÎ†• Í∞ÄÎä•Ìï©ÎãàÎã§</small>
                    </div>
                    
                    <div className="form-group">
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        Ïó∞ÎùΩÏ≤ò Îí∑ 4ÏûêÎ¶¨ <span className="text-red-500">*</span>
                      </label>
                      <input 
                        type="text" 
                        name="phoneLast4"
                        value={formData.phoneLast4}
                        onChange={handlePhoneInput}
                        maxLength="4"
                        className="w-full px-3 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent outline-none transition-all" 
                        placeholder="0000"
                        required
                        disabled={isLoading}
                      />
                      <small className="text-gray-500 text-xs mt-1">Îì±Î°ùÎêú Ïó∞ÎùΩÏ≤òÏùò Îí∑ 4ÏûêÎ¶¨Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî</small>
                    </div>
                  </form>
                </section>

                {/* Button Section */}
                <section className="mb-6">
                  <button 
                    onClick={handleSubmit}
                    disabled={isLoading}
                    className="w-full bg-gradient-to-r from-orange-400 to-orange-500 text-white py-4 rounded-lg font-medium text-lg hover:from-orange-500 hover:to-orange-600 transition-all duration-200 shadow-md disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    {isLoading ? (
                      <div className="flex items-center justify-center">
                        <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                          <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                          <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                        </svg>
                        Ï≤òÎ¶¨ Ï§ë...
                      </div>
                    ) : (
                      'Î∞úÍ∏â Ïã†Ï≤≠ÌïòÍ∏∞'
                    )}
                  </button>
                </section>
              </>
            ) : (
              /* Result Section */
              <section className="mb-6">
                <div className={`${resultStyle.bgColor} ${resultStyle.borderColor} border rounded-lg p-4 mb-4`}>
                  <div className="flex items-center mb-3">
                    {requestResult?.status === 'auto_approved' ? (
                      <svg className={`w-6 h-6 ${resultStyle.iconColor} mr-2`} fill="currentColor" viewBox="0 0 20 20">
                        <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                      </svg>
                    ) : requestResult?.status === 'rejected' ? (
                      <svg className={`w-6 h-6 ${resultStyle.iconColor} mr-2`} fill="currentColor" viewBox="0 0 20 20">
                        <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                      </svg>
                    ) : (
                      <svg className={`w-6 h-6 ${resultStyle.iconColor} mr-2`} fill="currentColor" viewBox="0 0 20 20">
                        <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm1-12a1 1 0 10-2 0v4a1 1 0 00.293.707l2.828 2.829a1 1 0 101.415-1.415L11 9.586V6z" clipRule="evenodd" />
                      </svg>
                    )}
                    <h3 className={`font-semibold ${resultStyle.titleColor}`}>
                      {requestResult?.status === 'auto_approved' ? 'Í≥ÑÏ†ï Î∞úÍ∏â ÏôÑÎ£å!' :
                       requestResult?.status === 'rejected' ? 'Ïã†Ï≤≠Ïù¥ Í±∞Î∂ÄÎêòÏóàÏäµÎãàÎã§' :
                       'Ïã†Ï≤≠Ïù¥ ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§'}
                    </h3>
                  </div>
                  
                  <div className="space-y-3">
                    <div className="flex justify-between items-center py-2 border-b border-gray-200">
                      <span className="text-sm font-medium text-gray-600">Îã¥ÎãπÏûêÎ™Ö</span>
                      <span className="text-sm font-semibold text-gray-900">{requestResult?.managerName}</span>
                    </div>
                    <div className="flex justify-between items-center py-2 border-b border-gray-200">
                      <span className="text-sm font-medium text-gray-600">Îß§Ïû•ÏΩîÎìú</span>
                      <span className="text-sm font-semibold text-gray-900">{requestResult?.storeCode}</span>
                    </div>
                    <div className="flex justify-between items-center py-2 border-b border-gray-200">
                      <span className="text-sm font-medium text-gray-600">Ïó∞ÎùΩÏ≤ò Îí∑ 4ÏûêÎ¶¨</span>
                      <span className="text-sm font-semibold text-gray-900">{requestResult?.phoneLast4}</span>
                    </div>
                    <div className="flex justify-between items-center py-2 border-b border-gray-200">
                      <span className="text-sm font-medium text-gray-600">Ïã†Ï≤≠ÏùºÏãú</span>
                      <span className="text-sm font-semibold text-gray-900">{requestResult?.requestDate}</span>
                    </div>
                    <div className="flex justify-between items-center py-2">
                      <span className="text-sm font-medium text-gray-600">Ï≤òÎ¶¨ÏÉÅÌÉú</span>
                      <span className={`text-sm font-semibold ${resultStyle.statusColor}`}>{resultStyle.statusText}</span>
                    </div>

                    {/* ÏûêÎèô ÏäπÏù∏Îêú Í≤ΩÏö∞ Í≥ÑÏ†ï Ï†ïÎ≥¥ ÌëúÏãú */}
                    {requestResult?.status === 'auto_approved' && (
                      <>
                        <div className="border-t border-gray-200 pt-3 mt-3">
                          <h4 className="text-sm font-medium text-gray-700 mb-2">Î∞úÍ∏âÎêú Í≥ÑÏ†ï Ï†ïÎ≥¥</h4>
                          <div className="bg-white rounded-lg p-3 space-y-2">
                            <div className="flex justify-between items-center">
                              <span className="text-sm font-medium text-gray-600">ÏÇ¨Ïö©Ïûê ID</span>
                              <span className="text-sm font-bold text-blue-600">{requestResult?.userId}</span>
                            </div>
                            <div className="flex justify-between items-center">
                              <span className="text-sm font-medium text-gray-600">ÏûÑÏãú ÎπÑÎ∞ÄÎ≤àÌò∏</span>
                              <span className="text-sm font-bold text-red-600">{requestResult?.tempPassword}</span>
                            </div>
                            <div className="flex justify-between items-center">
                              <span className="text-sm font-medium text-gray-600">Îß§Ïû•Î™Ö</span>
                              <span className="text-sm font-semibold text-gray-900">{requestResult?.storeName}</span>
                            </div>
                          </div>
                        </div>
                      </>
                    )}

                    {/* Í±∞Î∂ÄÎêú Í≤ΩÏö∞ ÏÇ¨Ïú† ÌëúÏãú */}
                    {requestResult?.status === 'rejected' && requestResult?.reason && (
                      <div className="border-t border-gray-200 pt-3 mt-3">
                        <h4 className="text-sm font-medium text-gray-700 mb-2">Í±∞Î∂Ä ÏÇ¨Ïú†</h4>
                        <div className="bg-white rounded-lg p-3">
                          <p className="text-sm text-red-600">{requestResult.reason}</p>
                        </div>
                      </div>
                    )}
                  </div>
                  
                  {/* ÏÉÅÌÉúÎ≥Ñ ÏïàÎÇ¥ Î©îÏãúÏßÄ */}
                  <div className={`mt-4 p-3 rounded-lg ${
                    requestResult?.status === 'auto_approved' ? 'bg-green-50' :
                    requestResult?.status === 'rejected' ? 'bg-red-50' : 'bg-yellow-50'
                  }`}>
                    <p className={`text-xs ${
                      requestResult?.status === 'auto_approved' ? 'text-green-800' :
                      requestResult?.status === 'rejected' ? 'text-red-800' : 'text-yellow-800'
                    }`}>
                      {requestResult?.status === 'auto_approved' ? 
                        'Í≥ÑÏ†ïÏù¥ ÏûêÎèôÏúºÎ°ú Î∞úÍ∏âÎêòÏóàÏäµÎãàÎã§. Î∞úÍ∏âÎêú Í≥ÑÏ†ï Ï†ïÎ≥¥Î•º ÏïàÏ†ÑÌïòÍ≤å Î≥¥Í¥ÄÌïòÏãúÍ≥†, ÏµúÏ¥à Î°úÍ∑∏Ïù∏ ÌõÑ ÎπÑÎ∞ÄÎ≤àÌò∏Î•º Î≥ÄÍ≤ΩÌï¥Ï£ºÏÑ∏Ïöî.' :
                        requestResult?.status === 'rejected' ?
                        'ÏûÖÎ†•ÌïòÏã† Ï†ïÎ≥¥Í∞Ä Îì±Î°ùÎêú Îß§Ïû• Ï†ïÎ≥¥ÏôÄ ÏùºÏπòÌïòÏßÄ ÏïäÏäµÎãàÎã§. Ï†ïÎ≥¥Î•º Îã§Ïãú ÌôïÏù∏ÌïòÏãúÍ±∞ÎÇò Í¥ÄÎ¶¨ÏûêÏóêÍ≤å Î¨∏ÏùòÌï¥Ï£ºÏÑ∏Ïöî.' :
                        'Í¥ÄÎ¶¨Ïûê ÏäπÏù∏ ÌõÑ Í≥ÑÏ†ï Ï†ïÎ≥¥Í∞Ä Î∞úÍ∏âÎê©ÎãàÎã§. ÏäπÏù∏ÍπåÏßÄ 1-2Ïùº Ï†ïÎèÑ ÏÜåÏöîÎê† Ïàò ÏûàÏäµÎãàÎã§.'
                      }
                    </p>
                  </div>
                </div>
                
                <div className="flex space-x-3">
                  <button 
                    onClick={resetForm}
                    className="flex-1 bg-gray-100 text-gray-700 py-3 rounded-lg font-medium hover:bg-gray-200 transition-colors"
                  >
                    ÏÉàÎ°ú Ïã†Ï≤≠ÌïòÍ∏∞
                  </button>
                  <button 
                    onClick={handleClose}
                    className="flex-1 bg-gradient-to-r from-orange-400 to-orange-500 text-white py-3 rounded-lg font-medium hover:from-orange-500 hover:to-orange-600 transition-all duration-200"
                  >
                    ÌôïÏù∏
                  </button>
                </div>
              </section>
            )}
          </div>
        </main>
      </div>
    </div>
  );
};

export default AccountIssue;
