import React from 'react';

const FloatingPreview = ({ content, onEdit, show = true }) => {
  if (!show) return null;

  return (
    <div className="fixed bottom-20 left-4 right-4 max-w-sm mx-auto">
      <div className="bg-white/95 backdrop-blur-sm p-3 rounded-xl border border-orange-500/20 shadow-lg">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-2">
            <div className="w-3 h-3 rounded-full bg-gradient-to-br from-orange-400 to-orange-500 flex items-center justify-center">
              <i className="fa-solid fa-check text-white text-[6px]"></i>
            </div>
            <span className="text-xs text-gray-700 font-medium">
              {content}
            </span>
          </div>
          <button 
            onClick={onEdit}
            className="text-gray-400 hover:text-orange-500 transition-colors p-1"
          >
            <i className="fa-solid fa-edit text-[8px]"></i>
          </button>
        </div>
      </div>
    </div>
  );
};

export default FloatingPreview;
