import React, { useState, useEffect, useRef } from 'react';

const AdminPanel = () => {
  const [stats, setStats] = useState({
    requests: { total: 0, pending: 0, approved: 0, rejected: 0 },
    users: { total: 0, active: 0, inactive: 0 }
  });
  const [requests, setRequests] = useState([]);
  const [loading, setLoading] = useState(false);
  const [processingIds, setProcessingIds] = useState(new Set());
  const [recentActivities, setRecentActivities] = useState([]);
  
  // 컴포넌트 마운트 상태 추적
  const isMountedRef = useRef(true);
  const intervalRef = useRef(null);

  // 안전한 상태 업데이트 함수
  const safeSetState = (setter) => {
    if (isMountedRef.current) {
      setter();
    }
  };

  // API 호출 함수들
  const fetchStats = async () => {
    try {
      const response = await fetch('http://localhost:5480/api/admin/stats');
      const result = await response.json();
      if (result.success && isMountedRef.current) {
        safeSetState(() => setStats(result.data));
      }
    } catch (error) {
      console.error('통계 조회 실패:', error);
    }
  };

  const fetchRequests = async (status = 'all') => {
    try {
      safeSetState(() => setLoading(true));
      const response = await fetch(`http://localhost:5480/api/admin/requests?status=${status}`);
      const result = await response.json();
      if (result.success && isMountedRef.current) {
        safeSetState(() => {
          setRequests(result.data);
          updateRecentActivities(result.data);
        });
      }
    } catch (error) {
      console.error('신청 목록 조회 실패:', error);
    } finally {
      if (isMountedRef.current) {
        safeSetState(() => setLoading(false));
      }
    }
  };

  const updateRecentActivities = (requestsData) => {
    if (!isMountedRef.current) return;
    
    const activities = requestsData
      .filter(req => req.processedDate)
      .sort((a, b) => new Date(b.processedDate) - new Date(a.processedDate))
      .slice(0, 5)
      .map(req => ({
        id: req._id,
        name: req.managerName,
        store: req.storeCode,
        status: req.status,
        time: getTimeAgo(req.processedDate),
        notes: req.notes
      }));
    
    safeSetState(() => setRecentActivities(activities));
  };

  const getTimeAgo = (dateString) => {
    const now = new Date();
    const date = new Date(dateString);
    const diffMs = now - date;
    const diffMins = Math.floor(diffMs / 60000);
    const diffHours = Math.floor(diffMs / 3600000);
    const diffDays = Math.floor(diffMs / 86400000);

    if (diffMins < 1) return '방금 전';
    if (diffMins < 60) return `${diffMins}분 전`;
    if (diffHours < 24) return `${diffHours}시간 전`;
    return `${diffDays}일 전`;
  };

  const handleApprove = async (requestId) => {
    if (!isMountedRef.current) return;
    
    try {
      safeSetState(() => setProcessingIds(prev => new Set([...prev, requestId])));
      
      const response = await fetch(`http://localhost:5480/api/admin/approve/${requestId}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          notes: '관리자 승인 완료'
        }),
      });

      const result = await response.json();
      if (result.success && isMountedRef.current) {
        alert(`계정 승인 완료!\n사용자 ID: ${result.data.userId}\n임시 비밀번호: ${result.data.tempPassword}`);
        await Promise.all([fetchRequests(), fetchStats()]);
      } else {
        alert(`승인 실패: ${result.message}`);
      }
    } catch (error) {
      console.error('승인 처리 실패:', error);
      alert('승인 처리 중 오류가 발생했습니다.');
    } finally {
      if (isMountedRef.current) {
        safeSetState(() => setProcessingIds(prev => {
          const newSet = new Set(prev);
          newSet.delete(requestId);
          return newSet;
        }));
      }
    }
  };

  const handleReject = async (requestId) => {
    if (!isMountedRef.current) return;
    
    try {
      safeSetState(() => setProcessingIds(prev => new Set([...prev, requestId])));
      
      const notes = prompt('거부 사유를 입력해주세요:', '서류 미비');
      if (!notes) return;

      const response = await fetch(`http://localhost:5480/api/admin/reject/${requestId}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ notes }),
      });

      const result = await response.json();
      if (result.success && isMountedRef.current) {
        alert('신청이 거부되었습니다.');
        await Promise.all([fetchRequests(), fetchStats()]);
      } else {
        alert(`거부 실패: ${result.message}`);
      }
    } catch (error) {
      console.error('거부 처리 실패:', error);
      alert('거부 처리 중 오류가 발생했습니다.');
    } finally {
      if (isMountedRef.current) {
        safeSetState(() => setProcessingIds(prev => {
          const newSet = new Set(prev);
          newSet.delete(requestId);
          return newSet;
        }));
      }
    }
  };

  // 컴포넌트 마운트 시 데이터 로드
  useEffect(() => {
    fetchStats();
    fetchRequests();

    // 자동 새로고침 설정
    intervalRef.current = setInterval(() => {
      if (isMountedRef.current) {
        fetchStats();
        fetchRequests();
      }
    }, 10000); // 10초마다

    // Cleanup 함수
    return () => {
      isMountedRef.current = false;
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, []);

  // 오늘 신청 수 계산
  const todayRequests = requests.filter(req => {
    const today = new Date().toDateString();
    const requestDate = new Date(req.createdAt).toDateString();
    return today === requestDate;
  }).length;

  // 대기 중인 신청들
  const pendingRequests = requests.filter(req => req.status === 'pending');

  return (
    <div className="bg-gray-100 font-sans">
      <div className="w-full max-w-sm mx-auto bg-white shadow-lg min-h-screen">
        
        {/* Header */}
        <header className="bg-gradient-to-br from-orange-400 via-orange-500 to-orange-500 text-white p-4 pt-12">
          <div className="flex items-center justify-between mb-6">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 bg-white/20 rounded-lg flex items-center justify-center">
                <div className="w-6 h-6 bg-white/80 rounded border-2 border-white/60"></div>
              </div>
              <div>
                <h1 className="text-lg font-bold">관리자 대시보드</h1>
                <p className="text-white/80 text-xs">계정 신청 관리</p>
              </div>
            </div>
            <div className="flex items-center gap-2">
              <button className="w-8 h-8 bg-white/20 rounded-lg flex items-center justify-center">
                <div className="w-3 h-3 bg-white rounded-full"></div>
              </button>
              <img 
                src="https://storage.googleapis.com/uxpilot-auth.appspot.com/avatars/avatar-2.jpg" 
                className="w-8 h-8 rounded-lg"
                alt="Admin Avatar"
              />
            </div>
          </div>
          
          {/* Header Stats */}
          <div className="grid grid-cols-2 gap-3">
            <div className="bg-white/10 rounded-xl p-4">
              <div className="text-white/80 text-xs mb-1">신청 계정</div>
              <div className="text-xl font-bold text-white">{stats.requests.total}</div>
              <div className="text-green-300 text-xs">+{todayRequests} 이번주</div>
            </div>
            <div className="bg-white/10 rounded-xl p-4">
              <div className="text-white/80 text-xs mb-1">승인 대기</div>
              <div className="text-xl font-bold text-white">{stats.requests.pending}</div>
              <div className="text-orange-300 text-xs">검토 필요</div>
            </div>
          </div>
        </header>

        {/* Main Content */}
        <main className="p-4 pb-20">
          <div className="mb-6">
            <h2 className="text-lg font-bold text-gray-800 mb-4">계정 신청 관리</h2>
            
            {/* Application Stats */}
            <div className="grid grid-cols-2 gap-4 mb-6">
              <div className="bg-white rounded-xl p-4 shadow-sm border">
                <div className="text-gray-500 text-sm mb-1">신규 신청</div>
                <div className="text-2xl font-bold text-gray-800">{stats.requests.pending}</div>
                <div className="text-orange-500 text-sm">+{todayRequests} 오늘</div>
              </div>
              <div className="bg-white rounded-xl p-4 shadow-sm border">
                <div className="text-gray-500 text-sm mb-1">승인 완료</div>
                <div className="text-2xl font-bold text-gray-800">{stats.requests.approved}</div>
                <div className="text-green-500 text-sm">전체</div>
              </div>
            </div>

            {/* Pending Applications */}
            <div className="bg-white rounded-xl p-4 shadow-sm border mb-6">
              <div className="flex items-center justify-between mb-3">
                <h3 className="font-semibold text-gray-800">승인 대기 계정</h3>
                <button 
                  onClick={() => fetchRequests()}
                  className="text-orange-500 text-sm"
                  disabled={loading}
                >
                  {loading ? '로딩...' : '전체보기'}
                </button>
              </div>
              
              <div className="space-y-3">
                {pendingRequests.length > 0 ? pendingRequests.slice(0, 3).map((request) => (
                  <div key={request._id} className="flex items-center justify-between p-3 bg-orange-50 rounded-lg">
                    <div className="flex items-center gap-3">
                      <div className="w-10 h-10 bg-orange-100 rounded-lg flex items-center justify-center">
                        <div className="w-5 h-5 bg-orange-500 rounded-full flex items-center justify-center">
                          <div className="text-white text-xs font-bold">+</div>
                        </div>
                      </div>
                      <div>
                        <div className="font-medium text-gray-800">{request.managerName}</div>
                        <div className="text-xs text-gray-500">
                          {request.storeCode} · 연락처: ****{request.phoneLast4}
                        </div>
                      </div>
                    </div>
                    <div className="flex gap-2">
                      <button
                        onClick={() => handleApprove(request._id)}
                        disabled={processingIds.has(request._id)}
                        className="px-3 py-1 bg-green-500 text-white text-xs rounded-md hover:bg-green-600 transition-colors disabled:opacity-50"
                      >
                        {processingIds.has(request._id) ? '처리중...' : '승인'}
                      </button>
                      <button
                        onClick={() => handleReject(request._id)}
                        disabled={processingIds.has(request._id)}
                        className="px-3 py-1 bg-gray-300 text-gray-600 text-xs rounded-md hover:bg-gray-400 transition-colors disabled:opacity-50"
                      >
                        {processingIds.has(request._id) ? '처리중...' : '반려'}
                      </button>
                    </div>
                  </div>
                )) : (
                  <div className="text-center py-4 text-gray-500 text-sm">
                    승인 대기 중인 계정이 없습니다.
                  </div>
                )}
              </div>
            </div>

            {/* Recent Applications */}
            <div className="bg-white rounded-xl p-4 shadow-sm border">
              <h3 className="font-semibold text-gray-800 mb-3">최근 처리된 계정</h3>
              <div className="space-y-3">
                {recentActivities.length > 0 ? recentActivities.map((activity, index) => (
                  <div key={`${activity.id}-${index}`} className="flex items-center gap-3">
                    <div className={`w-8 h-8 rounded-full flex items-center justify-center ${
                      activity.status === 'approved' ? 'bg-green-50' : 'bg-red-50'
                    }`}>
                      <div className={`w-3 h-3 flex items-center justify-center text-xs font-bold ${
                        activity.status === 'approved' ? 'text-green-500' : 'text-red-500'
                      }`}>
                        {activity.status === 'approved' ? '✓' : '✕'}
                      </div>
                    </div>
                    <div className="flex-1">
                      <div className="text-sm font-medium text-gray-800">
                        {activity.name} ({activity.store})
                      </div>
                      <div className="text-xs text-gray-500">
                        {activity.status === 'approved' ? '계정 승인 완료' : 
                         activity.notes ? `${activity.notes}로 반려` : '계정 반려'} - {activity.time}
                      </div>
                    </div>
                  </div>
                )) : (
                  <div className="text-center py-4 text-gray-500 text-sm">
                    최근 처리된 계정이 없습니다.
                  </div>
                )}
              </div>
            </div>
          </div>
        </main>

        {/* Bottom Navigation */}
        <nav className="fixed bottom-0 left-1/2 transform -translate-x-1/2 w-full max-w-sm bg-white border-t border-gray-200 p-4">
          <div className="grid grid-cols-3 gap-1">
            <button className="flex flex-col items-center gap-1 py-2 text-orange-500">
              <div className="w-5 h-5 bg-orange-500 rounded"></div>
              <span className="text-xs">홈</span>
            </button>
            <button className="flex flex-col items-center gap-1 py-2 text-gray-400">
              <div className="w-5 h-5 bg-gray-400 rounded-full flex items-center justify-center">
                <div className="text-white text-xs font-bold">+</div>
              </div>
              <span className="text-xs">계정관리</span>
            </button>
            <button className="flex flex-col items-center gap-1 py-2 text-gray-400">
              <div className="w-5 h-5 bg-gray-400 rounded"></div>
              <span className="text-xs">설정</span>
            </button>
          </div>
        </nav>
      </div>
    </div>
  );
};

export default AdminPanel;
