import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import AccountIssue from '../AccountMD/AccountIssue';
import WelcomePopup from '../Popup/WelcomePopup';

const Login = ({ onLogin, onNavigateToRegister, onNavigateToIdRequest }) => {
  const { login, error, loading, clearError } = useAuth();
  
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    rememberMe: false
  });
  const [showPassword, setShowPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [showAccountIssue, setShowAccountIssue] = useState(false);
  const [loginError, setLoginError] = useState('');
  // ÌåùÏóÖ ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [showWelcomePopup, setShowWelcomePopup] = useState(false);
  // üéØ Í≥ÑÏ†ï Î∞úÍ∏â ÏÑ±Í≥µ ÏïåÎ¶º ÏÉÅÌÉú
  const [showSuccessMessage, setShowSuccessMessage] = useState(false);
  const [successAccountInfo, setSuccessAccountInfo] = useState(null);

  // üéØ Î°úÏª¨Ïä§ÌÜ†Î¶¨ÏßÄÏóêÏÑú Ï†ÄÏû•Îêú Í≥ÑÏ†ï Ï†ïÎ≥¥ Î∂àÎü¨Ïò§Í∏∞
  useEffect(() => {
    const savedAccount = localStorage.getItem('quinors_account');
    if (savedAccount) {
      try {
        const parsedAccount = JSON.parse(savedAccount);
        setFormData(prev => ({
          ...prev,
          username: parsedAccount.username || '',
          password: parsedAccount.password || ''
        }));
      } catch (error) {
        console.error('Ï†ÄÏû•Îêú Í≥ÑÏ†ï Ï†ïÎ≥¥ Î°úÎìú Ïã§Ìå®:', error);
        localStorage.removeItem('quinors_account');
      }
    }
  }, []);

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
    
    // ÏûÖÎ†• Ïãú ÏóêÎü¨ Î©îÏãúÏßÄ Ï¥àÍ∏∞Ìôî
    if (loginError) {
      setLoginError('');
    }
    if (error) {
      clearError();
    }
  };

  const validateFormData = () => {
    if (!formData.username.trim()) {
      setLoginError('ÏÇ¨Ïö©Ïûê IDÎ•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return false;
    }
    if (!formData.password.trim()) {
      setLoginError('ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return false;
    }
    if (formData.username.length < 6) {
      setLoginError('ÏÇ¨Ïö©Ïûê IDÎäî 6ÏûêÎ¶¨ Ïù¥ÏÉÅÏù¥Ïñ¥Ïïº Ìï©ÎãàÎã§.');
      return false;
    }
    if (formData.password.length < 6) {
      setLoginError('ÎπÑÎ∞ÄÎ≤àÌò∏Îäî 6ÏûêÎ¶¨ Ïù¥ÏÉÅÏù¥Ïñ¥Ïïº Ìï©ÎãàÎã§.');
      return false;
    }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateFormData()) {
      return;
    }

    setIsLoading(true);
    setLoginError('');
    try {
      await login(formData);
      console.log('‚úÖ Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ!');
      
      if (onLogin) {
        onLogin(formData);
      }
    } catch (error) {
      console.error('‚ùå Î°úÍ∑∏Ïù∏ Ïã§Ìå®:', error);
      setLoginError(error.message || 'Î°úÍ∑∏Ïù∏Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    } finally {
      setIsLoading(false);
    }
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const handleAccountIssue = () => {
    setShowAccountIssue(true);
  };

  const handleCloseAccountIssue = () => {
    setShowAccountIssue(false);
  };

  // üéØ Í≥ÑÏ†ï Î∞úÍ∏â ÏÑ±Í≥µ Ïãú Ìò∏Ï∂úÎêòÎäî Ìï®Ïàò
  const handleAccountIssued = (accountInfo) => {
    // Î°úÍ∑∏Ïù∏ ÌèºÏóê ÏûêÎèô ÏûÖÎ†•
    setFormData(prev => ({
      ...prev,
      username: accountInfo.username,
      password: accountInfo.password
    }));

    // Î°úÏª¨Ïä§ÌÜ†Î¶¨ÏßÄÏóê Ï†ÄÏû•
    const accountData = {
      username: accountInfo.username,
      password: accountInfo.password,
      storeName: accountInfo.storeName,
      managerName: accountInfo.managerName,
      issuedAt: new Date().toISOString()
    };
    
    localStorage.setItem('quinors_account', JSON.stringify(accountData));

    // ÏÑ±Í≥µ Î©îÏãúÏßÄ ÌëúÏãú
    setSuccessAccountInfo(accountInfo);
    setShowSuccessMessage(true);

    // 3Ï¥à ÌõÑ ÏÑ±Í≥µ Î©îÏãúÏßÄ ÏûêÎèô Ïà®ÍπÄ
    setTimeout(() => {
      setShowSuccessMessage(false);
    }, 5000);

    console.log('‚úÖ Í≥ÑÏ†ï Î∞úÍ∏â ÏôÑÎ£å - ÏûêÎèô ÏûÖÎ†•:', accountInfo);
  };

  // ÌåùÏóÖ Ï†úÏñ¥ Ìï®ÏàòÎì§
  const handleCloseWelcome = () => {
    setShowWelcomePopup(false);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSubmit(e);
    }
  };

  // ÌéòÏù¥ÏßÄ Î°úÎìú Ïãú ÌåùÏóÖ ÌëúÏãú Ï≤¥ÌÅ¨
  useEffect(() => {
    const hideUntil = localStorage.getItem('hideWelcomeUntil');
    const today = new Date().toDateString();
    
    if (hideUntil !== today) {
      const timer = setTimeout(() => {
        setShowWelcomePopup(true);
      }, 500);
      
      return () => clearTimeout(timer);
    }
  }, []);

  const currentError = loginError || error;

  return (
    <>
      <div className="bg-gray-100 font-sans flex items-center justify-center min-h-screen p-4">
        <div className="w-full max-w-sm mx-auto bg-white shadow-lg rounded-3xl overflow-hidden max-h-[95vh] flex flex-col">
          <header className="bg-gradient-to-br from-orange-400 via-orange-500 to-orange-500 text-white p-6 pt-12 pb-6 flex-shrink-0">
            <div className="text-center">
              <div className="mx-auto mb-4 flex items-center justify-center h-16">
                <img
                  src="/Login.png"
                  alt="ÌÄ¥ÎÖ∏Ïä§ Î°úÍ≥†"
                  className="w-14 h-14 object-contain"
                  style={{
                    filter: 'brightness(0) invert(1) drop-shadow(0 2px 6px rgba(0,0,0,0.1))'
                  }}
                  onError={(e) => {
                    console.log('Ïù¥ÎØ∏ÏßÄ Î°úÎìú Ïã§Ìå®: /Login.png');
                    e.target.style.display = 'none';
                    e.target.nextSibling.style.display = 'block';
                  }}
                  onLoad={() => {
                    console.log('Ïù¥ÎØ∏ÏßÄ Î°úÎìú ÏÑ±Í≥µ: /Login.png');
                  }}
                />
                <svg
                  className="w-9 h-9 text-white hidden"
                  fill="currentColor"
                  viewBox="0 0 24 24"
                  style={{ display: 'none' }}
                >
                  <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-2 15l-5-5 1.41-1.41L10 14.17l7.59-7.59L19 8l-9 9z" />
                </svg>
              </div>
              <h1 className="text-2xl font-bold mb-2">ÌÄ¥ÎÖ∏Ïä§ ÎπÑÏ¶àÎãàÏä§</h1>
              <p className="text-white/80 text-sm">Î£®Ïù¥ÎπÑÌÜµ Îã¥ÎãπÏûêÏö©</p>
            </div>
          </header>

          <main className="flex-1 overflow-y-auto p-6">
            {/* üéØ Í≥ÑÏ†ï Î∞úÍ∏â ÏÑ±Í≥µ Î©îÏãúÏßÄ */}
            {showSuccessMessage && successAccountInfo && (
              <div className="mb-4 bg-green-50 border border-green-200 rounded-lg p-4 animate-in fade-in slide-in-from-top-2 duration-300">
                <div className="flex items-start">
                  <svg className="w-5 h-5 text-green-500 mt-0.5 mr-3 flex-shrink-0" fill="currentColor" viewBox="0 0 20 20">
                    <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                  </svg>
                  <div>
                    <p className="text-sm font-medium text-green-800">Í≥ÑÏ†ï Î∞úÍ∏â ÏôÑÎ£å!</p>
                    <p className="text-xs text-green-600 mt-1">
                      IDÏôÄ ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä ÏûêÎèôÏúºÎ°ú ÏûÖÎ†•ÎêòÏóàÏäµÎãàÎã§. Î°úÍ∑∏Ïù∏ Î≤ÑÌäºÏùÑ ÌÅ¥Î¶≠ÌïòÏÑ∏Ïöî.
                    </p>
                    <p className="text-xs text-green-500 mt-1">
                      Îß§Ïû•: {successAccountInfo.storeName} | Îã¥ÎãπÏûê: {successAccountInfo.managerName}
                    </p>
                  </div>
                  <button
                    onClick={() => setShowSuccessMessage(false)}
                    className="ml-auto text-green-400 hover:text-green-600"
                  >
                    <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
                      <path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd" />
                    </svg>
                  </button>
                </div>
              </div>
            )}

            {/* Error Message */}
            {currentError && (
              <div className="mb-4 bg-red-50 border border-red-200 rounded-lg p-3">
                <div className="flex items-start">
                  <svg className="w-5 h-5 text-red-500 mt-0.5 mr-2 flex-shrink-0" fill="currentColor" viewBox="0 0 20 20">
                    <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                  </svg>
                  <p className="text-sm text-red-700 whitespace-pre-line">{currentError}</p>
                </div>
              </div>
            )}

            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <label htmlFor="username" className="block text-sm font-medium text-gray-700 mb-2">
                  ÏÇ¨Ïö©Ïûê ID <span className="text-red-500">*</span>
                </label>
                <input
                  id="username"
                  type="text"
                  name="username"
                  value={formData.username}
                  onChange={handleInputChange}
                  onKeyPress={handleKeyPress}
                  className="w-full px-4 py-3.5 border border-gray-300 rounded-xl focus:ring-2 focus:ring-orange-500 focus:border-orange-500 outline-none transition-all"
                  placeholder="Î∞úÍ∏âÎ∞õÏùÄ ÏÇ¨Ïö©Ïûê IDÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
                  required
                  disabled={isLoading || loading}
                  autoComplete="username"
                />
                <small className="text-gray-500 text-xs mt-1">6ÏûêÎ¶¨ Ïù¥ÏÉÅÏùò ÎåÄÎ¨∏Ïûê, Ïà´Ïûê, Ïñ∏ÎçîÏä§ÏΩîÏñ¥</small>
              </div>

              <div>
                <label htmlFor="password" className="block text-sm font-medium text-gray-700 mb-2">
                  ÎπÑÎ∞ÄÎ≤àÌò∏ <span className="text-red-500">*</span>
                </label>
                <div className="relative">
                  <input
                    id="password"
                    type={showPassword ? "text" : "password"}
                    name="password"
                    value={formData.password}
                    onChange={handleInputChange}
                    onKeyPress={handleKeyPress}
                    className="w-full px-4 py-3.5 pr-12 border border-gray-300 rounded-xl focus:ring-2 focus:ring-orange-500 focus:border-orange-500 outline-none transition-all"
                    placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
                    required
                    disabled={isLoading || loading}
                    autoComplete="current-password"
                  />
                  <button
                    type="button"
                    onClick={togglePasswordVisibility}
                    className="absolute inset-y-0 right-0 pr-3 flex items-center"
                    disabled={isLoading || loading}
                  >
                    {showPassword ? (
                      <svg className="w-5 h-5 text-gray-400" fill="currentColor" viewBox="0 0 20 20">
                        <path fillRule="evenodd" d="M3.707 2.293a1 1 0 00-1.414 1.414l14 14a1 1 0 001.414-1.414l-1.473-1.473A10.014 10.014 0 0019.542 10C18.268 5.943 14.478 3 10 3a9.958 9.958 0 00-4.512 1.074l-1.78-1.781zm4.261 4.26l1.514 1.515a2.003 2.003 0 012.45 2.45l1.514 1.514a4 4 0 00-5.478-5.478z" clipRule="evenodd" />
                        <path d="M12.454 16.697L9.75 13.992a4 4 0 01-3.742-3.741L2.335 6.578A9.98 9.98 0 00.458 10c1.274 4.057 5.065 7 9.542 7 .847 0 1.669-.105 2.454-.303z" />
                      </svg>
                    ) : (
                      <svg className="w-5 h-5 text-gray-400" fill="currentColor" viewBox="0 0 20 20">
                        <path d="M10 12a2 2 0 100-4 2 2 0 000 4z" />
