import React, { useState, useEffect } from 'react';
import { useDelivery } from '../../context/DeliveryContext';
import { useAuth } from '../../context/AuthContext';
import Header from '../common/Header';
import FloatingPreview from '../common/FloatingPreview';
import Button from '../ui/Button';
import SearchInput from '../common/SearchInput';
import Card from '../ui/Card';

const OriginStoreSelector = () => {
  const { deliveryType, originStore, dispatch, setOriginStore } = useDelivery();
  const { user, token } = useAuth();
  
  const [selectedStore, setSelectedStore] = useState(originStore);
  const [searchTerm, setSearchTerm] = useState('');
  const [stores, setStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // ê¸°ì¡´ 3ë‹¨ê³„ ì„ íƒ ìƒíƒœ (ëª¨ë‹¬ì—ì„œ ì‚¬ìš©)
  const [step, setStep] = useState(1);
  const [selectedStoreData, setSelectedStoreData] = useState(null);
  const [selectedDepartment, setSelectedDepartment] = useState(null);
  const [selectedManager, setSelectedManager] = useState(null);
  const [departmentManagers, setDepartmentManagers] = useState([]);

  // ëª¨ë‹¬ ìƒíƒœ
  const [showModal, setShowModal] = useState(false);
  const [modalStep, setModalStep] = useState(1);

  // ëª¨ë‹¬ ë‚´ ê²€ìƒ‰ ìƒíƒœ
  const [modalSearchTerm, setModalSearchTerm] = useState('');

  // ë§¤ì¥ ë°ì´í„° ê°€ì ¸ì˜¤ê¸° - ìƒ˜í”Œ ë°ì´í„° ì™„ì „ ì œê±°
  useEffect(() => {
    const fetchStores = async () => {
      // í† í°ì´ ì—†ìœ¼ë©´ ì•„ë¬´ê²ƒë„ í•˜ì§€ ì•ŠìŒ
      if (!token) {
        console.log('âŒ í† í°ì´ ì—†ìŒ - ë¡œê·¸ì¸ì´ í•„ìš”í•©ë‹ˆë‹¤');
        setLoading(false);
        setError('ë¡œê·¸ì¸ì´ í•„ìš”í•©ë‹ˆë‹¤');
        setStores([]);
        return;
      }

      try {
        setLoading(true);
        setError('');
        setStores([]); // ê¸°ì¡´ ë°ì´í„° ì´ˆê¸°í™”
        
        console.log('ğŸ” ì‹¤ì œ API í˜¸ì¶œ ì‹œì‘');
        const response = await fetch('https://quinors-lv-backend.ngrok.io/api/auth/stores?active=true&limit=100', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
        });

        console.log('ğŸ” API ì‘ë‹µ ìƒíƒœ:', response.status);
        
        if (!response.ok) {
          throw new Error(`API í˜¸ì¶œ ì‹¤íŒ¨: ${response.status} ${response.statusText}`);
        }

        const result = await response.json();
        console.log('ğŸ” API ì‘ë‹µ ì›ë³¸ ë°ì´í„°:', result);

        if (!result.success) {
          throw new Error(result.message || 'API ì‘ë‹µì´ ì„±ê³µì´ ì•„ë‹˜');
        }

        if (!result.data || !Array.isArray(result.data) || result.data.length === 0) {
          throw new Error('ë§¤ì¥ ë°ì´í„°ê°€ ì—†ìŠµë‹ˆë‹¤');
        }

        console.log('ğŸ” ì‹¤ì œ ë§¤ì¥ ë°ì´í„° ê°œìˆ˜:', result.data.length);
        
        // âœ… ì‹¤ì œ ë°ì´í„°ë§Œ ì‚¬ìš© - ë§¤ì¥ë³„ ê·¸ë£¹í™”
        const storeGroups = {};
        let validDataCount = 0;
        
        result.data.forEach((store, index) => {
          console.log(`ğŸ” ì²˜ë¦¬ ì¤‘ì¸ ë°ì´í„° ${index + 1}/${result.data.length}:`, {
            _id: store._id,
            storeName: store.storeName,
            department: store.department,
            managerName: store.managerName,
            managerPhone: store.managerPhone,
            storeCode: store.storeCode,
            address: store.address,
            isActive: store.isActive
          });
          
          // âœ… í•„ìˆ˜ ë°ì´í„°ê°€ ìˆëŠ”ì§€ í™•ì¸ (ë§¤ì¥ëª…ê³¼ ë¶€ì„œ, ë‹´ë‹¹ìëª…ì´ ëª¨ë‘ ìˆì–´ì•¼ í•¨)
          if (!store.storeName || !store.department || !store.managerName) {
            console.log(`âŒ í•„ìˆ˜ ë°ì´í„° ëˆ„ë½ìœ¼ë¡œ ê±´ë„ˆëœ€:`, {
              storeName: !!store.storeName,
              department: !!store.department, 
              managerName: !!store.managerName
            });
            return;
          }

          validDataCount++;
          const storeName = store.storeName.trim();
          const department = store.department.trim();
          const managerName = store.managerName.trim();
          
          // ë§¤ì¥ ê·¸ë£¹ ì´ˆê¸°í™”
          if (!storeGroups[storeName]) {
            storeGroups[storeName] = {
              id: store._id,
              storeCode: store.storeCode || '',
              name: storeName,
              address: store.address || '',
              isActive: store.isActive !== false,
              createdAt: store.createdAt,
              departments: {}
            };
          }
          
          // ë¶€ì„œë³„ ë‹´ë‹¹ì ê·¸ë£¹ ì´ˆê¸°í™”
          if (!storeGroups[storeName].departments[department]) {
            storeGroups[storeName].departments[department] = [];
          }
          
          // ë‹´ë‹¹ì ì¶”ê°€
          storeGroups[storeName].departments[department].push({
            id: `${store._id}`,
            managerName: managerName,
            phone: store.managerPhone || '',
            fullPhone: store.managerPhone ? 
              store.managerPhone.replace(/(\d{3})(\d{4})(\d{4})/, '$1-$2-$3') : ''
          });
          
          console.log(`âœ… ìœ íš¨í•œ ë°ì´í„° ì¶”ê°€: ${storeName} - ${department} - ${managerName}`);
        });

        console.log('ğŸ” ìœ íš¨í•œ ë°ì´í„° ê°œìˆ˜:', validDataCount);
        console.log('ğŸ” ê·¸ë£¹í™”ëœ ë§¤ì¥ ë°ì´í„°:', storeGroups);

        if (validDataCount === 0) {
          throw new Error('ìœ íš¨í•œ ë§¤ì¥ ë°ì´í„°ê°€ ì—†ìŠµë‹ˆë‹¤. ë§¤ì¥ëª…, ë¶€ì„œ, ë‹´ë‹¹ìëª…ì´ ëª¨ë‘ í•„ìš”í•©ë‹ˆë‹¤.');
        }

        // ë§¤ì¥ ë°°ì—´ë¡œ ë³€í™˜
        const transformedStores = Object.values(storeGroups);
        
        console.log('ğŸ” ìµœì¢… ë³€í™˜ëœ ë§¤ì¥ë“¤:', transformedStores);
        console.log('ğŸ” ìµœì¢… ë§¤ì¥ ê°œìˆ˜:', transformedStores.length);
        
        setStores(transformedStores);
        console.log('âœ… ì‹¤ì œ ë§¤ì¥ ë°ì´í„° ì„¤ì • ì™„ë£Œ');
        
      } catch (error) {
        console.error('âŒ ì‹¤ì œ ë°ì´í„° ë¡œë“œ ì‹¤íŒ¨:', error);
        setError(error.message);
        setStores([]); // âœ… ì—ëŸ¬ ì‹œì—ë„ ë¹ˆ ë°°ì—´
      } finally {
        setLoading(false);
      }
    };

    fetchStores();
  }, [token]);

  // ê²€ìƒ‰ í•„í„°ë§
  const filteredStores = stores.filter(store => {
    if (!searchTerm.trim()) {
      return true;
    }
    
    const searchLower = searchTerm.toLowerCase();
    
    // ë§¤ì¥ëª…, ë§¤ì¥ì½”ë“œë¡œ ê²€ìƒ‰
    const matchStore = (store.name && store.name.toLowerCase().includes(searchLower)) ||
                      (store.storeCode && store.storeCode.toLowerCase().includes(searchLower));
    
    // ë‹´ë‹¹ìëª…ìœ¼ë¡œ ê²€ìƒ‰
    const matchManager = Object.values(store.departments || {}).some(managers =>
      Array.isArray(managers) && managers.some(manager => 
        manager && manager.managerName && manager.managerName.toLowerCase().includes(searchLower)
      )
    );
    
    return matchStore || matchManager;
  });

  console.log('ğŸ” í˜„ì¬ ìƒíƒœ:', {
    stores: stores.length,
    filteredStores: filteredStores.length,
    loading,
    error,
    token: !!token
  });

  // ëª¨ë‹¬ ë‚´ ë‹´ë‹¹ì ê²€ìƒ‰ í•„í„°ë§
  const filteredDepartmentManagers = departmentManagers.filter(manager => {
    if (!modalSearchTerm.trim()) return true;
    
    return (
      (manager && manager.managerName && manager.managerName.toLowerCase().includes(modalSearchTerm.toLowerCase())) ||
      (manager && manager.fullPhone && manager.fullPhone.includes(modalSearchTerm)) ||
      (manager && manager.phone && manager.phone.includes(modalSearchTerm))
    );
  });

  // ë§¤ì¥ ì„ íƒ ì‹œ ëª¨ë‹¬ ì—´ê¸°
  const handleStoreSelect = (store) => {
    console.log('ë§¤ì¥ ì„ íƒ:', store);
    setSelectedStoreData(store);
    setShowModal(true);
    setModalStep(1);
    setSelectedDepartment(null);
    setSelectedManager(null);
    setModalSearchTerm('');
  };

  // ëª¨ë‹¬ì—ì„œ ë¶€ì„œ ì„ íƒ
  const handleModalDepartmentSelect = (department) => {
    console.log('ë¶€ì„œ ì„ íƒ:', department);
    setSelectedDepartment(department);
    setDepartmentManagers(selectedStoreData.departments[department] || []);
    setModalStep(2);
    setModalSearchTerm('');
  };

  // ëª¨ë‹¬ì—ì„œ ë‹´ë‹¹ì ì„ íƒ
  const handleModalManagerSelect = (manager) => {
    console.log('ë‹´ë‹¹ì ì„ íƒ:', manager);
    setSelectedManager(manager);
  };

  // ëª¨ë‹¬ì—ì„œ ì„ íƒ ì™„ë£Œ
  const handleModalConfirm = () => {
    if (selectedManager && selectedDepartment && selectedStoreData) {
      const finalSelection = {
        store: selectedStoreData,
        department: selectedDepartment,
        manager: selectedManager,
        displayName: `${selectedStoreData.name} ${selectedDepartment} - ${selectedManager.managerName}`
      };
      
      setSelectedStore(finalSelection);
      dispatch({ type: 'SET_ORIGIN_STORE', payload: finalSelection });
      setShowModal(false);
    }
  };

  // ëª¨ë‹¬ ë‹«ê¸°
  const handleModalClose = () => {
    setShowModal(false);
    setModalStep(1);
    setSelectedDepartment(null);
    setSelectedManager(null);
    setModalSearchTerm('');
  };

  // ëª¨ë‹¬ ë’¤ë¡œê°€ê¸°
  const handleModalBack = () => {
    if (modalStep === 2) {
      setModalStep(1);
      setSelectedManager(null);
      setModalSearchTerm('');
    } else {
      handleModalClose();
    }
  };

  // ê¸°ì¡´ í•¨ìˆ˜ë“¤
  const handleBack = () => {
    dispatch({ type: 'SET_STEP', payload: 1 });
  };

  const handleNext = () => {
    if (selectedStore && selectedStore.manager) {
      dispatch({ type: 'SET_ORIGIN_STORE', payload: selectedStore });
      dispatch({ type: 'SET_STEP', payload: 3 });
    }
  };

  const getPreviewContent = () => {
    if (selectedStore && selectedStore.displayName) {
      return `ì¶œë°œì§€: ${selectedStore.displayName}`;
    }
    return '';
  };

  const getDepartmentColor = (department) => {
    const colors = {
      'ì—¬ì„±': 'bg-pink-100 text-pink-600 border-pink-200',
      'ë‚¨ì„±': 'bg-blue-100 text-blue-600 border-blue-200',
      'ìŠˆì¦ˆ': 'bg-green-100 text-green-600 border-green-200',
    };
    return colors[department] || 'bg-gray-100 text-gray-600 border-gray-200';
  };

  return (
    <>
      <div className="bg-gray-100 flex items-center justify-center min-h-screen font-sans">
        <div className="w-full max-w-sm mx-auto bg-white shadow-lg rounded-3xl overflow-hidden h-screen max-h-[812px] flex flex-col relative">
          
          <div className="relative z-30">
            <Header 
              title="ì¶œë°œì§€ ì„ íƒ"
              subtitle="ì¶œë°œì§€ ë§¤ì¥ì„ ì„ íƒí•´ì£¼ì„¸ìš”"
              currentStep={2}
              onBack={handleBack}
            />
          </div>

          <main className="flex-1 bg-gray-50 p-6 space-y-4 overflow-y-auto pb-20">
            <SearchInput
              placeholder="ë§¤ì¥ëª…, ë§¤ì¥ì½”ë“œ, ë‹´ë‹¹ìëª…ìœ¼ë¡œ ê²€ìƒ‰"
              value={searchTerm}
              onChange={setSearchTerm}
            />

